css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweetdeck.twitter.com") {

  /*Light TweetDeck 2.1 Night is a theme made by @CamaradeBerter for Tweetdeck's dark theme.*/
  
  /*Text*/
  .txt-mute, .txt-mute a:not(:hover):not(:focus)
  {
    color: #777;
  }
  a, .is-inverted-dark a
  {
    color: #55acee;
    text-decoration: none;
  }
  a:hover, a:focus, .is-inverted-dark a:hover, .is-inverted-dark a:focus, .link-complex:hover .link-complex-target, .link-complex:focus .link-complex-target, .link-complex:hover .link-complex-target
  {
    color: #2a5677;
    text-decoration: none;
  }
  
  /*Buttons*/
  .btn, .is-inverted-dark .btn-alt
  {
    background: transparent;
    border: none;
    box-shadow: none;
    color: #fff;
  }
  .btn:hover, .btn:focus, .btn:active, .btn-positive:hover, .btn-on-blue:hover, .btn.is-selected
  {
    background: transparent;
    box-shadow: none;
    color: #55acee;
  }
  .is-inverted-dark .btn-alt:hover, .is-inverted-dark .btn-alt:focus
  {
    color: #2a5677;
    background:transparent;
  }
  .btn[disabled], .btn[disabled]:hover, .btn[disabled]:focus, .btn[disabled]:active, .btn-positive.is-disabled, .btn-positive.is-disabled:hover, .btn-positive.is-disabled:focus, .btn-positive[disabled], .btn-positive[disabled]:hover, .btn-positive[disabled]:active, .btn-on-blue.is-disabled, .btn-on-blue.is-disabled:hover, .btn-on-blue.is-disabled:focus, .btn-negative.is-disabled, .btn-negative.is-disabled:hover, .is-inverted-dark .btn-alt[disabled]
  {
    background: transparent;
    color: transparent;
  }
  .btn-negative:hover, .btn-neutral-negative:hover, .btn-neutral-negative:hover .label
  {
    color: #55acee;
    text-decoration: none;
  }
  .more-tweets-btn
  {
    background: #55acee;
    color: #fff;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .more-tweets-btn:hover
  {
    background: #55acee;
    color: #2a5677;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  
  /*Inputs*/
  input, input:focus, textarea
  {
    background: transparent;
    color: #fff;
    border: none;
    box-shadow: none;
  }
  select
  {
    background: #fff;
    border-radius: 6px;
    border: none;
    box-shadow: none;
  }
  
  
  /*App header*/
  .app-header
  {
    background: #222426;
    box-shadow: 3px 0px 3px rgba(0, 0, 0, 0.2);
  }
  .tweet-button /*'New Tweet' button*/
  {
    background: transparent;
    color: #fff;
    box-shadow: none;
  }
  .tweet-button:hover
  {
    background: transparent;
    color: #55acee;
  }
  .attach-compose-buttons .tweet-button /*When the composer is open*/
  {
    background: transparent;
  }
  .app-search-input /*Search box*/
  {
    background: #222426;
    box-shadow: none;
  }
  .app-search-input:focus, .app-search-input.is-focused
  {
    background: #222426;
    border: none;
    border-radius: 6px;
    color: #fff;
  }
  .search-input-perform-search /*Search logo*/
  {
    color: #fff;
  }
  .search-input-perform-search:hover
  {
    color: #55acee;
  }
  .app-search-fake /*Search button*/
  {
    background: transparent;
    box-shadow: none;
    color: #fff;
  }
  .app-search-fake:hover
  {
    color: #55acee;
  }
  .column-nav-item /*Columns'icons*/
  {
    background: #222426;
  }
  .column-nav-link
  {
    color: #fff;
  }
  .column-nav-link:hover
  {
    color: #55acee;
  }
  .column-nav-link .attribution, .column-nav-link:after
  {
    color: #fff;
  }
  .numbered-badge-onnav /*New messages abdge*/
  {
    border: 2px solid #222426;
  }
  .numbered-badge-onheader
  {
    border: 2px solid #222426;
  }
  .numbered-badge
  {
    background: #fff;
    color: #222426;
    border-radius: 6px;
  }
  .app-nav-link /*'Add column' button*/
  {
    color: #fff;
  }
  .app-nav-link:hover
  {
    color: #55acee;
  }
  .with-nav-border-t:before
  {
    border: none;
  }
  .app-navigator /*The 3 buttons at the bottom of the header*/
  {
    background: transparent;
  }
  .app-nav-tab
  {
    color: #fff;
  }
  .app-nav-tab:hover
  {
    color: #55acee;
  }
  .app-nav-tab.is-selected
  {
    background: transparent;
    color: #fff
  }
  .app-nav-tab.is-selected:hover
  {
    color: #55acee;
  }
  .app-title /*TweetDeck logo*/
  {
    background: transparent;
  }
  
  /*Compose panel*/
  .is-open .drawer:after /*Shadow*/
  {
    box-shadow: 3px 0px 3px rgba(0, 0, 0, 0.2);
  }
  .compose
  {
    background: #55acee;
    color: #fff;
  }
  .compose-header
  {
    border: none;
  }
  .compose-text-title /*'From'*/
  {
    color: #fff;
  }
  .txt-r-white /*To choose how the accounts list appears*/
  {
    color: #fff;
  }
  .compose-account /*Accounts'icons*/
  {
    background: transparent;
  }
  .compose-account-img
  {
    border-radius: 20%;
  }
  .compose-account-img:focus
  {
    box-shadow: none;
  }
  .is-selected.compose-account .compose-account-img
  {
    box-shadow: none;
  }
  .compose-account-selected /*Tick*/
  {
    border-radius: 50%;
    box-shadow: none;
  }
  .compose-account b, .compose-account p
  {
    color: #fff;
  }
  .compose-message-recipient-input-container.is-focused /*DM recipient*/
  {
    box-shadow: none;
  }
  .compose-message-recipient-input-container
  {
    border-radius: 6px;
  }
  .dropdown-menu
  {
    border: none;
  }
  .compose-reply-tweet, .compose-reply-tweet-remove, .compose-reply-tweet .fullname, .compose-reply-tweet .username, .compose-reply-tweet .tweet-body a 
  {
    background: transparent;
    color: #fff;
  }
  .compose-reply-tweet .tweet-body a:hover
  {
    color: #2a5677;
  }
  .remove-recipient, .compose-message-recipient-name
  {
    border: none;
  }
  .compose-text-container, .compose-text
  {
    background: transparent;
    color: #fff;
  }
  .compose-media-bar-holder 
  {
    background: transparent;
  }
  .bg-r-white
  {
    background: transparent;
  }
  .lst-modal /*Usernames list*/
  {
    background: #fff;
    border: none;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .lst li
  {
    border: none;
  }
  .lst .s-selected, .lst .s-selected .fullname, .lst .s-selected .username
  {
    background: transparent;
    color: #55acee;
  }
  .character-count-compose:disabled /*Count remaining characters*/
  {
    color: #fff;
  }
  .invalid-char-count:disabled
  {
    color: #fff;
  }
  .compose .btn:hover, .compose .btn:focus
  {
    color: #2a5677;
  }
  .btn-on-blue.is-disabled:hover, .btn-on-blue.is-disabled:focus
  {
    color: transparent;
  }
  .compose .popover /*Scheduled Tweet box*/
  {
    background: #222426;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .compose .popover input
  {
    background: transparent;
  }
  .caret /*The little triangle*/
  {
    display: none;
  }
  .compose .popover input, .popover .cal
  {
    color: #fff;
  }
  .compose .popover .btn
  {
    color: #fff;
  }
  .compose .popover .btn:hover
  {
    color: #55acee;
  }
  #calbody /*Calender*/
  {
    background: transparent;
  }
  #caldays
  {
    border: none;
  }
  #caldays span
  {
    color: #fff;
  }
  #calweeks
  {
    background: transparent;
  }
  .calweek a
  {
    border-radius: 6px;
    background: transparent;
    color: #55acee;
  }
  .calweek a:hover
  {
    background: #fff;
  }
  #calcurrent
  {
    background: #fff;
    color: #222426;
  }
  #caltoday
  {
    background: transparent;
    color: #fff;
  }
  #caltoday:hover
  {
    background: #fff;
    color: #55acee;
  }
  a.caldisabled
  {
    background: transparent !important;
    color: transparent !important;
  }
  a.caloff
  {
    color: #aad5f6;
    background: transparent;
  }
  a.caloff:hover
  {
    background: #fff;
    color: #55acee;
  }
  .compose-remember-state /*'Stay open'*/
  {
    color: #fff;
  }
  
  /*Search panel*/
  .app-search .popover
  {
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .list-item, .list-item .list-icon /*Search history*/
  {
    color: #55acee;
  }
  .list-item.is-selected, .list-item.is-selected .list-icon
  {
    background: transparent;
    color: #2a5677;
  }
  .list-item:hover txt-mute, .list-item.is-selected .txt-mute
  {
    color: #2a5677;
  }
  .list-item-button /*'Clear history' button*/
  {
    background: transparent;
  }
  .app-search .popover button
  {
    color: #55acee;
  }
  .app-search .popover input
  {
    color: #111;
  }
  .app-search .btn.is-selected
  {
    color: #2a5677;
  }
  .is-inverted-dark .accordion-divider-t /*Search controls separations*/
  {
    border: none;
  }
  .is-inverted-dark .facet-type
  {
    border: none;
  }
  .with-column-divider-bottom
  {
    border: none;
  }
  .is-inverted-dark .facet-type-content.is-active, .is-inverted-dark .facet-type-user.is-active, .is-inverted-dark .facet-type-engagement.is-active /*Search controls*/
  {
    background: transparent;
  }
  .column-header-temp, .is-inverted-dark .with-column-divider-bottom
  {
    border: none;
  }
  .app-search .column-header-temp
  {
    display: none;
  }
  .is-inverted-dark .scroll-conversation, .is-inverted-dark .tweet-detail-wrapper, .is-inverted-dark .card-holder  /*Tweet*/
  {
    background: transparent; 
    border: none;
  }
  .app-search .popover .is-inverted-dark a
  {
    color: #55acee;
  }
  .list-divider /*Footer*/
  {
    border: none;
  }
  
  /*Accounts Panel*/
  .bg-r-light-gray /*Background*/ 
  {
    background: #55acee;
  }
  .drawer-header /*Header*/
  {
    border: none;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    z-index: 2;
  }
  .drawer-header-title
  {
    color: #fff;
  }
  .obj-right .txt-r-dark-gray, .drawer-header .txt-r-dark-gray, .is-inverted-dark .accordion .icon-arrow-d, .is-inverted-dark .accordion .icon-arrow-u  
  {
    color: #fff;
  }
  .drawer-header:after
  {
    background: transparent;
  }
  .drawer-content
  {
    top: 55px;
    z-index: 1;
  }
  .drawer .is-inverted-dark .account-link, .txt-r-dark-gray /*ID*/
  {
    color: #fff;
  }
  .txt-r-text-link /*Tweets, following and followers numbers*/
  {
    color: #fff;
  }
  .is-inverted-dark .accordion .is-active /*'Default account*/
  {
    color: #2a5677;
  }
  .drawer .btn, .is-inverted-dark a
  {
    color: #fff;
  }
  .drawer .btn:hover
  {
    color: #2a5677;
  }
  .account-settings-row.is-highlighted:last-child /*Notification*/
  {
    background: #aad5f6;
    border: none;
    color: #fff;
  }
  .bg-r-faded-gray
  {
    background: transparent;
  }
  .txt-r-deep-gray
  {
    color: #fff;
  }
  .contributor-row[data-state="settings"], .contributor-settings-role
  {
    background: #aad5f6;
    border: none;
  }
  .contributor-row[data-state="closed"]::after
  {
    background: none;
  }
  .contributor-adder-block.position-sticky-stuck 
  {
    border: none;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    background: linear-gradient( to bottom, #55acee, rgba(85, 172, 238, 0.9));
    z-index: 2;
  }
  .account-row-separator-b:after /*Separator*/
  {
    background: transparent;
  }
  
  /*Columns*/
  .app-content, .app-columns-container /*Background*/
  {
    background: #2a5677;
  }
  .column
  {
    border-radius: 6px;
    background: #222426;
    border: none;
  }
  .column-header-link, .column .column-title, .column .column-type-icon, .column .column-title-back
  {
    border: none;
    color: #fff;
  }
  .column-header
  {
    background: transparent;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    z-index: 2;
  }
  .column-number
  {
    color: #fff;
  }
  .column-header .txt-mute /*ID*/
  {
    color: #fff;
  }
  .is-options-open .column-settings-link /*Options logo*/
  {
    color: #fff;
    background: #55acee;
    border-radius: 6px 6px 0px 0px;
  }
  .column-options
  {
    background: #55acee;
    z-index: 1;
  }
  .facet-type
  {
    border: none;
  }
  .facet-type.is-expanded
  {
    background: none;
  }
  .search-input-control .icon
  {
    color: #fff;
  }
  .column .facet-subtitle, .toggle-item
  {
    color: #fff;
  }
  .facet-subtitle:hover, .toggle-item:hover
  {
    color: #2a5677;
  }
  .column-options .button-tray /*Footer's column options*/
  {
    background: #55acee;
  }
  .accordion-divider-t
  {
    border: none;
  }
  .btn-options-tray:hover .label, .btn-neutral-positive:hover .label
  {
    text-decoration: none;
  }
  .column-message
  {
    background: transparent;
    z-index: 1;
  }
  .stream-item:not(.conversation-event) /*Tweets*/
  {
    border-bottom: none;
  }
  .txt-mute a:hover, .txt-mute a:focus /*Time*/
  {
    color: #55acee;
  }
  footer a:hover, footer a:hover span /*'Details'*/
  {
    color: #55acee;
  }
  .avatar, .avatar.size36
  {
    border-radius: 20%;
    z-index: 1;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .media-size-small, .media-size-medium /*Pictures, videos...*/
  {
    border-radius: 6px;
    z-index: 1;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .in-tweet-divider::before
  {
    display: none;
  }
  .card-holder
  {
    border: none;
  }
  .quoted-tweet /*Tweet's preview*/
  {
    border: none;
    background: #55acee;
    color: #fff;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .quoted-tweet a
  {
    color: #fff;
  }
  .quoted-tweet a:hover
  {
    color: #2A5677;
  }
  .quoted-tweet .txt-mute, .quoted-tweet a:not(:hover):not(:focus)
  {
    color: #fff;
  }
  .pos-l /*Menu*/
  {
    z-index: 2;
    background: #55acee;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .dropdown-menu a:hover
  {
    color: #22a677;
  }
  .rpl.s-replying textarea, .rpl textarea /*Messages*/
  {
    background: transparent;
  }
  .input-group-button /*Collection's footer*/
  {
    border: none;
  }
  .tweet-dogear /*Display colored bar on the side instead of orange and green Fav/RT corners, original by @AlmtyCwrd in Snowflake*/
  {
	  display: block;
	  width: 0.1px;
	  height: 100%;
    background-image: none;
  }
  .is-favorite .tweet-dogear 
  {
	  border-right: 4px solid #ff9b00;
	  box-shadow: 0px 0px 0px #ff9b00;
  }
  .is-retweet.is-favorite .tweet-dogear
  {
	  border-right: 4px solid #b8a719;
	  box-shadow: 0px 0px 0px #b8a719;
  }
  .is-retweet .tweet-dogear 
  {
	  border-right: 4px solid #71b332;
	  box-shadow: 0px 0px 0px #71b332;
  }
  .is-selected-tweet, .is-unread 
  {
    background: none;
    border-right: 4px solid #55acee;
  }
  
  /*Tweet*/
  .column-title-back
  {
    color: #55acee;
  }
  .column-title-back:hover .icon
  {
    color: #2a5677;
  }
  .scroll-conversation
  {
    background: transparent;
  }
  .tweet-detail-wrapper
  {
    background: transparent;
    border: none;
  }
  .column-detail .is-selected-tweet
  {
    background: transparent;
    border-right: 4px solid #55acee;
  }
  .tweet-stats
  {
    border: none;
  }
  .tweet-detail-actions /*Reply, RT, fav, more*/
  {
    border: none;
  }
  .detail-view-inline, .detail-view-inline-text /*'Reply to @...'*/
  {
    border: none;
    background: transparent;
  }
  .mdl-column-rhs textarea
  {
    color: #111;
  }
  .is-selected .reply-triangle
  {
    visibility: hidden;
  }
  .inline-reply
  {
    background: transparent;
  }
  .inline-reply .btn-neutral
  {
    color: #55acee;
  }
  .social-proof-for-tweet-title
  {
    background: transparent;
    color: #2a5677;
    border: none;
  }
  
  /*Messages*/
  .rpl textarea
  {
    border: none;
    box-shadow: none;
  }
  
  /*Windows*/
  .mdl
  {
    border-radius: 6px;
    background: #55acee;
  }
  .mdl-header
  {
    color: #fff;
  }
  .mdl .btn
  {
    color: #fff;
  }
  .mdl .btn:hover
  {
    color: #2a5677;
  }
  .mdl .btn[disabled], .mdl .btn[disabled]:hover
  {
    color: transparent;
  }
  .mdl-content, .mdl-column-med
  {
    background: transparent;
    border: none;
  }
  .mdl-column-med input, .search-input-control .icon  /*Left column*/
  {
    color: #fff;
  }
  .mdl-column-med h3
  {
    color: #fff;
  }
  .mdl-column-med .txt-mute
  {
    color: #fff;
  }
  .mdl-column-med select
  {
    background: #fff;
    border-radius: 6px;
  }
  .mdl-placeholder
  {
    color: #fff;
    text-shadow: none;
  }
  .lst-group .selected a, .lst-group .selected a:hover /*Selected account*/
  {
    background: #fff;
    border-radius: 6px 0px 0px 6px;
  }
  .lst-group .selected .fullname, .lst-group .selected .inner strong, .list-twitter-list p, .lst-group .selected .list-link
  {
    color: #292f33;    
  }
  .lst-group .selected .username, .lst-group .selected .bytext, .lst-group .selected .subtitle
  {
    color: #888;
  }
  .list-account:hover:hover, .list-twitter-list:hover:hover, .list-listmember:hover:hover, .list-link:hover:hover /*Others accounts*/
  {
    border-radius: 6px 0px 0px 6px;
  }
  .list-account .fullname, .list-account .username, .list-twitter-list .inner strong, .list-twitter-list .bytext, .list-twitter-list .subtitle, .list-link
  {
    color: #fff;
    text-shadow: none;
  }
  .list-account:hover:hover .fullname, .list-twitter-list:hover:hover .inner strong
  {
    color: #292f33;
  }
  .list-account:hover:hover .username, .list-twitter-list:hover:hover .bytext, .list-twitter-list:hover:hover .subtitle
  {
    color: #888;
  }
  .mdl-column-rhs /*Right column*/
  {
    background: #fff;
    border-radius: 6px;
  }
  .mdl-col-settings
  {
    border-radius: 0px 6px 6px 6px;
  }
  .mdl-column-rhs .column-header, .mdl-column-rhs .column-header-temp
  {
    color: #55acee;
    border: none;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    z-index: 2;
  }
  .mdl-column-rhs header a, .mdl-column-rhs .is-inverted-dark a
  {
    color: #55acee;
  }
  .mdl-column-rhs .is-inverted-dark a:hover
  {
    color: #2a5677;
  }
  .mdl-column-rhs .quoted-tweet a
  {
    color: #fff;
  }
  .mdl-column-rhs .quoted-tweet a:hover
  {
    color: #2a5677;
  }
  .mdl-column-rhs .column-title .attribution
  {
    color: #55acee;
  }
  .mdl-column-rhs input
  {
    color: #111;
  }
  .mdl-column-rhs .btn /*'Edit' button*/
  {
    color: #55acee;
  }
  .column-background-fill
  {
    background: transparent;
  }
  #list_details
  {
    background: #fff;
    border-radius: 6px;
  }
  .mdl-column-rhs .is-inverted-dark .txt-mute
  {
    color: #888;
  }
  
  /*'Choose column type' window*/
  .lst-launcher a, .lst-launcher a:hover, .lst-launcher a:focus
  {
    border: none;
  }
  
  /*Profile*/
  .modal-content 
  {
    border-radius: 6px;
    background: transparent;
  }
  .prf-header
  {
    border-radius: 6px 6px 0px 0px;
  }
  .prf-img /*Avatar*/
  {
    border-radius: 20%;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .prf-img img
  {
    box-shadow: none;
  }
  .prf-header .link-complex:hover .link-complex-target
  {
    color: #fff;
  }
  .prf-meta /*'Tweets, following, followers' line*/
  {
    background: transparent;
    border: none;
  }
  .follow-btn .icon /*'Follow' button icon*/
  {
    color: #fff;
  }
  .follow-btn:hover .icon
  {
    color: #2a5677;
  }
  .s-following .follow-btn /*'Following' button*/
  {
    background: transparent;
    box-shadow: none;
  }
  .s-following .follow-btn:hover 
  {
    background: none;
  }
  .s-following .follow-btn:hover .unfollow-text, .s-blocking .follow-btn:hover .unblock-text
  {
    color: #fff;
  }
  .prf-stats a, .prf-stats a strong /*'Tweets, following, followers, listed'*/
  {
    color: #fff;
  }
  .prf-stats li + li a
  {
    border: none;
  }
  .social-proof-container /*'Followed by...*/
  {
    background: transparent;
    color: #fff;
  }
  .social-proof-container a
  {
    color: #fff;
  }
  .social-proof-container a:hover
  {
    color: #2a5677;
  }
  .prf .lst-profile a /*Icons*/
  {
    border: none;
  }
  .prf .lst-profile i, .prf .lst-profile span 
  {
    color: #fff;
  }
  .prf .lst-profile a:hover i, .prf .lst-profile a:hover span 
  {
    color: #2a5677;
  }
  
  /*Media viewer*/
  .med-tweet p
  {
    color: #fff;
  }
  .is-inverted-light a, .is-inverted-light a:focus, .is-inverted-light .txt-mute a:not(:hover):not(:focus), .is-inverted-light .tweet-action
  {
    color: #fff;
  }
  .is-inverted-light a:hover, .is-inverted-light .tweet-action:hover
  {
    color: #2a5677;
    text-decoration: none;
  }
  .med-tweet .account-inline .fullname
  {
    color: #fff;
  }
  .med-tweet .link-complex:hover .link-complex-target
  {
    color: #2a5677;
  }
  .is-inverted-light .txt-mute 
  {
    color: #fff;
  }
  
  /*Retweet window*/
  .cmp-replyto /*Tweet*/
  {
    background: transparent;
    border: none;
  }
  .cmp-replyto .txt-mute, .cmp-replyto .txt-mute a:not(:hover):not(:focus)
  {
    color: #fff;
  }
  .cmp-replyto .txt-mute a:hover
  {
    color: #2a5677;
  }
  .cmp-replyto p, .cmp-replyto a
  {
    color: #fff;
  }
  .cmp-replyto a:hover
  {
    color: #2a5677;
  }
  .accs .acc-avatar
  {
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .accs li /*Accounts icons*/
  {
    border: none;
    background: transparent;
  }
  .accs li:hover
  {
    background: transparent;
  }
  .accs .icon
  {
    color: transparent;
  }
  .accs li:hover .icon
  {
    color: #add5f6;
  }
  .accs .acc-selected, .accs .acc-selected:hover
  {
    background: transparent;
    border: none;
  }
}
