css:
@-moz-document domain("animeonline.su") {
/*------------------------------------ОБЩИЕ НАСТРОЙКИ------------------------------------*/

#header .center {
	display: none !important;
}
#header {
	height: 0px !important;
	width: 100%;
	position: static;
	min-width: 1240px;
	z-index: 10;
}
body.loaded {
	background-image: url(http://i.imgur.com/6rZr3bh.png)!important;
	/* http: //i.imgur.com/p43TRh9.png; */
	
	background-repeat: repeat-x;
	background-position: center 0px !important;
	background-position-y: -60px !important;
}
#middle {
	width: 100%;
	height: 1%;
	position: relative;
	z-index: 11;
	margin-top: 100px;
}
/*------------------------------------Тело новости*/

body {
	/*background: #f2f2f2;*/
	outline: none;
	color: #272727;
}
.sl {
	display: none !important;
}
.sr {
	display: none !important;
}
#wrapper {
	padding-top: 0px !important;
	width: 830px !important;
	margin: 0 auto;
}
/*------------------------------------ПАНЕЛЬ------------------------------------*/

.icona {
	background-image: url("http://i.imgur.com/WcL7zrS.png");
	background-repeat: no-repeat;
	display: inline-block !important;
	padding: 0 !important;
	position: static !important;
	vertical-align: middle !important;
	text-align: center !important;
}
#logo {
	position: relative;
	font-size: 14px;
	color: white;
	font-weight: bold;
	float: left;
	display: block;
	width: 76px;
	height: 60px;
	padding-top: 11px;
	padding-left: 4px;
	margin-right: 18px;
}
/*------------------------------------Лого*/

a#logo img {
	background-image: url(http://i.imgur.com/M8ljeQt.png);
	box-sizing: border-box;
	padding-left: 92px;
	width: 92px;
	height: 40px;
	background-size: auto 30px;
	background-repeat: no-repeat;
	margin-top: 3px;
}
/*------------------------------------Анимация*/

.icona-05-my {
	width: 37px !important;
	height: 37px !important;
	margin-top: 11px !important;
	margin-bottom: 4px !important;
	margin-left: 8px !important;
	background-position: -30px -402px;
	transition: 0.3s;
}
.icona-03-bell {
	width: 33px !important;
	height: 33px !important;
	background-position: -30px -216px;
	margin-top: 13px !important;
	transition: 0.3s;
}
.icona-01-more {
	width: 33px !important;
	height: 33px !important;
	background-position: -30px -30px;
	line-height: 33px;
	transition: 0.3s;
}
form {
	padding-top: 7px;
	padding-bottom: 0px;
	padding-right: 5px;
	padding-left: 10px;
	margin: 0px;
}
#panelLogin img {
	margin-top: 13px;
}
#panel a {
	color: #999;
}
#toppanel:hover {
	background-color: black;
	transition: 0.18s;
}
#toppanel {
	position: fixed;
	left: 0;
	top: 0;
	width: 100%;
	z-index: 99999;
	text-align: center;
	margin-left: auto;
	margin-right: auto;
	background-color: rgba(0, 0, 0, 0.2);
	height: 60px;
	min-width: 1240px;
	-webkit-box-shadow: 0px 1px 4px -2px #000;
	font-weight: 400;
}
#panel {
	min-width: 1000px;
	width: 1200px;
	height: 60px;
	color: #999999;
	margin: 0 auto;
	text-align: left;
	position: relative;
	box-shadow: none !important;
}
#panel ul {
	display: block;
	float: left;
	padding: 0;
	margin: 0;
	list-style: none;
	padding-top: 0px;
}
#panelRight li.s {
	min-width: 50px;
	display: block;
	height: 60px;
	position: relative;
}
#panel ul li {
	float: left;
	display: block;
	position: relative;
	height: 60px;
	padding: 0px 0px;
}
div#panelRight {
	padding-top: 0px;
}
/*------------------------------------Цвет текста*/

#panel ul li span,
#panel ul li a {
	display: block;
	color: #eee;
	text-decoration: none;
	font-size: 14px;
	line-height: 40px;
	height: 40px;
	padding: 0px 15px;
}
span.lb {
	line-height: 60px !important;
}
a.lb {
	line-height: 60px !important;
}
/*------------------------------------Ховер пунктов*/

#panel .hover .lb {
	color: #ed3247;
	text-shadow: 0 0 5px #000;
	transition: 0.3s;
}
#panel ul ul a:hover {
	color: #fff !important;
	text-shadow: 0 0 0px #000;
	background-color: #ed3247;
}
#panel ul ul li {
	float: left;
	height: auto;
	padding: 0px 0;
}
#panelEpisodes .foto {
	width: 60px !important;
	height: auto !important;
	float: left;
	margin-right: 0px;
	border: none;
}
/*------------------------------------Полоска под пунктом*/

#panel .hover .gb {
	background: #ed3247;
	-webkit-box-shadow: 0px 0px 5px 0px #ed3247;
	display: block;
	padding: 0px;
	margin: 0px;
	line-height: 0;
	height: 3px;
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	transition: 1s;
}
/*------------------------------------Поисковая строка*/

#findForm input {
	border: none;
	width: 1px;
	height: 25px;
	padding: 10px;
	padding-left: 39px;
	border-right: solid 0px #444444;
	border-left: solid 0px #444444;
	color: #ababab;
	font-size: 0px;
	cursor: pointer;
	background: url("http://i.imgur.com/WcL7zrS.png") 11px -134px no-repeat;
	outline: none;
	-webkit-transition: width 0.3s ease-out;
	-moz-transition: width 0.3s ease-out;
	-o-transition: width 0.3s ease-out;
	transition: width 0.3s ease-out;
	position: relative;
}
#findForm input:hover {
	background-position: -32px -134px;
	transition: 0.3s;
}
#findForm.active input {
	-webkit-transition: width 0.3s ease-out;
	-moz-transition: width 0.3s ease-out;
	-o-transition: width 0.3s ease-out;
	transition: width 0.3s ease-out;
	width: 200px !important;
	font-size: 14px !important;
	padding-left: 25px;
	padding-right: 55px;
	background: none;
	cursor: auto;
}
/*------------------------------------Кнопка "Найти"*/

#findForm.active button:hover {
	color: #ed3247 !important;
	text-shadow: none !important;
}
#findForm.active button {
	display: block;
	padding: 0;
	height: 45px;
	width: 54px;
	border: none;
	position: absolute;
	top: 0;
	right: 0;
	cursor: pointer;
	background: transparent;
	border-left: solid 0px #999;
	border-right: solid 0px #444444;
	color: #eee !important;
	font-size: 14px;
}
/*------------------------------------Икона выхода*/

#panelOut {
	display: inline-block;
	width: 1px;
	background-image: url("http://i.imgur.com/WcL7zrS.png");
	background-repeat: no-repeat;
	background-position: -6px -736px;
	transition: 0.3s;
	margin-top: 9px;
}
#panelOut:hover {
	background-position: -40px -736px;
	transition: 0.3s;
}
/*------------------------------------Кнопка входа ховер*/

#panelLoginButton:hover {
	color: #ed3247 !important;
	text-shadow: none !important;
}
#panel ul ul a:hover {
	color: #fff !important;
	text-shadow: 0 0 px #000;
}
#panel ul ul a.h:hover {
	color: #fff !important;
	background-color: #0074be;
	text-shadow: 0 0 0 !important;
}
#panel .delimiter {
	display: block;
	padding: 0;
	height: 4px;
	width: 100%;
	border-bottom: dashed 0px #808080;
	margin-bottom: 0px;
}
#panelLogin:hover img {
	box-shadow: 0 0 0px 0px #ed3247;
}
#panelEpisodes .number {
	color: #fff !important;
	font-weight: normal;
	text-decoration: none;
	text-align: center;
	font-size: 15px !important;
	line-height: 30px !important;
	padding: 0px 20px !important;
	background-color: #ed3247 !important;
	width: 110px !important;
	height: 30px !important;
	float: left !important;
}
#panelEpisodes .label_rus {
	color: #fff !important;
	text-decoration: none;
	text-align: left;
	font-size: 15px !important;
	line-height: 30px !important;
	overflow: hidden;
	height: 30px !important;
	padding-bottom: 10px !important;
	font-weight: 300;
	margin-top: 0px;
}
#panelEpisodes .right {
	float: right !important;
	width: 260px !important;
	padding: 0 !important;
	height: auto !important;
}
#notices {
	right: 0px !important;
	padding-top: 45px !important;
	top: 15px !important;
	background: transparent !important;
	border: none !important;
	box-shadow: none !important;
	font-weight: 300;
	font-size: 14px;
}
/*------------------------------------Все сообщения*/

#notices .footerLink {
	padding: 0;
	border-bottom: none;
	margin: 0;
	color: #ed3247;
	height: 35px !important;
	background-color: #484848;
}
/*------------------------------------Все сообщения ховер*/

#notices .footerLink a:hover {
	height: 25px !important;
	line-height: 25px !important;
	background-color: #ed3247 !important;
	color: #fff !important;
}
#panel ul ul {
	display: none;
	position: absolute;
	border-top: none;
	top: 60px;
	background-color: rgba(0, 0, 0, 0.9);
	border: solid 0px #333333;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
	-webkit-box-shadow: 0 3px 10px -2px #000;
}
#tabpm {
	border: solid 0px #333333 !important;
	width: 165px !important;
}
#tabep {
	border: solid 0px #333333 !important;
	width: 169px !important;
}
#panel .menuTab.hover {
	background: #ed3247;
	color: #ed3247 !important;
	transition: 0.3s;
}
#panel .menuTab.hover .l {
	color: #fff !important;
	transition: 0.3s;
}
#panel .menuTab {
	float: left;
	background-color: #484848;
	font-size: 14px;
	width: 165px;
	text-align: center;
	border: none;
	height: 35px;
	line-height: 35px;
	padding: 0 !important;
}
a#panelLoginButton {
	line-height: 60px !important;
}
#events b {
	background-color: #ed3247;
	border: 0px solid #000000;
	border-radius: 20px;
	color: #fff;
	font-size: 10px;
	height: 12px;
	line-height: 12px;
	padding: 2px;
	position: absolute;
	right: 15px;
	text-align: center;
	top: 21px;
	width: 12px;
	font-weight: 500;
}
.eventNew {
	background-color: #ed3247;
	border: 0px solid #000000;
	border-radius: 20px;
	color: #fff;
	font-size: 12px;
	height: 12px;
	line-height: 10px;
	padding: 2px;
	position: absolute;
	left: -3px;
	text-align: center;
	top: 8px;
	width: 12px;
	font-weight: 600;
	-webkit-box-shadow: 0 1px 3px #000;
}
ul#panelEpisodes {
	width: 334px !important;
}
ul#panelMessages {
	width: 334px !important;
}
/*------------------------------------ПАНЕЛЬ ЛОГИНА------------------------------------*/

#loginWindow {
	display: block;
	background: #fff;
	position: fixed;
	top: 50%;
	left: 50%;
	margin-top: -140px;
	margin-left: -200px;
	z-index: 10000;
	padding: 25px;
	border: solid 0px #888888;
	width: 350px;
	height: 240px;
	background-image: none;
	background-repeat: no-repeat;
	background-position: center bottom;
	border-radius: 3px;
	-webkit-box-shadow: 0 2px 10px #000;
}
#loginWindow h4 {
	font-weight: Bold;
	font-size: 18px;
	color: #ed3247;
	margin-bottom: 10px;
	margin-left: 10px;
}
#loginWindow .text {
	border: none;
	background: #e8e8e8;
	color: #8b8a8a;
	font-size: 14px;
	padding: 10px;
	padding-top: 7px;
	padding-bottom: 7px;
	display: block;
	width: 180px;
	margin-bottom: 10px;
}
#loginWindow .forget {
	font-size: 12px;
	text-decoration: underline;
}
#panel a:hover {
	color: #ed3247;
	text-decoration: none;
}
#loginWindow .button {
	padding: 10px;
	/*padding-top: 7px;
  padding-bottom: 7px;*/
	
	width: 200px;
	text-align: center;
	margin-bottom: 15px;
}
#loginWindow .button:hover {
	-webkit-box-shadow: 0 3px 15px #888;
	transition: 0.3s;
}
.button {
	background-color: #ed3247;
	color: white !important;
	text-decoration: none;
	padding: 5px;
	padding-left: 15px;
	padding-right: 15px;
	display: block;
	float: left;
	line-height: 20px;
	font-size: 14px;
	font-weight: normal;
	border: none !important;
	cursor: pointer;
	margin-right: 25px;
	border-radius: 3px;
}
/*------------------------------------Разделитель*/

#loginWindow hr {
	display: block;
	padding: 0;
	height: 2px;
	border: none;
	background: #f1f1f1;
	margin-bottom: 10px;
	margin-top: 10px;
}
/*------------------------------------Выключить показ соцсетей*/

#loginWindow .rc {
	float: left;
	padding-left: 25px;
	width: 125px;
	overflow: hidden;
	display: none;
}
/*------------------------------------Р О Т А Т О Р------------------------------------*/

#rotator {
	width: 810px;
	height: 313px;
	overflow: hidden;
	margin-top: 50px;
	/*background: #f1f1f1;*/
    background: #000;
	border-radius: 10px;
}
#rotator .shapka {
	background: none;
	background-position: left 5px;
	height: 30px;
	background-size: 810px;
	width: 810px;
	overflow: visible;
	position: relative;
	z-index: 30;
	margin-bottom: 0px;
}
#rotator .slider {
	width: 810px;
	height: 260px;
	position: relative;
	overflow: visible;
	/*background: #f1f1f1;*/
    background: #000;
}
#rotator .slider .item {
	float: left;
	width: 724px;
	height: 260px;
	position: relative;
	color: #cccccc !important;
	font-size: 11px;
	text-decoration: none !important;
	text-align: left;
	overflow: hidden;
	background-position: center top;
	background-repeat: no-repeat;
	z-index: 20;
}
#rotator .slider .prev:hover {
	background-position: 81px 2px;
}
#rotator .slider .prev {
	display: block !important;
	position: absolute;
	z-index: 20;
	width: 30px;
	height: 35px;
	right: 0px;
	margin-top: 20px;
	background-image: url("http://i.imgur.com/bGRHgRz.png");
	background-position: 109px 2px;
	background-size: auto 35px;
	box-sizing: border-box;
	text-indent: -999px;
	visibility: visible;
	transition: 0.3s;
}
#rotator .slider:hover .slides_container {
	/*-webkit-box-shadow: 0 5px 30px -3px #333;*/
    -webkit-box-shadow: 0 5px 30px -3px #000;
	transition: 0.5s;
}
#rotator .slider .slides_container {
	width: 724px;
	height: 260px;
	margin: 0 auto;
	border-radius: 3px;
	/*-webkit-box-shadow: 0 1px 5px #999;*/
    -webkit-box-shadow: 0 1px 5px #000;
}
#rotator .slider:hover .next {
	background-position: 28px 2px;
}
#rotator .slider .next:hover {
	background-position: 54px 2px;
}
#rotator .slider .next {
	display: block !important;
	position: absolute;
	z-index: 20;
	width: 30px;
	height: 35px;
	right: 0px;
	margin-top: 20px;
	background-image: url("http://i.imgur.com/bGRHgRz.png");
	background-position: 28px 2px;
	background-size: auto 35px;
	box-sizing: border-box;
	text-indent: -999px;
	visibility: visible;
	transition: 0.3s;
}
#rotator .slider .pagination a {
	display: inline-block;
	padding: 8px;
	height: 0 !important;
	width: 0 !important;
	text-decoration: none;
	font-size: 0px !important;
	text-indent: -100px !important;
	background-image: url("http://i.imgur.com/dBoOIZF.png");
	background-position: center -18px;
	background-repeat: no-repeat;
	overflow: hidden !important;
	transition: 0.3s;
}
#rotator .slider .pagination {
	visibility: visible;
	display: block;
	list-style: none;
	text-align: center;
	padding: 0px;
	margin: 0;
	position: absolute;
	bottom: -26px;
	right: 15px;
	z-index: 40;
	width: 810px;
}
#rotator .shapka .clearfix {
	position: absolute;
	padding: 0 339px;
}
#rotator .shapka a.active:hover {
	color: #fff;
}
#rotator .shapka a.active {
	background-image: none;
	background: #ec3247;
	background-repeat: no-repeat;
	background-position: center 0px;
	width: 130px;
	height: 40px;
	text-align: center;
	float: left;
	display: block;
	font-size: 14px;
	color: #fff;
	text-decoration: none;
	line-height: 41px;
	font-weight: normal;
	text-shadow: none;
	border-bottom: dotted 0px;
	background-size: 200px 140px;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
	-webkit-box-shadow: 0 3px 8px -3px #000;
}
#rotator .shapka a:hover {
	color: #008ACE;
}
#rotator .shapka a {
	width: 51px;
	height: 26px;
	text-align: center;
	display: block;
	float: left;
	font-size: 14px;
	color: #ed3247;
	line-height: 37px;
	font-weight: bold;
	text-shadow: none;
	border-bottom: dashed 1px;
	margin: -5px 10px;
	transition: none;
}
.shapka a.active:after {
	content: " аниме";
}
#rotator .slider:hover .item .head {
	background: none
	/*url("http://animeonline.su/templates/main/images/rotator/head.png") no-repeat*/
	
	;
	background-color: rgba(0, 0, 0, 0.7);
	transition: 0.5s;
}
#rotator .slider .item .head {
	/*Подложка названия*/
	
	background: none
	/*url("http://animeonline.su/templates/main/images/rotator/h..") no-repeat*/
	
	;
	background-color: rgba(0, 0, 0, 0.0);
	background-size: 350px 265px;
	color: #fff;
	width: 690px;
	height: 264px;
	padding-left: 25px;
	margin: 0px;
	display: block;
	text-decoration: none;
	position: relative;
	z-index: 10;
	margin-bottom: 0px;
	padding-right: 10px;
	padding-top: 0px;
	transition: 0.3s;
}
/*------------------------------------Ховер ротатора*/

#rotator .slider .item .head:hover {
	color: #ed3247 !important;
	text-shadow: 0 1px 3px rgb(237, 50, 71);
	transition: 0.3s;
	text-align: left;
}
/*------------------------------------Русское название*/

#rotator .slider .item .label_rus {
	font-size: 40px !important;
	overflow: visible;
	height: 26px;
	line-height: 26px;
	font-weight: bold;
	display: block;
	text-align: center;
	padding: 20px 0 0 0;
	text-shadow: 0 1px 15px rgb(0, 0, 0);
}
/*------------------------------------Английское название*/

#rotator .slider .item .label_eng {
	font-size: 14px;
	overflow: visible;
	height: 6px;
	line-height: 60px;
	display: block;
	text-align: center;
	text-shadow: 0 0px 0px rgb(0, 0, 0);
}
#rotator .watch {
	display: none;
}
#rotator .slider:hover .item .descr {
	visibility: visible;
	opacity: 1;
	transition: 0.2s;
}
/*------------------------------------Описание аниме*/

#rotator .slider .item .descr {
	color: white;
	font-size: 13px;
	line-height: 140%;
	visibility: hidden;
	text-shadow: 0 0px 0px black;
	width: 580px !important;
	opacity: 0;
}
#rotator .slider .item .body {
	background: none;
	width: 680px;
	height: 119px;
	padding: 10px 10px 10px 25px;
	margin-left: 0px;
	position: relative;
	z-index: 10;
	bottom: 143px;
}
#rotator:hover .date {
	visibility: visible;
}
#rotator .date {
	padding: 0px;
	float: right;
	text-align: center;
	background-image: none
	/*url("../images/rotator/calendar_date.png")*/
	
	;
	background-repeat: no-repeat;
	background-position: center top;
	width: 80px;
	height: 100px;
	color: #E32443;
	font-size: 40px;
	padding-top: 0px;
	visibility: hidden;
}
#rotator .btn {
	display: none
}
/*------------------------------------Н О В О С Т Ь------------------------------------*/
/*------------------------------------Верх новости*/

div.new_ .head_ {
	font-weight: normal;
	background: none !important;
	position: relative;
	text-align: left;
	width: 430px;
	line-height: 16px;
	border: none !important;
	border-bottom: solid 1px #F0F0F0 !important;
	padding: 10px 10px 30px 10px;
	margin-left: 320px;
}
div.new_ .head_ a.full_link {
	display: none;
	padding: 0px;
	float: left;
	text-decoration: none !important;
	width: 505px;
}
div#full_news h2 {
	background: none;
	font-size: 20px;
	line-height: 30px;
	color: black;
}
ul {
	padding-left: 40px;
}
div#full_news h1 {
	background: none !important;
	font-size: 26px !important;
	line-height: 35px;
	text-shadow: #000 0px 0px 0px !important;
	color: black !important;
}
h1 span {
	color: black !important;
}
p.normal span {
	color: #272727 !important;
	font-size: 14px !important;
	line-height: 140%;
}
span div {
	margin: 0px 10px 20px 10px;
}
h1 {
	display: block;
	font-size: 4em !important;
	-webkit-margin-before: 0.67em;
	-webkit-margin-after: 0.67em;
	-webkit-margin-start: 0px;
	-webkit-margin-end: 0px;
	font-weight: 600 !important;
	color: black !important;
}
