css:
@-moz-document domain("se7ensins.com") {

img[src*="logo_bg"]{
    width:0;
    height:100px;
    padding-right:375px;
    background:transparent url(http://i.imgur.com/3eRUbPJ.png?1) top left no-repeat;
}
#userBox {
    background: rgba(26,26,26,0.7);
}
#logoBlock { background: url("http://sig.grumpybumpers.com/host/Ethogmxcom.gif") no-repeat scroll 0% 0% rgb(26, 26, 26) !important;}
.sidebar .section .secondaryContent h3 {
    font-weight: bold;
}
    /* Member card stats and last activity in two columns Code provided by xGfcHoster */
    .xenOverlay.memberCard .userInfo .userStats {
    float: left;
    width: 49% !important;
    border-right: 1px solid #556655 !important;
    }

    .xenOverlay.memberCard .userInfo .userStats dd {
    float: right !important;
    }


    .xenOverlay.memberCard .userInfo .userStats dt  {
    float: left;
    clear: both;
    }

    .xenOverlay.memberCard .lastActivity {
    float: right;
    width: 49% !important;
    margin-top: 0px !important;
    padding-top: 0px !important;
    border-top: 0 !important;
    }

    .xenOverlay.memberCard .lastActivity.pairsInline dt {
    float: left;
    clear: both;
    }

    .xenOverlay.memberCard .lastActivity.pairsInline dd {
    float: center;
    clear: both;
}
}
