css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cokeandpopcorn.ch")
{
  body
   { background: url(http://i.imgur.com/nRjF8DN.jpg) #000 scroll left top !important; }
  
  #right p.rightTop,
  #right,
  #right p.rightBottom
   {
     background-color: rgba(0,0,0,0.5) !important;
     background-image: none !important;
   }
  
  .announcement
   {
     background-color: rgba(0,0,0,0.5);
     color: #83837d;
   }
  
  #right img.cover
   {
     border-top-color: rgba(255,255,255,0.1);
     border-right-color: rgba(255,255,255,0.1);
     border-bottom-color: rgba(255,255,255,0.1);
     border-left-color: rgba(255,255,255,0.1);
   }
  
  #right div.details div.title,
  #right div.details,
  #right div.banner728 div,
  .midPage,
  .midPage2,
  .left,
  .left2,
  .left3,
  .left4,
  .left5,
  .left6,
  .left7,
  .left8,
  #rightcol1,
  #rightcol2,
  #rightcol3
   {
     background-color: transparent !important;
     border-top-color: rgba(255,255,255,0.1);
     border-right-color: rgba(255,255,255,0.1);
     border-bottom-color: rgba(255,255,255,0.1);
     border-left-color: rgba(255,255,255,0.1);
     color:#83837d;
   }
  
  span.boxTitle,
  h3
   { border-bottom-color: #62625c; }
}
