css:
@-moz-document domain("web.meridianvk.com") {
body, .sidebar, .win-itembox {
	background-color: /*[[Color2]]*/ !important
}

.albumItem.active {
	background-color: /*[[Color1]]*/ !important;
	fill: /*[[Color1]]*/ !important;
	color: #fff !important;
}

.audioItem:hover .audioButton, #playerControl #playerControls .controlButton:hover, #playerControl .coverButtonsBlock .coverButton#shuffle.active, #playerControl .coverButtonsBlock .coverButton#repeat.active {
	fill: /*[[Color1]]*/ !important;
}

.audioItem.active span, .audioItem.active .artist {
	color: /*[[Color1]]*/ !important;
}

.menuItem.active, .win-pivot .win-pivot-header.win-pivot-header-selected, .win-pivot button.win-pivot-header.win-pivot-header-selected:hover, .win-pivot.win-pivot-locked .win-pivot-header.win-pivot-header-selected, .win-pivot button.win-pivot-header:focus, .win-pivot button.win-pivot-header.win-pivot-header:hover, .win-pivot button.win-pivot-header.win-pivot-header:hover:active, html.win-hoverable .win-pivot button.win-pivot-header-selected:hover, #playerControl #coverBlock  {
	background-color: /*[[Color1]]*/ !important;
}

#progressBar .ui-slider-range {
	background: /*[[Color1]]*/ !important;
}

  #playerControl #playerControls .controlButton {
    width: 36px !important;
  }
  #playerControl #playerControls {
    height: 45px !important;
  }
  #volumeBar {
    height: 21px !important;
    margin-top: -13px !important;
    cursor: pointer !important;
    left: 0px !important;
  }
  #progressBar {
    height: 18px !important;
    left: 0px !important;
    margin-top: -12px !important;
    padding-bottom: 5px !important;
    cursor: pointer !important;
  }
  #progressBarContainer {
    margin: 15px 0px -7px !important;
  }
  #playerControl #playerControls {
    margin: 8px 0 -10px !important;
  }
  #volumeBar .ui-slider-range {
    background: none repeat scroll 0% 0% /*[[Color1]]*/ !important;
    opacity: 0.7 !important;
  }
  #volumeBar .ui-slider-range:hover {
    background: none repeat scroll 0% 0% /*[[Color1]]*/ !important;
    opacity: 0.95 !important;
  }
}
