css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com") {
html {
	min-height:100%;
}

body.fbIndex {
	background: url("http://fc03.deviantart.net/fs71/i/2012/069/5/5/sleeping_with_butterflies_by_tincek_marincek-d4s80fk.jpg") 50% 50% no-repeat !important;
	background-size: cover !important;
	height:100%;
	overflow:hidden;
}

.loggedout_menubar_container {
	background:none !important;
}
.loggedout_menubar {
	background:rgba(60,50,150,.6) !important;
	padding:0 10px 14px 10px;

	-webkit-border-radius: 4px;
	border-radius: 4px;

	-webkit-box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
	box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
}
.loggedout_menubar label,
.login_form_label_field a {
	color:#fff !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
	display: none !important
}

.fbIndex .loggedout_menubar_container {
	position: fixed !important;
	width: 480px !important;
	height: 82px !important;
	min-width: 0 !important;
	top: 50% !important;
	left: 50% !important;
	margin: -60px 0 0 -240px !important;
	z-index: -1 !important;
}


.fbIndex .loggedout_menubar {
	width: auto !important;
}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {
	float: none !important;
}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
	display: block !important;
	margin: 0 auto !important;
}

.fbIndex .loggedout_menubar_container .lfloat img {
	display: block;
	margin: -60px auto 20px !important;
}

#SetAsHomepage_Callout {
	  display: none;
}

.inputtext{
          color:#000 !important;
          background:#fff !important;
}

.fb_logo {
	background:url("data:image/svg+xml;base64,") 50% 50% no-repeat !important;
	background-size:100% auto !important;

	width: 200px;
	margin: 12px 0 4px -10px;

}
}
