/*
Secure Login
https://addons.mozilla.org/en-US/firefox/addon/secure-login/
Style based on 
https://userstyles.org/styles/105345/secure-login-firefoxaddon-oldstyle-key-icon
*/


@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/*SECURE LOGIN - TOOLBARBUTTON, STATUSBAR ICON - AUSTRALIS STYLE KEY (TRANSBG) - ENABLED*/
#secureLoginButton .toolbarbutton-icon,
#secureLoginPanelIcon
{list-style-image: url("data:image/png;base64,") !important;}

/*SECURE LOGIN - TOOLBARBUTTON, STATUSBAR ICON - PRESSED*/
#secureLoginButton:hover:active .toolbarbutton-icon,
#secureLoginPanelIcon:hover:active
{list-style-image: url("data:image/png;base64,") !important;}

/*SECURE LOGIN - TOOLBARBUTTON, STATUSBAR ICON -  AUSTRALIS STYLE KEY (TRANSBG) - DISABLED*/
#secureLoginButton.toolbarbutton-1.secureLoginButtonDisabled .toolbarbutton-icon,
#secureLoginPanelIcon.secureLoginIconDisabled 
{list-style-image: url("data:image/png;base64,") !important;}

/*===========================================================================*/
/*TOOLS MENU MENUITEM ICON*/
#secureLoginToolsMenu.menu-iconic
{list-style-image:none !important;
-moz-appearance: none !important;
background:  url("data:image/png;base64,") !important;
margin-left:-0px !important;
background-repeat:no-repeat!important;}

/*===========================================================================*/

/*RIGHTCLICK CONTEXT MENU MENUITEM ICON*/
#secureLoginContextMenuItem .menu-iconic-left,
#secureLoginContextMenuMenu .menu-iconic-left
{list-style-image:none !important;
-moz-appearance: none !important;
background:  url("data:image/png;base64,") !important;
width: 19px!important;
margin-right:11px !important;
margin-left: 0px !important;
background-repeat:no-repeat!important;}

/*===========================================================================*/

/*END*/
