css:
@-moz-document domain("www.roblox.com") {
.forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th
{ background: #BD0016 !important; }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button
{ background-color: #BD0016 !important; border-color: #BD0016; }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover
{ background: #BD0016 !important; border-color: #BD0016; }

#TopAbpContainer
{ display: none; }

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer
{ display: none; }

#party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off
{ background: linear-gradient(to bottom, #e53935, #b71c1c); }

#ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4)
{ display: none; }
/*why why manti*/

#header
{ background-color: #BD0016; }

.navigation .notification-icon
{ background: #A00000; }

.forum-table-header, body .forum-table-header th, body .forum-table-footer
{ background: #A00000 !important; }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more
{ background-color: #BD0016 !important; }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover
{ background: #BD0020 !important; }

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer
{ display: none; }

#MasterContainer, .nav-content-inner, .container-main, .gutter-ads
{ background: url(http://www.cityrider.com/fixed/43aspect.png) top center repeat-x #DDDDDB; }

.content
{ background-color: #FFFFFF; }

.games-list:hover .scroller, .scroller:hover
{ background-color: rgba(100, 100, 100, 0); box-shadow: 0 0 3px 0 rgba(204, 204, 204, 0); }

.scroller
{ background-color: rgba(100, 100, 100, 0); box-shadow: 0 0 3px 0 rgba(204, 204, 204, 0); }

.btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited
{ border: solid 1px rgba(127, 127, 127, 0); background-color: #BD0016; background-image: url(http://www.cityrider.com/fixed/43aspect.png); color: white; }

div#friend_dock_thumb_container
{ box-shadow: 0px 1px 5px #888888; }

.redesign .tab_white_31h_container .ajax__tab_inner, .tab, .tab-container>div
{ border: solid 1px rgba(127, 127, 127, 0); }

div.chat-header-blink-off
{ background-color: #BD0016; background-image: linear-gradient(to bottom, #BD0016, #BD0016); }

.party-window .title
{ background-color: #BD0016; background-image: linear-gradient(to bottom, #BD0016, #BD0016); }

.chat-sender-self
{ font-weight: bold; color: #BD0016; }

.party-window .main
{ border: 1px solid rgba(166, 166, 166, 0); }

#chat_input textarea
{ border: 1px solid rgba(170, 170, 170, 0); }

#chat_messages
{ border: 1px solid rgba(170, 170, 170, 0); }

div.friend_dock_chatbox
{ border: solid 1px rgba(127, 127, 127, 0); }

div.friend_dock_chatbox_chat
{ border: solid 1px transparent; }

textarea
{ border-color: rgb(169, 169, 169, 0); }

div#friend_dock_thumb_container
{ border: solid 1px rgba(127, 127, 127, 0); }

.btn-primary
{ color: #fff; background-color: #BD0016; border-color: #BD0016; }

.btn-primary, .btn-primary:link, .btn-primary:active, .btn-primary:visited
{ background-image: url(http://www.cityrider.com/fixed/43aspect.png)!important; }

.btn-control, .btn-control:active, .btn-control:link, .btn-control:visited, .btn-control:hover
{ border: 1px solid rgba(119, 119, 119, 0); color: #FFF; background-color: #BD0016; }

.btn-control.btn-control-medium
{ background-image: url(http://www.cityrider.com/fixed/43aspect.png); }

.btn-control.disabled
{ border: 1px solid rgba(204, 204, 204, 0); color: rgba(255, 255, 255, 0.62); }

#MasterContainer, .nav-content-inner, .container-main, .gutter-ads
{ background: url(http://www.cityrider.com/fixed/43aspect.png) top center repeat-x #FFFFFF; }

.btn-control.btn-control-large
{ height: 25px; line-height: 24px; font-size: 13px; padding: 0 7px; background-image: url(http://www.cityrider.com/fixed/43aspect.png); }

.GrayDropdown .Button
{ background: url(http://i.imgur.com/Z7U9uDM.png) no-repeat right top; border: 1px solid rgba(119, 119, 119, 0); color: #fff; }

.pager.next
{ background: url('http://i.imgur.com/fZ3KJvh.png') no-repeat top left; }

.pager.previous
{ background: url('http://i.imgur.com/kA1nEgo.png') no-repeat top left; }

.roblox-message-large-btn
{ background-image: url(http://www.cityrider.com/fixed/43aspect.png); }

.filter select
{ background: #BD0016; border: 1px solid rgba(165, 165, 165, 0); color: white; }

#searchbox
{ padding: 1px 22px 1px 7px; border: 1px solid #bbb; color: #D85C6A; height: 20px; background-color: #BD0016; }

.rbx-header .rbx-nav-collapse .rbx-nav-notification
{ position: absolute; left: 18px; top: 8px; width: 12px; height: 12px; border-radius: 50%; background-color: #ffffff; }

.rbx-highlight
{ background-color: #BD0016; font-size: 14px; font-weight: 300; color: #fff; text-align: center; border-radius: 12px; }

.notifications-count
{ border: 1px solid #BD0016; background-color: #BD0016; color: #ffffff; padding: 0 4px; font-weight: normal; font-size: 12px; margin-left: 2px; }

.rbx-left-col
{ background-color: #FFFFFF; }

.rbx-left-col
{ box-shadow: 0 0 20px rgba(25, 25, 25, 0.5); }

.rbx-text-notes
{ color: #6F6F6F; }

.rbx-btn-primary, .rbx-btn-primary-lg, .rbx-btn-primary-md, .rbx-btn-primary-sm, .rbx-btn-primary-xs
{ background-color: #BD0016; border-color: #BD0016; }

.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading
{ box-shadow: 0 -4px 0 0 #BD0016 inset; }

.rbx-link, .rbx-link-sm, .rbx-link-xs, .rbx-link:link, .rbx-link-sm:link, .rbx-link-xs:link, .rbx-link:visited, .rbx-link-sm:visited, .rbx-link-xs:visited, .rbx-link:active, .rbx-link-sm:active, .rbx-link-xs:active
{ color: #BD0016; transition: all, 0.2s, ease-in-out; font-weight: 300; }

#GamesPageLeftColumn
{ width: auto; margin-right: 10px !important; margin-left: 10px !important; }

#GamesListsContainer > div
{ background-color: transparent !important; width: 100% !important; }

#GamesPageSearch
{ float: right; position: relative; padding-top: 6px; right: -337px; z-index: 500; }

#videoPrerollPanel
{ background-color: #ffffff !important; height: auto !important; border: 1px solid white !important; }

#videoPrerollPanel *
{ color: black !important; }

#videoPrerollLoadingDiv
{ padding-top: 10px !important; padding-bottom: 10px; }

#videoPrerollLoadingBar
{ border-color: #fff !important; }

#videoPrerollLoadingBarCompleted
{ background-color: #BD0016 !important; }

#videoPrerollTitleDiv, #videoPrerollMainDiv, #videoPrerollTitleData, #videoPrerollJoinBC, #videoPrerollCompanionAd
{ display: none !important; color: #000000; }

element.style
{ background-color: rgba(189, 0, 22, 0.4); }

#LoginButton, #SignUpButton
{ background-color: #BD0016; color: white; border: 1px solid #a40013; font-weight: 400; }

.navbar-landing
{ background-color: #BD0016 !important; color: white; min-height: 66px; }

#RollerContainer
{ background-image: url('http://therandywhite.com/wp-content/uploads/2014/11/white.png') !important; background-repeat: no-repeat !important; background-size: cover !important; background-color: #fff !important; }

#SignUpFormContainer
{ background-color: white; background-color: rgba(189, 0, 22, 1); padding: 15px; max-width: 435px; color: #fff; border-radius: 25px; border: 2px solid #fff; }

#BodyWrapper
{ margin-right: 0px; margin-left: 0px; }

#catalog
{ width: 1340px; }

.right-content
{ float: left; width: 1040px; margin-left: -1px; padding-left: 20px; z-index: 1; position: relative; }

.right-content .btn-medium.btn-primary
{ position: absolute; right: 70px; top: -10px; }

div#catalog .CatalogSearchBar
{ padding-left: 9px; float: right; padding-top: 5px; padding-right: 200px; }

img.original-image
{ border: 1px solid #FFF; }

div#catalog div#dropdown ul#dropdownUl
{ margin: 0; padding: 0; list-style: none; width: 134px; border: 1px solid #EFEFEF; background: #efefef; }

.redesign .tab_white_31h_container .ajax__tab_inner, .tab, .tab-container>div
{ float: left; background-color: #CCCCCC; padding: 7px; border: 1px solid #9e9e9e; font-weight: bold; font-size: 15px; margin: 4px 2px 0 1px; border-bottom-width: 0; position: relative; top: -1px; }

.btn-control.btn-control-small
{ height: 18px; line-height: 18px; font-size: 11px; background-image: url(http://pre06.deviantart.net/f9e8/th/pre/i/2014/037/c/8/invisible_girl_in_front_of_transparent_background_by_horemweb-d75djfq.png); background-color: #bd0016; }

.redesign .tab_white_31h_container .ajax__tab_inner, .tab, .tab-container>div
{ float: left; background-color: #CCCCCC; padding: 7px; border: 1px solid #CCCCCC; font-weight: bold; font-size: 15px; margin: 4px 2px 0 1px; border-bottom-width: 0; position: relative; top: -1px; }

.chat-main .chat-header
{ background-color: #BD0016; }

.dialog-container .dialog-header .dialog-header-title
{ position: relative; overflow: hidden; background-color: #BD0016; white-space: nowrap; padding: 9px 0 10px 12px; width: 100%; }

.dialog-container .dialog-header
{ background-color: #BD0016; }

.dialog-container .dialog-header .dialog-header-title:after
{ background-image: linear-gradient(to right, rgba(0, 162, 255, 0) 0, #BD0016 90%); content: ""; height: 16px; position: absolute; right: 0; bottom: 0; width: 10%; }
}
