css:
@-moz-document domain("xat.com") {

/*main.css, css included on all pages*/

/*********/
/* theme */
/*********/

html {
    margin: 0;
}
body {
    position: relative;
    padding-top: 69px;
    background: #000000;
    font-size: 14px;
    font-family: 'Lato', sans-serif !important;
    color: #333;
}

/*scrolltop
-------------------------------------------------- */

.scrolltop {
    background-color: #F1F1F1;
    border: 1px solid darkGray;
    border-radius: 100px 100px 100px 100px;
    -webkit-border-radius: 100px;
    -moz-border-radius: 100px;
    display: none;
    height: 36px;
    opacity: 0.6;
    filter: alpha(opacity=60);
    position: fixed;
    right: 2%;
    top: 50%;
    width: 36px;
    z-index: 33;
}
.scrolltop span {
    position: relative;
    top: 7px;
    left: 10px;
}

/*old versions of IE patches
-------------------------------------------------- */

.old-ie #hero .carousel-inner .row {
    padding-left: 55px;
}
.old-ie #box_login .box .social a.face_login span.text {
    font-size: 16px;
    margin: 8px 0 0 10px;
}
.old-ie .nav-collapse.collapse {
    height: auto !important;
    overflow: visible !important;
}

/*bootstrap overides
-------------------------------------------------- */

textarea:focus,
input[type="text"]:focus,
input[type="password"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="time"]:focus,
input[type="week"]:focus,
input[type="number"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="search"]:focus,
input[type="tel"]:focus,
input[type="color"]:focus,
.uneditable-input:focus {
    /*border-color: #006dcc;*/
    outline: 0;
    outline: thin dotted \9;
    /* IE6-9 */
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px 1px #0cacd3;
    -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px 1px #0cacd3;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px 1px #0cacd3; 
}

/*navbar
-------------------------------------------------- */

.navbar-fixed-top {
    margin-bottom: 0;
}

.navbar-inner {
    border-bottom: 0 none;
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
    -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
    box-shadow: 0 1px 2px 5px rgba(0, 0, FF, 0.5);
    filter: none;

    /*blue grad*/
    background: #00002b;
    background-color: #00002b;
    background: none repeat scroll 0 0 #00002b;
    background-image: -moz-linear-gradient(top, #000011, #000066);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#000011), to(#000066));
    background-image: -webkit-linear-gradient(top, #000011, #000066);
    background-image: -o-linear-gradient(top, #000011, #000066);
    background-image: linear-gradient(to bottom, #000011, #000066);
    background-repeat: repeat-x;
}

.navbar .brand {
    font-size: 26px;
    text-shadow: none;
}
.navbar .nav.pull-right {
    top: 10px;
}
.navbar .nav > li > a {
    color: #F8F8F8;
    font-size: 15px;
    font-weight: bold;
    text-shadow: none;
    -webkit-transition: all .2s linear;
    -moz-transition: all .2s linear;
    -ms-transition: all .2s linear;
    -o-transition: all .2s linear;
    transition: all .2s linear;	
}
.navbar .nav > li > a:hover {
    color: #ccc;		
}
.navbar .nav > li.active > a,
.navbar .nav > li.active:hover > a,
.navbar .nav > .active > a,
.navbar .nav > .active > a:hover,
.navbar .nav > .active > a:focus {
    padding-bottom: 4px;
    -webkit-box-shadow: none;
    box-shadow: none;
    -moz-box-shadow: none;
    background: none;
    color: #fff;
}
.btn-dropnav {
    float: right;
    margin-left: 5px;
    margin-right: 5px;
    padding: 7px 10px;
    display: none;
}
.btn-dropnav .icon-bar {
    background-color: #F5F5F5;
    -webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, 0.25);
    -moz-box-shadow: 0 1px 0 rgba(0, 0, 0, 0.25);	
    box-shadow: 0 1px 0 rgba(0, 0, 0, 0.25);
    display: block;
    height: 2px;
    width: 18px;
}
.btn-dropnav .icon-bar + .icon-bar {
    margin-top: 3px;
}
.navbar .nav li.dropdown.open > .dropdown-toggle, 
.navbar .nav li.dropdown.active > .dropdown-toggle, 
.navbar .nav li.dropdown.open.active > .dropdown-toggle {
    background: none;
    color: #fff;
}
.dropdown-menu li > a:hover,
.dropdown-menu li > a:focus,
.dropdown-submenu:hover > a {
    background-color: #475B77 !important;
    text-decoration: none;
    color: #ffffff;
    background-image: -moz-linear-gradient(top, #475B77, #475B77);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#475B77), to(#475B77));
    background-image: -webkit-linear-gradient(top, #475B77, #475B77);
    background-image: -o-linear-gradient(top, #475B77, #475B77);
    background-image: linear-gradient(to bottom, #475B77, #475B77);
    background-repeat: repeat-x;
}
.navbar .btn-navbar .icon-bar {
    display: block;
    width: 18px;
    height: 2px;
    background-color: #0cacd3;
    -webkit-border-radius: 1px;
    -moz-border-radius: 1px;
    border-radius: 1px;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
@media (min-width: 980px) {
    .navbar .nav .btn-header {
        /*blue grad*/
        background-color: #000033;
        background-image: -moz-linear-gradient(top, #1e1e51, #000033);
        background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#1e1e51), to(#000033));
        background-image: -webkit-linear-gradient(top, #1e1e51, #000033);
        background-image: -o-linear-gradient(top, #1e1e51, #000033);
        background-image: linear-gradient(to bottom, #00003f, #00008d);

        border: 1px solid #1e1e1e;
        border-radius: 6px;
        display: inline-block;
        height: 10px;
        line-height: 8px;
        position: relative;
        top: 4px;
        margin-left: 8px;
    }
    .navbar .nav .btn-header:hover {
        /*blue grad*/
        background: #000051;
        background-color: #000051;
        background-image: -moz-linear-gradient(top, #3c3c6f, #000051);
        background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#3c3c6f), to(#000051));
        background-image: -webkit-linear-gradient(top, #3c3c6f, #000051);
        background-image: -o-linear-gradient(top, #3c3c51, #000051);
        background-image: linear-gradient(to bottom, #00008d, #0000b4);
    }
}
@media (max-width: 979px) {
    .nav-collapse .nav > li > a:hover,
    .nav-collapse .dropdown-menu a:hover {
    background: none !important;
    }
    .nav-collapse .nav > li > a, .nav-collapse .dropdown-menu a {
        color: #ccc;
    }
}

/*footer
-------------------------------------------------- */

#footer {
    /*blue grad*/
    background: #00002b;
    background-color: #00002b;
    background: none repeat scroll 0 0 #00002b;
    background-image: -moz-linear-gradient(top, #000011, #000066);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#000011), to(#000066));
    background-image: -webkit-linear-gradient(top, #000011, #000066);
    background-image: -o-linear-gradient(top, #000011, #000066);
    background-image: linear-gradient(to bottom, #000011, #000066);
    background-repeat: repeat-x;

    min-height: 50px;
    margin-top: 10px;
    color: #fff;
    padding-top: 10px;
    padding-bottom: 10px;
}
#footer li
{
    display:inline;
    padding-left: 5px;
    padding-right: 5px;
}

#footer ul
{
    text-align: center;
}
#footer p
{
    text-align: center;
}

/*section header
-------------------------------------------------- */

.section_header {
    text-align: center;
    font-weight: bolder;
    color: #252b34;
    margin-top: 80px;
    position: relative;
    margin-bottom: 40px;
}
.section_header.left span {
    margin-left: 0px;
    text-align: left;
}
.section_header hr {
    border-top: 1px solid #BEC0C3;
    border-bottom: 2px solid white;
    width: 36%;
    position: absolute;
}
.section_header hr.left { }
.section_header hr.right {
    right: 0px;
    top: 0px;
}
.section_header span {
    display: block;
    margin: 0 auto;
}
.section_header small {
    margin-left: 20px;
    font-size: 16px;
    color: #7f8387;
    display: none;
}

/*page
-------------------------------------------------- */

.page {
  min-height: 20px;
  padding: 19px;
  margin-bottom: 20px;
  background-color: #ffffff;
  border: 1px solid #e3e3e3;
  -webkit-border-radius: 4px;
     -moz-border-radius: 4px;
          border-radius: 4px;
  -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
     -moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
          box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
}

/*glyphicon-xat
*/

[class^="xicon-"],
[class*=" xicon-"] {
  display: inline-block;
  width: 15px;
  height: 15px;
  margin-top: 1px;
  margin-right: .3em;
  line-height: 15px;
  vertical-align: text-top;
  background-image: url("http://xat.com/images/glyphicons-xat.png");
  background-position: 15px 15px;
  background-repeat: no-repeat;
}

.xicon-about {
  background-position: -8px -8px;
}
.xicon-auction {
  background-position: -31px -8px;
}
.xicon-contact {
  background-position: -54px -8px;
}
.xicon-groups {
  background-position: -77px -8px;
}
.xicon-help {
  background-position: -100px -8px;
}
.xicon-promote {
  background-position: -8px -31px;
}
.xicon-shortname {
  background-position: -31px -31px;
}
.xicon-support {
  background-position: -54px -31px;
}
.xicon-twitter {
  background-position: -77px -31px;
}
.xicon-wiki {
  background-position: -100px -31px;
}
.xicon-xavi {
  background-position: -8px -54px;
}

/*responsive
-------------------------------------------------- */

/* Large desktop */
@media (min-width: 1200px) {

}
@media (min-width: 980px) {

}
@media (max-width: 979px) {
    body {
        padding-top: 0px;
    }
}
/* Portrait tablet to landscape and desktop */
@media (min-width: 768px) and (max-width: 979px) { 

}
/* Landscape phone to portrait tablet */
@media (max-width: 767px) {	
    #footer {
        margin-right: -20px;
        margin-left: -20px;
    }
}
/* Landscape phones and down */
@media (max-width: 480px) {

}

.adxat {
	margin-top: 15px;
	margin-bottom: 15px;
	overflow: hidden;
}

.xcheck {
	vertical-align:text-top;
}
}
