css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("diaspora.com.ar"),domain("/*[[diasp-page]]*/") {
  body {
    background-color: #222;
    color: white;   
  }
  
  #publisher_textarea_wrapper {
    background-color: #222;
    color: white;
  }
  
  #conversation_show .conversation_participants {
    background-color: #222;
    color: white;
  }
  #message_text {
    background-color: #222;
    color:white;
  }
  .comment_box{
    background-color: #222;
    color:white;
  }
  
  .content {
    color: #ddd;
  }
    
  
  /* No centralizar la página... */
  .container {
    margin-left: 10px;
  }
  
  /*--------------------------------------------------------------------------------
   Contenido del stream
  */
  
  .avatar {
    background-color: transparent;
  }
  
  a:hover > img.avatar {
    border-style: solid;
    border-width: 1px;
    border-color: #8f8;
}
  
  a, body > header #nav_badges #notification_dropdown a {
    color: #8f8;
  }
  
  a.tag {
    color: #fff;
  }  
  a.tag:hover{
   color: #fff;
    text-decoration: underline;
    background-color:transparent;
  }
  
  a:hover, body > header #nav_badges #notification_dropdown a:hover{
    color: #8f8;
    text-decoration: underline;    
  }
  
  h1,h2,h3,h4,h5,h6 {
    color: #fff;
  }
  
  /** Feedback **/
  .feedback {
    padding: 10px;
  }
  
  
  
  /** Comentarios **/
  .comments {
    background-color: #111;
    margin-left: 10px;
  }
  
  .bd > .comments:before {    
    content: "";
    font-size: 28px;
    z-index: 1;
    position: relative;
    left: 0;
    top: 0;
    background-color: #111;
    line-height: 0px;
    font-family: "entypo";
    font-style: normal;
  }
  
  /** Controles (Reportar, ignorar, ocultar) **/
  .controls:hover, .control_icon:hover {
    background-color: white;
  }
  .controls, .control_icon {
    background-color: #222;
    opacity: 1;
  }
  
  /** Controles de feedback **/
  /*.focus_comment_textarea:before {
    content: "";
    font-family: "entypo";
  }
  .reshare:before {
    content: "";
    font-family: "entypo";
  }
  .like:before {
    content: "♥";
    font-family: "entypo";
  }*/
  
  /** Enviar publicación **/
  #status_message_fake_text{
    background-color: #222;
    color:#888;    
  }  
  #publisher_textarea_wrapper textarea:focus{
    background-color: #222;
    color:white;
  }
  
  .post_preview{
    background-color: #222;
    color:white;
  }
  

  
  /*--------------------------------------------------------------------------------
  Panel izquierdo.
  */
  #leftNavBar a {
    color: #fff;
  }
  
  #leftNavBar a.hoverable:hover {
    background-color: transparent;
    color: #8f8;
  }
  
  /*--------------------------------------------------------------------------------
  Chat
  */
  
  .jsxc_textarea {
    background-color: #333;
  }
  div.jsxc_chatmessage{
    background-color: #333;
  }
  div.jsxc_out {
    background-color: #333;
    color: #f88;
  }
  div.jsxc_in {
    background-color: #333;
    color: #8f8;
  }
  #jsxc_dialog {
    background-color: #383c43;
  }
  
  /*--------------------------------------------------------------------------------
  Notifications
  */
  
  #notifications_container .stream .stream_element.media.unread {
   background-color: #422;
  }
  
  /** Notification badge **/
  body > header #nav_badges #notification_badge.active {
   background-color: #333;
  }
  body > header #nav_badges #notification_dropdown{
   background-color: #333;
  }
  
  body > header #nav_badges #notification_dropdown .notification_element .notification_message{
    color: #fff;
  }
  
  body > header #nav_badges #notification_dropdown .header h4 { 
    color: #fff 
  }
  
  /* unreaded */
  body > header #nav_badges #notification_dropdown .notification_element.unread {    
    background-color: #eee;
  }
  body > header #nav_badges #notification_dropdown .notification_element.unread .notification_message{
    color: #333;
  }
  
  /*--------------------------------------------------------------------------------
  Editar perfil
  */
  .well {
   background-color: transparent;
  }
  
  /*--------------------------------------------------------------------------------
  Contactos
  */
  
  #contacts_container #people_stream.contacts .stream_element.in_aspect{
   background-color: #333;
  }
  
  .info .tag  {
   background-color: transparent;
  }
  
  /*--------------------------------------------------------------------------------
  Hovercard
  */
  #hovercard {
    background-color: #333;
    color: white;
  }
  
  /*--------------------------------------------------------------------------------
  Crear perfil
  */
  
  #facebox .content {
   background-color: #333;
   color:white;
  }
}
