css:
@-moz-document domain("docs.oracle.com") {
body{
    background-color: rgb(55, 53, 53);
    color: rgb(148, 186, 156);

  }
  
  h1, h2, h3, h4, h5{
    color: #CD9AB1;;
  }
  
  #TopBar_left{
    color:rgba(174, 195, 229, 1);
  }
  
  #TopBar{
    min-width: 0px;
    border-bottom: 1px solid rgba( 0, 30, 255, 1 );
    border-radius: 3px;
    background: linear-gradient( to bottom, rgba( 0, 0, 0, 1 ) 0%, rgba( 0, 69, 122, 1 ) 100% ) repeat scroll 0% 0% transparent;
  }
  
  .header-container{
    background-color: rgba( 0, 0, 0, 1 );
    border-bottom: 1px solid rgba( 0, 143, 255, 1 );
    box-shadow: 0px 2px 2px rgba( 0, 66, 255, 0.15 );
  }
  
  #logocover{
    width: 0px;
  }
  
  .nolinkLESSON, .nolinkAHEAD, .nolinkBHEAD, .nolinkCHEAD, .nolinkQUESTIONS{
    color: rgb(68, 153, 168);
  }
  
  #Contents a, .NavBit a, #TagNotes a{
    color:rgb(66, 178, 228);
  }
  
  #BreadCrumbs a{
    color:rgb(201, 148, 195);
  }
  
  a:link{
    text-decoration: none;
    color: #76B1E6 !important;
  }

a:visited,#Contents a:visited, .NavBit a:visited, #TagNotes a:visited{
  color: #8780E5;
}
  
  #productName{
    color: rgb(227, 168, 168);
  }
code{
  color: rgb(186, 190, 255);
}

.TutorialLink{
  color: #5AA3E8;
  }
#homeGetStarted a:link, #homeGetStarted a:active, #homeGetStarted a:visited, .threecolumns a:link, .threecolumns a:active, .threecolumns a:visited{
color: #739BC0;
}

.button{
text-align: center;
display: inline-block;
background-color: #525252;
  border: none;
  box-shadow:none;
border-radius: 2px;
padding: 2px 5px;
margin-left: 5px;
}

.button:hover{
  background-color: #626262;
      border:none;
      box-shadow:none;
    }


.treeview li, .treeview li.submenu{
background-color: rgb(55, 53, 53);
}

.booktitle{
background: none;
border-bottom: 1px solid #898989;
border-radius: 0;
}

#breadcrumb a:link, #breadcrumb a:visited{
color:#76B1E6;
}

.footer-container,.footer-containerbook{
background:none;
border-top: 1px solid #656565;
border-radius: 0;
}

footer, #Footer .footertext{
color:rgb(148, 186, 156);
}

.SourceLink{
border-bottom:1px solid rgb(186, 190, 255);
}

div.block{
color:rgb(148, 186, 156);
font-family: sans-serif;
}

.title{
color: #93B2C8;
}

.contentContainer .description dl dt, .contentContainer .details dl dt, .serializedFormContainer dl dt{
color: #9E9E9E;
}

ul.blockList ul.blockList li.blockList, ul.blockList ul.blockListLast li.blockList{
border: 1px solid #777777;
background-color: #353535;
}

.overviewSummary, .memberSummary, .typeSummary, .useSummary, .constantsSummary, .deprecatedSummary{
border-left: 1px solid #737373;
border-right: 1px solid #737373;
border-bottom: 1px solid #737373;
}

th.colFirst, th.colLast, th.colOne, .constantsSummary th{
background: #474747;
}

.altColor{
background-color: #353434;
}

.rowColor{
background-color: #474747;
}

ul.blockList ul.blockList ul.blockList li.blockList, ul.blockList ul.blockList ul.blockListLast li.blockList{
background-color: #3D3D3D;
}
ul.blockList ul.blockList ul.blockList li.blockList h3{
border: 1px solid #696969;
background-color: #545454;
}
div.details ul.blockList ul.blockList ul.blockList li.blockList h4, div.details ul.blockList ul.blockList ul.blockListLast li.blockList h4{
background-color: #4D4D4D;
border: 1px solid #6B6B6B;
}

.topNav{
background-color: #696969;
}
.subNav{
background-color: #4D4D4D;
}

.overviewSummary caption span, .memberSummary caption span, .typeSummary caption span, .useSummary caption span, .constantsSummary caption span, .deprecatedSummary caption span{
background-color: #474747;
color: rgb(211, 155, 189);
border-left: 1px solid #737373;
border-right: 1px solid #737373;
border-top: 1px solid #737373;
}

.memberSummary caption span.activeTableTab span{
background-color: #474747;
color: rgb(211, 155, 189);
border-left: 1px solid #737373;
border-right: 1px solid #737373;
border-top: 1px solid #737373;
}

.memberSummary caption span.tableTab span{
background-color: #565656;
}

.memberSummary caption span.tableTab, .memberSummary caption span.activeTableTab{
opacity:0;
}

dd, h3{
font-family: sans-serif;
}

.IND div > pre, .IND dd > pre, .IND li > pre{
   background-color:rgba(238, 238, 238, 0); 
  }

div.syntaxhighlighter{
    background-color:rgba(238, 238, 238, 0) !important; 
  }

div.syntaxhighlighter .line.alt1, div.syntaxhighlighter .line.alt2 {
        background-color:rgba(238, 238, 238, 0) !important; 
      }

.syntaxhighlighter code.comments,
.syntaxhighlighter code.comments a,
.syntaxhighlighter code.keyword,
.syntaxhighlighter code.plain, .syntaxhighlighter code.plain a,
.syntaxhighlighter code.string, .syntaxhighlighter code.string a
    {
      color:rgb(148, 186, 156) !important;      
    }

.TableRowColor{
  background-color: #434343;
  }
  
  .TableSubHeadingColor{
  background-color: #5A5A5A;
    }
    
    .TableHeadingColor{
  background: #525252;
      }

body, th, td, p, h5, h5 a, h6, h6 a, h5 span, h6 span, h1, h2, h3, h1 span, h2 span, h3 span, h1 a, h2 a, h3 a, h4, h4 span, h4 a{
   color: rgb(148, 186, 156);
  
}

div.maincontent pre, div.maincontent code, div.maincontent tt{
color:rgba(174, 195, 229, 1);
}

pre{
background-color: #303030;
}
}
