css:
@-moz-document domain("vk.com") {/* отображаем только на страницах вконтакте */
/* ~~~~~~~~~~~~~~~~~~~~~~~ АВТОР: МАКСИМ ГУСЕВ ~~~~~~~~~~~~~~~~~~~~~ */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ ФОН ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
body,
#fmenu,
#side_bar,
#im_footer_filler,
#side_bar ol li .left_row {
	background: #f0f2f5 !important;
}
#im_footer_sh { /* тень в сообщениях */
    border-bottom: 1px solid #e5e7ea !important;
}
#ads_page_wrap1,
#wrap1 {
	background: #fff;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~ ОНЛАЙН ЧАТ ~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.chat_onl_wrap,
.chat_active,
.chat_expand {
	background: #d8dfe6 !important;
}
.chat_tab_wrap:hover {
	background: #d0d9e1 !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~ ОТКРЫТОЕ ФОТО ~~~~~~~~~~~~~~~~~~~~~~~~ */
.layers_shown .chat_onl_wrap {
	background: #6B6B6B !important;
}
.layers_shown .chat_active,
.layers_shown .chat_expand {
	background: #6B6B6B !important;
}
.layers_shown .chat_tab_wrap:hover {
	background: #7B7B7B !important;
}
.layers_shown .chat_onl_icon {
	background-position: -51px -44px!important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ DEV ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.widget_body { /* страница vk/dev */
	background: #fff !important;
}
.login_appdevices_promo{ /* при входе на сайт */
	background: #fff !important;
}
#side_bar ol li .left_row:hover {
	background: #E1E7ED !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~ STICKERS ~~~~~~~~~~~~~~~~~~~~~~~~~ */
.im_stickers_store_wrap {
	background: #fff !important;
}
.ad_box_new { /* возможные друзья */
	background: #f0f2f5 !important;
}
.layers_shown .tb_fixed { /* при прокрутке подарков */
	margin-top: 0px !important;
}
#nav li a {
	background: #f0f2f5 !important;
}
#nav li a:hover {
	background: #E1E7ED !important;
	-webkit-border-radius: 2px;
	-khtml-border-radius: 2px;
	-moz-border-radius: 2px;
	border-radius: 2px;
}
#header_wrap1 { /* прозрачность бордера у бара c ф.и.о и онлайн статусом */
	border-color: rgba(100, 100, 100, 0.08) !important;
}
#side_bar ol {
	margin: 0px 0px 5px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~ ВСПЛЫВАЮЩИЕ МЕНЮ ~~~~~~~~~~~~~~~~~~~~~~~ */
.fmenu_item { /* выплывающего меню при прокрутки страницы */
	line-height: 10px !important;
}
.fmenu_icon { /* прозрачный фон иконок всплывающего меню */
	background-image: url(http://savepic.net/6230906.png) !important;
}
/* ~~~~~~~~~~~~ КНОПКИ ВВЕРХ, ВНИЗ И НАЗАД НА СТРАНИЦАХ ~~~~~~~~~~~~ */
#stl_bg {
	padding-top: 68px !important;
	width: 114px !important;
}
#stl_text {
	height: 15px !important;
	color: #466a94 !important;
	font-size: 12px !important;
	margin: 0 0 0 19px !important;
	padding: 0px 24px !important;
	background: url(http://vk.com/images/dev_toplink.png) no-repeat left 0px !important;
	-webkit-font-smoothing: antialiased !important;
	-moz-osx-font-smoothing: grayscale !important;
}
.stl_active.over_fast #stl_bg {
	background-color: #dce2e8 !important;
}
#stl_left .down {
	background-position: left -15px !important;
}
#stl_left .back {
	background-position: left -30px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~ ЭЛЕМЕНТЫ ВЫДЕЛЕНИЯ ~~~~~~~~~~~~~~~~~~~~~~ */
.checkbox div {
	background: url(http://vk.com/images/icons/dev_check.png) 0px 0px no-repeat !important;
	background-size: 15px 75px !important;
    height: 15px !important;
}
.checkbox:hover div {
	background-position: 0px -30px !important;
}
.checkbox.disabled:hover div {
	background-position: 0px 0px !important;
}
.checkbox.on div {
	background-position: 0px -15px !important;
}
.checkbox.on:hover div {
	background-position: 0px -45px !important;
}
.checkbox.disabled.on:hover div {
	background-position: 0px -15px !important;
}
.checkbox_container table td .checkbox_on,
.checkbox_container table td .checkbox_off,
.checkbox_container table td .checkbox_on_over,
.checkbox_container table td .checkbox_off_over {
	background: url(http://vk.com/images/icons/dev_check.png) 0px 0px no-repeat !important;
	background-size: 15px 75px !important;
    height: 15px !important;
}
.checkbox_container table td .checkbox_on {
	background-position: 0 -15px!important;
}
.checkbox_container table td .checkbox_off_over {
	background-position: 0px -30px!important;
}
.checkbox_container table td .checkbox_on_over {
	background-position: 0 -45px!important;
}
.checkbox_container.disabled td.checkbox div.checkbox_on_over {
	background-position: 0 -15px !important;
}
.checkbox_container.disabled td.checkbox div.checkbox_off_over {
	background-position: 0 0 !important;
}
.like_tt .bottom .checkbox div { /* для репоста */
	background: url(http://vk.com/images/icons/like_icons_bl.png) -2px 0 no-repeat !important;
	width: 14px !important;
	height: 12px !important;
}
.like_tt .bottom .on div { /* для репоста, репост сделан */
background: url(http://vk.com/images/icons/like_icons_bl.png) -2px -15px no-repeat !important;
	width: 15px !important;
	height: 12px !important;
}
.radiobtn div {
	background: url(http://vk.com/images/icons/dev_radio.png) 0px 0px no-repeat !important;
	background-size: 14px 70px !important;
}
.radiobtn div,
.radiobtn_container table td .radiobtn_on div,
.radiobtn_container table td .radiobtn_off div,
.radiobtn_container table td .radiobtn_on_over div,
.radiobtn_container table td .radiobtn_off_over div {
	background: url(http://vk.com/images/icons/dev_radio.png) 0px 0px no-repeat !important;
	background-size: 14px 70px !important;
}
.radiobtn:hover div {
	background-position: 0px -28px !important;
}
.radiobtn.disabled:hover div {
	background-position: 0px 0px !important;
}
.radiobtn_hover div {
	background-position: 0px -28px !important;
}
.radiobtn.on div {
	background-position: 0px -14px !important;
}
.radiobtn.on:hover div {
	background-position: 0px -42px !important;
}
.radiobtn.disabled.on:hover div {
	background-position: 0px -14px !important;
}
.radiobtn.on.radiobtn_hover div {
	background-position: 0px -42px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~ НАПОМИНАНИЯ ~~~~~~~~~~~~~~~~~~~~~~~~ */
.left_hide { /* кнопка «закрыть» для напоминания */
	background: url(http://savepic.net/6241146.png) no-repeat center center !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ РЕКЛАМА ~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#left_ads,
#group_recom_wrap,
.ads_ads_news_wrap { /* скрываем 3 рекламных блока */
	display: none !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ МЕНЮ ~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#myprofile .left_label,
#l_fr .left_label,
#l_ph .left_label,
#l_vid .left_label,
#side_bar a[href*="audio"] .left_label,
#l_msg .left_label,
#l_nts .left_label,
#l_gr .left_label,
#l_ev .left_label,
#l_nwsf .left_label,
#l_nws .left_label,
#side_bar a[href*="fave"] .left_label,
#l_set .left_label,
#l_ap .left_label,
#side_bar a[href*="docs"] .left_label,
#l_ads .left_label,
#l_spr .left_label {
	font-size:0px
}
#side_bar .left_label:before {
	font-size:11px;
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px 0px no-repeat;
}
#myprofile .left_label:before {
	content: "Cтраница";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -163px no-repeat;
	padding-left: 17px;
}
#l_fr .left_label:before {
	content: "Друзья";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px 1px no-repeat;
	padding-left: 17px;
}
#l_ph .left_label:before {
	content:"Фотографии";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -29px no-repeat;
	padding-left: 17px;
}
#l_vid .left_label:before {
	content:"Видеозаписи";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -74px no-repeat;
	padding-left: 17px;
}
#side_bar a[href*="audio"] .left_label:before {
	content:"Аудиозаписи";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -221px no-repeat;
	padding-left: 17px;
}
#l_msg .left_label:before {
	content:"Сообщения";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -192px no-repeat;
	padding-left: 17px;
}
#l_nts .left_label:before {
	content:"Заметки";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -133px no-repeat;
	padding-left: 17px;
}
#l_gr .left_label:before {
	content:"Группы";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -87px no-repeat;
	padding-left: 17px;
}
#l_ev .left_label:before {
	content:"Встречи";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px 1px no-repeat;
	padding-left: 17px;
}
#l_nwsf .left_label:before {
	content:"Новости";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -293px no-repeat;
	padding-left: 17px;
}
#l_nws .left_label:before {
	content:"Ответы";
	background: url(http://savepic.net/6279871.png) 0px 1px no-repeat;
	padding-left: 17px;
}
#side_bar a[href*="fave"] .left_label:before {
	content:"Закладки";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -118px no-repeat;
	padding-left: 17px;
}
#l_set .left_label:before {
	content:"Настройки";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -58px no-repeat;
	padding-left: 17px;
}
#side_bar a[href*="app"] .left_label:before  {
	content: "";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -104px no-repeat;
	padding-left: 17px;
}
#l_ap .left_label:before {
	content:"Приложение"!important;
}
#side_bar a[href*="docs"] .left_label:before {
	content:"Документы";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -148px no-repeat;
	padding-left: 17px;
}
#l_ads .left_label:before {
	content:"Реклама";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -235px no-repeat;
	padding-left: 17px;
}
#l_spr .left_label:before {
	content:"Поддержка";
	background: url(http://vk.com/images/icons/mono_iconset.png) 0px -320px no-repeat;
	padding-left: 17px;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~ ИНФОРМАЦИЯ ~~~~~~~~~~~~~~~~~~~~~~~~ */
/* ~~~~~~~~~~~~~~~~~~~~~~~~ ФИКСАЦИЯ МЕНЮ ~~~~~~~~~~~~~~~~~~~~~~~ */
#page_layout {
	z-index: inherit !important;
}
#side_bar {
	padding: 5px 0 0px 4px !important;
	border-bottom-left-radius: 0px !important;
	position: fixed;
	z-index: 11;
}
#page_body, #footer_wrap {
	z-index: 6;
}
#fmenu {
	display: none !important;
}
#stl_side {
	height: 100% !important;
	top: 40px !important;
}
.left_box {
	margin: 0px 0px 5px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ ШАПКА ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#page_header {
	position: fixed;
	z-index: 120;
	width: inherit; /* в приложениях все четко */
	height: 50px !important;
}
#page_header .back {
	position: fixed !important;
	left: 0px;
	height: 50px !important;
	background: #567ca4 !important;
}
#page_header .left{
	background: #567ca4 url(http://vk.com/images/logo_vk_white.png) 0px 14px no-repeat !important;
	margin: 0px 8px 0px 4px;
	height: 50px !important;
}
#page_header .right {
	display: none !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ МЕНЮ ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.head_nav {
	padding: 0px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ ЛОГО ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#top_logo_down {
	background: #567ca4 url(http://vk.com/images/logo_vk_white.png) 0px 15px no-repeat !important;
	height: 50px !important;
}
.top_home_link {
	height: 50px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ ССЫЛКИ ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.head_nav a.top_nav_link {
	color: #fff !important;
	height: 50px !important;
	line-height: 47px !important;
	font-size: 13px !important;
	padding: 0px 15px !important;
	text-shadow: none !important;
	font-weight: normal !important;
}
.head_nav a.top_nav_link:hover,
.head_nav a.top_nav_link.over,
#top_back_link:hover {
	background-color: #51759b !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ НАЗАД ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#top_back_link {
	max-width: 149px !important;
	padding-left: 25px !important;
	background: url(http://vk.com/images/backlink.gif) no-repeat left -80px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ ПОИСК ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#ts_wrap {
	left: 146px !important;
	height: 50px !important;
	width: 170px !important;
}
#ts_wrap .ts_input_wrap {
	top: 0px !important;
	margin: 0px !important;
	padding: 0px !important;
	border-radius: none !important;
	width: 170px;
}
#ts_wrap .ts {
	background-color: rgba(255, 255, 255, 0) !important;
	border-radius: 0px !important;
}
#ts_wrap .ts_input_wrap2 {
	margin: 0px !important;
}
#ts_wrap .input_back {
	padding: 17px 0px 17px 25px !important;
	color: #92adc8 !important;
	margin: 0px !important;
	font-size: 13px;
}
#ts_wrap .input_back_content {
	width: 141px !important;
}
#ts_wrap #ts_input {
	border: 0px !important;
	outline: none !important;
	padding: 7px 7px 7px 27px !important;
	margin: 11px 0px !important;
	height: 14px !important;
	width: 136px !important;
	border-radius: 14px !important;
	transition: background-color 0.05s, color 0.05s !important;
	font-size: 13px !important;
	background: #4b6d94 url(http://vk.com/images/icons/dev_head_magglass.png) 8px 8px no-repeat !important;
	color: #d9e2ec !important;
}
#ts_wrap input#ts_input:focus {
	background-color: #fff !important;
	color: #2b2f33 !important;
}
#ts_cont_wrap {
	top: 58px !important;
	left: 146px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~ ИКОНКА МУЗЫКИ ~~~~~~~~~~~~~~~~~~~~~~~~ */
.head_nav a.top_nav_link span#head_music_text {
	color: #fff !important;
}
.head_nav a.top_nav_link span#head_music_text img { /* полоса загрузки */
	margin-top: 20px !important;
	margin-left: 8px !important;
}
.head_nav a.top_nav_link#head_music {
	padding-right: 36px !important;
}
#head_play_btn {
	top: 13px !important;
	background: url(http://vk.com/images/icons/audio_iconsf.png) -144px -25px no-repeat !important;
}
a:hover #head_play_btn,
a.over #head_play_btn {
	background-position: -144px -25px !important;
}
#head_play_btn.playing {
	background-position: -162px -25px !important;
}
a:hover #head_play_btn.playing,
a.over #head_play_btn.playing {
	background-position: -162px -25px !important;
}
a:hover #head_play_btn.over,
a:hover #head_play_btn.down,
a.over #head_play_btn.over,
a.over #head_play_btn.down {
	background-position: -144px -25px !important;
}
a:hover #head_play_btn.playing.over,
a:hover #head_play_btn.playing.down,
a.over #head_play_btn.playing.over,
a.over #head_play_btn.playing.down {
	background-position: -162px -25px !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ МЕНЮ ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#side_bar,
#stl_side,
#page_body {
	margin-top: 50px;
}
.im_fixed_nav #page_body,
.audio_fixed_nav #page_body,
.im_fixed_nav #side_bar,
.audio_fixed_nav #side_bar {
	margin-top: 0px;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ ФОТО ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.photos_period_fixed { /* отступ при прокрутке фотоальбома 6:50 16.12.2014 */
	margin-top: 50px;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ АУДИО ~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#gp { /* отступ, когда с правой стороны */
	top: 65px !important;
}
#gp.reverse { /* отступ, когда с левой стороны */
	top: auto !important;
}
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
#login_relogin_wrap { /* отступ при релогине */
	padding-top: 60px !important;
}
#pg_fixed { /* страницы листаем */ 
	top: 65px;
}
.widget_body #stl_side { /* страница vk/dev */
	margin-top: 0px
}
.tb_fixed { /* страница стикеров */
	margin-top: 50px;
}
/* ~~~~~~~~~~~~~~~~~~~~~~ primary mail phone ~~~~~~~~~~~~~~~~~~~ */
/*
#reg_bar.top_info_wrap {
	top: inherit !important;
	border-bottom: 0px !important;
	bottom: 0 !important;
	box-shadow: 0 0 3px #444 !important;
}
*/
#reg_bar {
	display: none !important;
}
#please_confirm_mail, #change_phone_wrap {  /* привязать телефон и емейл */
	position: fixed !important;
	z-index: 400 !important;
	width: 100% !important;
	bottom: 0 !important;
	box-shadow: 0 0 3px #444 !important;
}
#please_confirm_mail .hide_button,
#change_phone_wrap .hide_button {
	float: left; /* привязать телефон и емейл, кнопка в левую сторону */
}
.new_join #content { /* регистрация */
	margin-top: 50px !important;
}
.im_add_row .im_log_author a, .im_add_row .im_log_date span, .im_add_row .im_log_date a.im_date_link, .im_add_row .im_log_author_chat_thumb, .im_add_row .im_log_author_chat_name {
	display: block !important;
}
.im_important_toggler {
	margin-top: -2px !important;
}
.im_log_check {
	margin-top: 9px !important;
}
.im_log_act, .im_log_author, .im_log_body, .im_log_date, .im_log_rspacer {
	border-top: 5px solid #fff !important;
}
}
