css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(apollo.rip), domain(passtheheadphones.me) {
	.torrent_label {
		padding: 0 .25em;
		border-radius: .2em;
		font-weight: normal;
		background: #84795F;
		color: #EEE;
	}
	a:hover>.torrent_label { opacity: .9; }

	.tl_approved { background: #56B0C7; }
	.tl_approved.tl_cassete { background: #0992B5; }
	.tl_approved.tl_lossy_master { background: #6B6FBF; }
	.tl_approved.tl_lossy_web { background: #7E83ED; }

	.tl_free { background: #3BA53B; }
	.tl_free.tl_neutral {
		background: #7ACE4A;
		color: #444
	}
	.tl_free.tl_personal {
		background: #FFC0CB;
		color: #444;
	}

	.tl_reported { background: #CC2F24; }
	.tl_reported.tl_bad_tags {
		background: #F4D524;
		color: #444;
	}
	.tl_reported.tl_bad_folders {
		background: #F28E24;
		color: #444;
	}
	.tl_reported.tl_bad_file_names { background: #F04724; }

	.tl_snatched {
		background: #E5B244;
		color: #444;
	}
}
