@-moz-document domain("vozforums.com") {
/*VOZ Background Color - phần viền bao quanh phần nội dung, không phải nền đen phía sau*/
.body {
	background: #222222 !important; 
}

/*Thanh điều hướng của Voz*/
.navbar {
	color: #303030;
}

/*Các thanh tiêu đề như Thread, Thread Starter,..*/
.thead, .tfoot { 
	background: #005554 !important;
  	font-weight: normal !important;
  	padding: 6px !important;
}
  
/*Thanh bar chứa Control menu như UserCP, Search, ...*/
.vbmenu_control { 
	background: #005554 !important;
  	border: 0px solid !important;
  	font-weight: normal !important;
  	padding: 8px 8px 8px 8px;
}

/*Popup bật ra khi nhấn mũi tên xuống. Ví dụ: Gotopage, Search popup*/
.vbmenu_popup { 
  	display: inline-block;
	border: 0px solid !important;
    background: #ffffff !important;
  	font-weight: normal !important;
}

/*Màu khi hover các thẻ lựa chọn trong popup*/
.vbmenu_hilite { 
	background: #00C16A !important;
  	font-weight: normal;
}

.vbmenu_hilite a:hover { 
	color: #FFFFFF !important;
  	font-weight: normal;
}

/*Màu mặc định của các thẻ lựa chọn trong popup*/
.vbmenu_option { 
	background: #FFFFFF !important;
  	color: #000000 !important;
}

/*Viền bao ngoài các thead, tcat, ...*/
.tborder { 
  	border: 0px solid !important; /*Không viền*/
    background: #EBEBEB !important;	
}

/*Màu nền mặc định ở list các topic trong mỗi f*/
.page { 
	background: #F5F5F5 !important;
  	box-shadow: none;
}

/*Thanh phân loại, ví dụ: Đại sảnh, máy tính để bàn*/
.tcat { 
	background: #005554 !important;
  	color: #FFFFFF;
}

/*Background của các f*/
.alt1Active, .alt1Active_didongnet, .alt1Active_zotac, .alt1Active_brother { 
	background: #FFFFFF !important;
}

/*Màu nền ô chứa thông tin về số thread của mỗi f ở trang chủ, ...*/
.alt1 { 
	background: #FFFFFF !important;
  	border: none;
  	color: #000000 !important;
}     

/*Màu nền phần chứa thông tin của các thím, ...*/
.alt2 { 
	background: #F0F0F0 !important;
	border: none;
}     

table[style="margin-bottom:3px;padding:0px 10px 0px 0px"] {
	padding: 0px 0px 0px 0px !important;
}

/*Trong phần QuickReply, panelsurround là phần bao ngoài cùng, panelsurround chứa panel, hai phần này bao ngoài khung soạn thảo cmt*/
.panelsurround { 
    background: #E5E5E5 !important;
  	width: 100% !important;
}
.panel {
    background: #E5E5E5 !important;
	border: none !important;
}

/*CSS chung cho các button*/
.button { 
  	background: #ACACAC !important;
    border: none;
    color: #ffffff;
    height: 30px;
  	transition: background-color ease-in-out .3s;
}

.rainbow_button { 
  	background: #ACACAC !important;
    border: none;
    color: #ffffff;
  	padding: 4px 5px 5px 5px;
  	transition: background-color ease-in-out .3s;
}    

.cmnw .btn, .showimage { 
  	background: #ACACAC !important;
    border: none;
    margin-right: 4px;
    padding: 4px 6px 4px 5px;
    color: #ffffff;
    height: 30px;
  	transition: background-color ease-in-out .3s;
}

/*Màu khi mouse over button*/
.button:hover, .rainbow_button:hover, .cmnw .btn:hover, .showimage:hover { 
	background: #005554 !important;
  	transition: background-color ease-in-out .3s;
}

/*Các textbox điền thông tin*/
.bginput, #rainbow_fontcolor { 
  	background: #f2f2f2;
  	display: INLINE;
  	padding-left: 5px;
	margin: 0px 2px 0px 2px;
  	border-top: none;
  	border-bottom: none;
  	border-right: none;
  	border-left: 5px solid #B523FF;
  	color: #000000;
  	height: 30px;
  	width: auto;
	transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}

/*Đặt màu đậm hơn khi focus textbox*/
.bginput:focus, #rainbow_fontcolor:focus { 
  	background: #ffffff;
  	display: INLINE;
  	padding-left: 5px;
	margin: 0px 2px 0px 2px;
  	border-top: none;
  	border-bottom: none;
  	border-right: none;
  	border-left: 5px solid #B523FF;
  	color: #000000;
  	height: 30px;
  	width: auto;
	transition: box-shadow ease-in-out .15s, background-color ease-in-out .15s;	
}

/*Fieldset là phần chứa các tùy chọn hoặc các mục bổ sung trong Voz, như: Option khi cmt */
fieldset { 
  	border: 1px solid #005554;
  	color: #505050;
  	margin-top: 30px;
  	background: transparent;
}

/*Legend là phần chữ nằm đè lên đường chiều dài phía trên của các Fieldset*/
legend {
  	background: #005554;
  	border: 1px solid #005554;
	color: #f2f2f2 !important;
  	padding: 3px 6px 3px 6px;
}

/*Các thẻ select*/
select { 
  	padding: 3px 0px!important;
	color: #000000 !important;
  	background-color: #ACACAC !important;
  	border: 0px;
  	transition: all 0.3s ease-out;
} 

/*Tên người dùng hiển thị trong mỗi post*/
.bigusername {
  	font-family: Segoe UI !important;
    color: #E74C3C !important;
  	font-size: 20px !important;
  	font-weight: bold;
}

/*Trang User Profile*/
/*Tên người dùng*/
#main_userinfo h1 {
  	color: #FF3D43 !important;
    font-weight: bold;
}

/*Phần chức vụ thành viên*/
#main_userinfo h2 {
	color: #303030 !important;
}

/*Xóa khung bên phải, mở rộng phần nội dung Last Post cho khỏi bị đè chữ*/
TD[width = "160"] {
  	display: none !important;
}
[style*=wrap] > STRONG { margin-right: 100px } 


/*Các tab thông tin bên dưới*/
ul.tab_list li.tcat {
	font-weight: lighter !important;
}

#aboutme_tab:hover, #stats_tab:hover, #friends_tab:hover, #contactinfo_tab:hover {
  	color: #43A6DF !important;
}
/*Màu nền thanh chứa các tab*/
.tab_list {
    background: #D0D0D0 !important;
}

/*Bo góc cho ảnh Avatar của các thành viên trong Voz*/
[alt*="'s Avatar"] {
  	border-radius: 5px !important;
}

/*Dành cho tự sướng, làm cho avatar của bạn thành hình tròn, nên dùng ảnh có size chiều rộng = chiều cao*/    
[alt="UTatsuya's Avatar"] {
	width: 100px;
  	height: 100px;
  	border-radius: 50% !important;
}

/*Bo tròn cho ảnh trong Profile*/
[alt*="'s Profile Picture"] {
  	border-radius: 50% !important;
}

/*Dành cho ai dùng Autopage, đổi màu nền thanh loading của AutoPager*/
.autoPagerS {
  	background: #656565 !important;
}


/*Phần comments*/
.vBulletin_editor { 
	background: #DDDDDD !important;
    border: none !important;
  	color: #000000;
}

