}

.plk_backdrop {
	width: 100%;
	height: 100%;
	position: fixed;
	top: 0px;
	left: 0px;
	z-index: 5;
	background: black;
	opacity: 0.9;
	-webkit-transition:all 0.3s;
	-moz-transition:all 0.3s;
	-ms-transition:all 0.3s;
	-o-transition:all 0.3s;
	transition:all 0.3s
}

.plk_contentbar {
	position: fixed;
	z-index: 9999;
	top: 0;
	left: 0;
	width: auto;
	height: auto;
	background-color: #003D3B;
}

.bar-item-wrapper {
	float: left;
	width: 40px;
	height: 40px;
	position: relative; 
}

.plk_contentbar .bar-item-wrapper.active {
	background-color: #005554;
}

a.handler {
	line-height: 40px;
	text-align: center;
	display: block;
	width: 40px;
	height: 40px;
	position: absolute;
	top: 0px;
	left: 0px;
	z-index: 100;
}

.plk_contentbar .bar-item-wrapper a.handler, #settings_option_bb_img {
	background: url(http://i.imgur.com/7IyqBeF.png) no-repeat
}

#setting_bar > a.handler {
	background-position: 6px center
}

/*#followthread_bar a.handler {
	background: url(http://i.imgur.com/WRZAZKq.png) no-repeat center center
}*/

#followthread_bar > a.handler {
	background-position: -62px center
}

#quoteNotification_bar > a.handler {
	background-position: -25px center
}

.bar-item-container {
	display: none;
	width: 30%;
	min-width: 400px;
	max-height: 400px;
	overflow: auto; 
	background-color: #005554;
	color: white;
	box-shadow: black 2px 2px 3px;
	position: absolute;
	top: 40px;
	left: 0px;
	z-index: 99
}

.bar-item-wrapper.active > .bar-item-container {
	opacity: 1;
	display: block
}

#setting_bar > .bar-item-container {
	max-height: none
}

.data-list .data-item {
	color: white;
	border-bottom: 1px solid #003334 ;
	padding: 8px
}

.SubscriptionItem--unhighlight {
	background-color: #101010
}

.data-list .data-item a {
	color: white
}

.data-list .data-item a:hover {
	text-decoration: underline !important;
}

.setting_bar_input {
	float: right
}

#settings_option_bb {
	margin-top: 0;
	margin-bottom: 0;
	height: 40px
}

#settings_option_bb_img {
	display: inline-block;
	width: 45px;
	height: 40px;
	background-position: -100px !important
}

.quoteContent {
	line-height: 1.5em
}

.capture {
	background: url(http://i.imgur.com/g1AgVvp.png) no-repeat;
	height: 22px;
  	width: 70px;
  	border: none;
  	cursor: pointer;
  	display: inline-block
}

.settingContent {
	display: inline-block;
  	max-width: 300px;
  	line-height: 1.5em
}
}

