css:
@-moz-document domain("userstyles.org") {
/* SITE WIDE CHANGES */

body
{
font-family: Consolas;
background: black;
color: #EEE;
margin: 0;
max-width: none!important;
}

pre
{
  border:none;
  color: black;
}

/* ----- Links ----- */

a
{
color: white;
text-decoration: none;
}

a:hover
{
color: white;
text-decoration: underline;
}

a:active
{
color: white;
text-decoration: underline;
text-shadow: 2px 2px 1px grey;
}

a:visited, a:visited:hover
{
color: #84D5D5;
}


input, button, select
{
  font-family: Consolas;
  border: none;
}


/* ----- Ad remover ----- */

div .ad
{
display: none;
}

/* ----- Scrollbar ----- */

::-webkit-scrollbar
{
  width: 20px;
  background: rgba(61, 152, 152, 0.6);
}

::-webkit-scrollbar-thumb
{
  background: rgba(61, 152, 152, 0.9);
}
}

@-moz-document url-prefix("https://forum.userstyles.org") {
#Head
{
  position: fixed;
  top: 0;
  width: 100%;
  padding: 10px 0 10px!important;
  z-index: 1;
  background-color: rgb(61, 152, 152)
}

#Body
{
  margin-top: 57px;
}

.BoxButtons>a,
.BoxButtons>a:hover
{
  background: rgba(61, 152, 152, 0.6);
  box-shadow: none;
  border: 0;
  text-shadow: none;
  border-radius: 0;
  color: white;
}
}

@-moz-document url-prefix("https://userstyles.org") {
/* ----- Top bar ----- */

#main-header
{
  position: absolute;
  width: 100%;
  height: 50px;
  margin: 0;
  padding: 0;
  border-radius: 0;
  background: black;
  border-bottom: 1px solid #555;
  font-family: Consolas;
  z-index: 1;
}

h1
{
  position: absolute;
  height: 100%;
  line-height: 50px;
  left: 10px;
}

/* ----- Content ----- */

#content-wrapper
{
  margin-top: 51px;
  padding-top: 5px;
  background: radial-gradient(at top left, black, grey);
}


/* ----- Sidebar ----- */

#left-sidebar
{
  position: fixed;
  float: none;
  height: 100%;
  margin: 5px;
  margin-top: 0;
  padding-top: 56px;
  background-color: rgba(61, 152, 152, 0.6);
}

nav, #related
{
  background-color: #6AADAD!important;
  border-radius: 0!important;
  height: auto;
}


/* ----- Article ----- */

#main-article
{
  margin: 5px;
  margin-left: 195px;
}


/* ----- Footer ----- */

body footer
{
  position: relative;
  height: 30px;
  width: 160px;
  margin: 0 10px 0 10px;
  padding: 5px;
  background: #6AADAD;
  top: 5px;
}

body footer p
{
  margin: 0;
}


/* Search Terms */

#search-terms
{
  width: 143px;
  margin-bottom: 5px;
}

#search-submit
{
  width: 17px; 
}


/* STYLE PAGE CHANGES */

/* ----- Install button ----- */

div.install-status.install
{
  border-radius: 0;
  background-image: none;
  background: #6AADAD;
}

h2
{
  margin-left: 0!important;
}

/* ----- Screenshot ----- */

#screenshots
{
  border-radius: 0;
  margin-left: 15px;
}

/* Notes */

#additional-info
{
  padding-left: 15px;
}

#additional-info-text
{
  border: 1px solid #FFF;
  padding: 5px;
}

#social, #discussions-area h3
{
  display: none;
}


/* HELP SECTION */

#widget-preview
{
  background: #FFF
}


/* USER PAGE CHANGES */

.notice
{
  background: #6AADAD;
  padding: 5px;
}
}
