css:
@-moz-document domain(www.tumblr.com) {

body {

    background: url('https://38.media.tumblr.com/beb22f29410b0d2f6e5dfd0cf96da224/tumblr_nhseq8sHMX1r9ka6jo1_1280.png') center right #000000 repeat fixed !important;

  }





ol#posts li blockquote,



body.mceContentBody blockquote {



margin-left: 0 !important;



margin-right: 0 !important;



padding-left: 10px !important;



border-width: 4px !important;



border-color: #333333 !important;



}



ol#posts li blockquote blockquote,



body.mceContentBody blockquote blockquote {



border-color: #839aaf !important;



}



ol#posts li blockquote blockquote blockquote,



body.mceContentBody blockquote blockquote blockquote {



border-color: #ffffff !important;



}



ol#posts li blockquote blockquote blockquote blockquote,



body.mceContentBody blockquote blockquote blockquote blockquote {



border-color: #ffffff !important;



}



ol#posts li blockquote blockquote blockquote blockquote blockquote,



body.mceContentBody blockquote blockquote blockquote blockquote blockquote {



border-color: #ffffff !important;



}



#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: #ffffff !important;

  }



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: #000000 !important;

    -webkit-border-radius: 20px;

    -moz-border-radius: 20px;

    border-radius: 20px;

    margin: auto;

    padding: 20px 20px;

  }



h1 {

color: #ffffff;

text-shadow: 0 0 3px #ffffff;

}



.no_posts_found, h1.dashboard_header {

color: #ffffff;

}



img#content_top, img#content_bottom {

display: none;

}



#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {

    background: none;

  }



#right_column .dashboard_nav_item {

    background: none;

    background-attachment: initial;

    background-position-x: 50%;

    background-position-y: 100%;

    background-origin: initial;

    background-clip: initial;

    background-color: initial;

}



#right_column .dashboard_nav_item #hide_radar {

background-image: none;

background-repeat-x: no-repeat;

background-repeat-y: repeat;

background-attachment: initial;

background-position-x: 0%;

background-position-y: 0%;

background-origin: initial;

background-clip: initial;

background-color: transparent;

}



#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(0,0,0,0);

}



#nav .nav_item.active {

    background: rgba(0,0,0,0) !important;

  }



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

    background: rgba(0,0,0,0.5) !important;

  }



ol#posts li.notification.alt {

    background-color: rgba(0,0,0,0.4);

}



ol#posts li.notification.first_notification {

    background-color: rgba(0,0,0,0.4);

}



ol#posts li.notification {

    background-color: rgba(0,0,0,0.4);

    border-bottom: 1px solid #000000;

}



ol#posts li.notification.last_notification {

    border-bottom: 1px solid #000000;

}



ol#posts li.post .permalink {

    background: rgba(0,0,0,0);

}



#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {

background: rgba(0,0,0,0);

border: solid #000000;

}



ol#posts li.post .post_avatar{

border-bottom: 0px solid #333333;

}



#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #000000;

border-bottom: 1px solid #000000;

}



#right_column a#dashboard_switch_blog_menu_current:hover {

background: none;

}



#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {

background: none;

}



form.dashboard_options_form {

background-color: rgba(0,0,0,0.5);

border-bottom: 1px solid #000000;

border-bottom-left-radius: 10px 10px;

border-bottom-left-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-right-radius: 10px 10px;

border-top-right-radius: 10px 10px;

color: #333333;

display: block;

font-size: 13px;

margin-bottom: 25px;

padding: 15px 22px;

}



form.dashboard_options_form .option_container {

border-top: 1px solid #000000;

}



.no_posts_found, h1.dashboard_header {

color: #ffffff;

}

/* .identity, .identity-refresh ,*/ .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column, .l-header-container--refresh, .identity-refresh .controls_section li:not(.section_header):hover, .identity-refresh .post_avatar, .collection_carousel .blogs_wrapper_carousel:before, .collection_carousel .blogs_wrapper_carousel:after, .discover-controls-wrapper, .discover .blog_row:hover, #search_actions_search .elevator, #discover_actions_discover .elevator {
  background: black;
}
    
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .right_column:after, .identity-refresh .radar .radar_footer .radar_avatar:before, .discover .blog_row .info:before, .discover .blog_row:hover .info:before {
  border-color: transparent;
  background: transparent;
}

.identity-refresh .post_full .post_permalink, .identity-refresh .post_full .post_permalink:not(.no-transition--dogear) {
  border-color: #ccc;
  background: transparent;
}

.identity-refresh .post_avatar .post_avatar_link, #toaster .toast .toast_border
{
  background-color: transparent !important;
}

.dashboard_options_form, .tag_psa {
  border-color: transparent;
}

.compose-button, .compose-button:active .identity-refresh .search_results_container .tumblelog_mask_item {
  color: #fff;
}

.plus-follow-button, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container, .identity-refresh .tab_notice .tab_notice_value {
  color: #333;
  border-color: #333;
}

.plus-follow-button:hover, .identity-refresh .controls_section .item:hover .follow:hover, .compose-button, .compose-button:active, .identity-refresh .search_results_container .tumblelog_mask_item {
  background-color: #aaa;
  border-color: #aaa;
}

#dashboard_controls a, #dashboard_controls .control {
  color: #aaa;
  text-shadow: #333 1px 1px 1px;
}

#pagination a, .dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form .dashboard_options_form_disclaimer a, .post_full .post_avatar .queue .publish_info.time {
  color: #aaa;
}

#pagination a:hover, #dashboard_controls a:hover, #dashboard_controls .control:hover, .dashboard_options_form, .tag_psa, .post_full .post_avatar .queue .publish_info.day {
  color: #ccc;
}

.identity-refresh .tab_notice {
  background-color: #aaa;
}

.glass.blue, #hello_glass.blue, #glass_overlay.blue, .identity-refresh .plexi.show.color {
  background: rgba(51,51,51,.9);
}
}
