css:
@-moz-document domain("ariyala.com") {

body * {
  font-family: AXIS Latin Pro, Meiryo, Verdana, Segoe UI; 
}


span.author, a.author { 
  font-family: AXIS Latin Pro, Meiryo, Verdana, Segoe UI; 
  text-shadow: 0px 0px 6px rgba(0, 0, 0, 1);
}

td.location, div.cellName, div.cellName:not(span.capsCompact) {
  text-shadow: 0px 0px 3px rgba(0, 0, 0, 1);
}

div#mainMenu, div.footer, div#categoryBox, div.categoryBoxCategory, input.categoryBoxILevelRange {
box-shadow: none;
}

td.th {
  font-weight: inherit;
  text-shadow: 0px 0px 3px rgba(0, 0, 0, 1);
}

span.overlayWindowTitle, a.title {
  font-family: Michroma, Meiryo, Eurostile Lt Std Ext Two, Verdana;
  text-transform: uppercase;
}

a.title {
  font-size:24pt;
}

}
