css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {


  .ar.as {
    position: relative;
    box-sizing: border-box;
    background-color: transparent;
    padding-left: 1.5em;
    width: 0;
    padding-top: 1px;
  }
  .ar.as .au {
    border-width: 0;
    margin-top: 0;
  }
  
  .zA.yO.aps .ar.as,
  .zA.zE:hover .ar.as,
  .zA.yO:hover .ar.as,  
  .zA.apv[id]:hover + .zA + .zA .ar.as,
  .zA.apv[id] + .zA:hover + .zA .ar.as {
    padding-left: 0;
    width: auto;
  }

  .zA.yO.aps .ar.as .at:before,
  .zA.zE:hover .ar.as .at:before,
  .zA.yO:hover .ar.as .at:before,
  .zA.apv[id]:hover + .zA + .zA .ar.as .at:before,
  .zA.apv[id] + .zA:hover + .zA .ar.as .at:before {
    display: none;
  }
  .zA.yO.aps .ar.as .au,
  .zA.zE:hover .ar.as .au,
  .zA.yO:hover .ar.as .au,
  .zA.apv[id]:hover + .zA + .zA .ar.as .au,
  .zA.apv[id] + .zA:hover + .zA .ar.as .au {
    border-width: 0 1px;
  }
  .ar.as .at:before {
    content: "";
    text-align: center;
    display: block;
    position: absolute;
    height: 1.25em;
    width: 1.25em;
    border-radius: 50%;
    background-color: inherit;
    border-color: inherit;
    border-width: 2px;
    border-style: solid;
    box-sizing: border-box;
    
    transform: translateX(-100%);
  }
  .as {
    position: relative;
  }
  .ar.as .at .au .av {
    overflow: hidden;
  }
}
