css:
@-moz-document domain("love.e1.ru"), domain("love.ngs.ru") {
.detailed-user-information{ padding: 0px 10% 0px 10%;}
.lv-top-menu.stickyeah{  width: 100% !important;}
.lv-talks-dialog.ui-dialog {left: 20% !important;}
.lv-communication {margin: 0 18px 2px;}
.lv-content   {margin-right: 0px;}
.e1-top-panel, .lv-seoblock, .page-footer, .lv-old-link, .lv-aside {display:none !important; }
.user-brief-info, .lv-top-tape, .profile__photos-user-photo-block_actions, .lv-search__first-string { margin-bottom: 4px; }
#lv-page-wrap, .top-menu__wrapper{max-width: 90% !important;}
.lv-people__item {width: 11% !important;}
.lv-people__list {margin-top: 18px;}
.lv-main-menu__link {padding: 8px 10px}
.lv-main-menu__submenu {padding: 20px 27px 4px;margin-bottom: 4px;}
.lv-content {margin-top: 4px;}
.lv-people__header-link_active, .lv-people__header-hint, .lv-pr-general__additional-info {font-size: 1.5em;}
.lv-pr-general__nick {font-size: 1.7em;}
.lv-user-info__nick-wrap_main, .lv-people__info-age, .lv-people__distance {font-size: 0.7em;vertical-align: middle;}
.lv-segment-ctrl__segment {width: 20%;}
.lv-advanced-search__item-block, .lv-advanced-search__item, .lv-advanced-search__item:first-child{margin-top: 0.5em;margin-bottom: 0.5em;}
.lv-advanced-search__actions {margin: 0.5em -10px 0;padding: 0.5em 0 0 10px;}
.lv-advanced-search__header {font-size: 1.2em;}
input[type="checkbox"]+label, .lv-custom-form__radio+label {font-size: 1.1em;vertical-align: middle;}
.lv-people__info { outline: 0px dotted blue; background-color: hsla(152, 65%, 45%, 0.2); }
.lv-user-info__decorated-wrap_newmain:visited *{ color: hsla(199, 35%, 65%, 1) !important; background: #0094FF; outline-color: orange; background-color: hsla(137, 5%, 65%, 1);}
}
