css:
@-moz-document domain("www.pandora.com") {
html {
overflow-y: auto !important;
}

body {
background-color: #DA6E86 !important;
}

#brandingBar {
background: -webkit-linear-gradient(bottom,  #DA6E86 65%,#F09DAF 100%) !important; /* Chrome10+,Safari5.1+ */
border-bottom: 1px solid #DA6E86 !important;
}

#brandingBar .middlecolumn {
color: #FFF !important;
}

#brandingBar .rightcolumn {
color: #FFF !important;
}

#brandingBar .rightcolumn a {
color: #FFF !important;
}

#brandingBar .rightcolumn .user_activator:hover {
background: #F09DAF !important;
}

#brandingBar .rightcolumn .dd_container ul {
background-color: #F09DAF;
}

/* trying to find menu */
#brandingBar .rightcolumn .dd_container ul:hover {
background-color: #F09DAF;
}

.stationChangeSelectorNoMenu p {
width: auto !important;
max-width: none !important;
}

#playerBar {
background: -webkit-linear-gradient(top,  #DA6E86 65%,#F09DAF 100%) !important; /* Chrome10+,Safari5.1+ */
border-top-color: #DA6E86;
border-bottom-color: #F09DAF
}

#playerBar .searchBox {
margin-left: 18px !important;
background-image: none !important;
display: none;
padding: 0 !important;
}

#playerBar .searchBox:before {
content: "New Station" !important;
color: #FF99CC !important;
font-weight: bold !important;
position: relative !important;
top: 6px !important;
left: 5px !important;
padding: 2px 5px 3px 6px !important;
border-top-left-radius: 3px !important;
border-bottom-left-radius: 3px !important;

background: -webkit-linear-gradient(top,  #DA6E86,#DA6E86) !important; /* Chrome10+,Safari5.1+ */
}

#playerBar .searchInput {
width: 100% !important;
background-color: #FF6699 !important;
margin-left: 2px !important;
padding: 3px 5px 3px 5px !important;
border-bottom-right-radius: 2px !important;
border-top-right-radius: 2px !important;
background-color: FF6699;  
}

#searchPopupNavPosition {
left: 80px !important;
}

#mainContainer {
background-image: url(http://fc06.deviantart.net/fs70/f/2011/023/b/a/purple_n_pink_glitter_texture_by_asphyxiate_stock-d37wru2.jpg);
background-size: cover;
}

.adSupported-layout #adLayout {
min-width: 666px !important;
width: 70% !important;
}

#mainContentContainer {
width: 100% !important;
background: #DA6E86 !important;
}

.contentContainer .top {
background-color: #DA6E86 !important;
border-top: 1px solid #B6425B !important;
}

.contentnav {
background-color: #DA6E86 !important;
border: 0 !important;
}

.stationContent {
background-color: #DA6E86 !important;
border-right: 1px solid #DA6E86 !important;
}

.stationListItem .stationName {
color: #FFF !important;
text-shadow: 1px 1px 0 #333 !important;
}

.stationListItem .stationName_hover {
background-color: #B6425B !important;
}

.stationListItem.selected {
border-top: 1px solid transparent !important;
border-bottom: 1px solid transparent !important;

background: -webkit-radial-gradient(center, ellipse cover,  #DA6E86 0%,#B6425B 100%)  !important; /* Chrome10+,Safari5.1+ */
}

.stationListItem.selected .options {
background: #F09DAF !important;
}

.btn_bg_sort {
color: #FFF !important;
text-shadow: 1px 1px 0 #333 !important;
background: -webkit-radial-gradient(center, ellipse cover,  #DA6E86 0%,#B6425B 100%) !important; /* Chrome10+,Safari5.1+ */
}

.btn_bg_sort:hover {
background: -webkit-radial-gradient(center, ellipse cover,  #DA6E86 0%,#B6425B 100%) !important; /* Chrome10+,Safari5.1+ */
}

.btn_bg_sort.selected {
background: -webkit-radial-gradient(center, ellipse cover,  #DA6E86 0%,#B6425B 100%) !important; /* Chrome10+,Safari5.1+ */
}

#stationSortDate {
border-top: 1px solid #FFF !important;
}

#stationSortAlpha {
border-top: 1px solid #FFF !important;
border-left: 1px solid #FFF !important;
}

.slidesForeground .slides {
width: 200% !important;
left: -100% !important;
padding: 0 !important;
}

.slidesForeground .slides .stationSlides {
left: 220px !important;
width: 100% !important;
}

.slidesBackground {
width: 100% !important;
background: #DA6E85 !important;
}

.slidesBackground img {
opacity: 0 !important;
}

.slidesBackground div img {
opacity: 0 !important;
}

#currentSlideArrow {
opacity: 0 !important;
width: 0 !important;
height: 0 !important;
}

#trackInfo {
background: #DA6E86 !important;
border: 0 !important;
margin-top: -4px !important;
}

#trackInfo .info {
color: #fff !important;
text-shadow: 1px 1px 0 #3b3b3b !important;
}

#trackInfo .info a {
color: #fff !important;
}

.fxCol-cont-5 {
background: none !important;
}

.fxCol-cont-1 {
background: none !important;
}

#trackInfoButtons {
  background-color: #DA6E86 !important;
}

#trackInfoButtons .buttons .button {
  border: 0 !important;
  border-radius: 0 !important;
  background-color: #B6425B !important;
  background-image: none !important;
  color: #fff !important;
}

#trackInfoButtons .buttons .button:hover {
  background-color: #F09DAF !important;
}

.btn_bg {
  text-shadow: none /*1px 1px #fff*/ !important;
}

.dropdownmenu ul {
  border-radius: 0 !important;
  background: #B6425B !important;
}

.dropdownmenu ul li:hover a {
  background: #F09DAF !important;
}
  
.dropdownmenu ul li a {
  color: #FFF !important;
}

.dd_container ul, #user_menu_dd {
    top: 21px;
    right: 4px;
    width: 120px !important;
    background-color: #B6425B !important;
    z-index: 10 !important;
    border-top-left-radius: 0 !important;
  	border: none;
}
.dd_container li > a:hover, .dd_container li:hover {
    background-color: #F09DAF !important;
}

#trackDetail a {
color: #FFF !important;
}

.detail .heading {
color: #FFF !important;
text-shadow: 1px 1px 0 #3b3b3b !important;
}

.detail .itemContent {
color: #FFF !important;
}

.detail .divider {
border-bottom: 1px solid #FFF !important;
}

#ad_container {
opacity: 0 !important;
width: 0 !important;
height: 0 !important;
}

#footer {
width: 100% !important;
margin: auto auto 10px auto !important;
}

#registeredNavFooter a {
color: #FFF !important;
}

#footer .legal {
color: #FFF !important;
}
}
