css:
@-moz-document url-prefix("http://www.incisozluk.com.tr/"), url-prefix("http://incisozluk.com.tr/"), url-prefix("http://inci.sozlukspot.com/") {
body, .table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th { background:#222 !important; color:#ccc !important; }

a {color:#999 !important; transition:all linear 0.2s !important;}
a:hover {color:#ddd !important; }

#categories { background:none !important; }

#ustbar { background:#333 !important; box-shadow: 0 0 10px #111 !important; }

.wbox { background:#111 !important; box-shadow: 0 0 10px #111 !important; border-radius:7px !important; overflow:hidden !important; }

.son_entryler li { border-bottom: #333 dashed 1px !important; width: 90% !important; margin-left: 5% !important; }
.son_entryler li:hover, .box a:hover { background:#333 !important; color:#ddd !important; }

.wbox_title { color:#ccc !important; border-bottom: dashed #333 1px !important; }

#categories .nav>li>a { color:#999 !important; transition:all linear 0.2s !important; }

#categories .nav>li>a:hover, .nav>li>a:focus { background:#333 !important; color:#ddd !important; }

#ustbar .nav>li>a, .nav>li>a { transition:all linear 0.2s !important; color:#999 !important; }

#ustbar .nav>li>a:hover, .nav>li>a:focus { background:#222 !important; color:#ddd !important; }

.giris_list .nav-tabs li>a { color:#999 !important; transition:all linear 0.2s !important; }

.giris_list .nav-tabs li>a:hover { color:#ddd !important; }

.search-query, .wbox_sub input, .wbox_sub textarea, .submit-form .input-cont textarea { box-shadow:0px 0px 5px #333 inset !important; border: 1px solid #333 !important; background:#555 !important; color:#ccc !important; }

#head_bildirim_wrap.active { box-shadow: 0 0 10px #111 !important; }

#head_bildirim_wrap { background:#333 !important; }

.alert-danger { color:#aaa !important; }

.giris_list .baslik-puan span { background:#333 !important; color:#bbb !important; transition:all linear 0.2s;}

.giris_list .baslik-puan span:hover { background:#444 !important; color:#ddd !important; }

.giris_list .index-ilk_entry { color:#999 !important;  }

.giris_list .index-title { color:#ddd !important; transition:all linear 0.2s !important;}

.giris_list .index-title:hover { background:rgba(0,0,0, 0.3) !important; color:#eee !important;}

.bigindex-list li { border-bottom: dashed 1px #333 !important; }

.entry-list .entry-text .entry-text-wrap { color:#ccc !important; }

.giris_list .index-title small { color:#333 !important; }

.dropdown-menu { background:#111 !important; box-shadow:0 0 10px #111; }

.dropdown-menu>li>a { transition:all linear 0.2s !important; }

.dropdown-menu>li>a:hover { color:#ddd !important; background:#333 !important; }

.entry-list .entry-text .entry-puan span:hover strong { color:#ccc !important; }

.btn-xs, .btn-group-xs>.btn { background:#444 !important; transition:all linear 0.2s !important;}

.btn-sm, .btn-group-sm>.btn { color:#222 !important; }

.btn-xs:hover, .btn-group-xs>.btn:hover { background:#666 !important; color:#ddd !important; }

.table-bordered { border:none !important; }

#left-frame { background:#111 !important; border-radius:7px; overflow:hidden; }

.nav-tabs { background:none !important; border:none !important; }

.index-list-li.active, #left-frame li:hover { background:#333 !important; border-bottom: 1px dashed #333 !important; }

#left-frame .nav-tabs .first a { margin-left:10px !important; }

.altsayfa-tavsiye-ul li { background:#333 !important; }

/*--------------------Takip-Edilen---------------*/

#sol_kutu {
border-radius: 0 5px 5px 0 !important;
overflow:hidden !important;
background: #333 !important;
padding: 6px 0 10px 2px !important;
box-shadow: 1px 1px 8px #111 !important;
width:133px !important; 
transition: all linear 0.2s !important;
left:-115px;
}

#sol_kutu li.active, #sol_kutu li:hover {
background:#444 !important;
}

#sol_kutu_toggle {
padding: 0 2px !important;
position: absolute !important;
height: 100% !important;
width: 18% !important;
text-align: right !important;
line-height: 1.7em !important;
float: right !important;
left: 110px !important;
top: 0px !important;
background: #444 !important;
}

/*-------------------Webkit-Scroll--------------*/

.uiScrollableArea {
   background-color: #212121!important;
}
::-webkit-scrollbar {
  width: 9px;
  height: 9px;
}
::-webkit-scrollbar-track-piece {
  background-color: #212121;
}
body::-webkit-scrollbar-track-piece {
  background-color: #212121;
}
::-webkit-scrollbar-track-piece:no-button {}
::-webkit-scrollbar-thumb {
  background-color: #444;
  border-radius: 3px;
}
::-webkit-scrollbar-thumb:hover {
  background-color: #777;
}
::-webkit-scrollbar-thumb:active {
  background-color: #666;
}

/* AGENT_SHEET */
scrollbar{
-moz-appearance: none !important;
background: none #212121 !important;
padding: 2px !important;
width: 9px !important}
scrollbar[orient="vertical"]{
padding: 2px !important;}
scrollbar[orient="horizontal"]{
height: 9px !important;}
scrollbarbutton[sbattr="scrollbar-up-top"],
scrollbarbutton[sbattr="scrollbar-down-top"],
scrollbarbutton[sbattr="scrollbar-up-bottom"],
scrollbarbutton[sbattr="scrollbar-down-bottom"]{
-moz-appearance: none !important;
display: none !important}
scrollbar thumb{
    -moz-appearance: none !important;
    background: none #444 !important;
    border: none !important;
    border-radius: 5px; }
scrollbar thumb:hover{
    background: none #555 !important;}
scrollbar thumb:active{
    background: none #555 !important}

}
