.collapsible dt,
.color-blue-medium,
.color-green-darkest,
.post-authors p:first-child,
em,
h3,
h4,
th {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown ___________________________________________________________*/

.label,
strong,
h5,
h6 {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________*/

.color-secondary-text:not(.gap-top-300),
time {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*_________________________________________________________________________*/

.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*_________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre::selection {
	background: #ff9!important;
	color: #fff!important;
}

/*___ Pre Code Colors _______________________________________________________*/

.atn {
  color: #ff53ff !important;
}

.code,
code,
pre {
  color: #02ff02 !important;
}

.token.operator {
  color: #f6e5c9 !important;
}

.dec,
.var {
  color: #f0f !important;
}

.kwd {
  color: #9e9eff !important;
}

.language-css .token.string,
.token.attr-name,
.token.attr-value .punctuation:first-child,
.token.keyword,
.token.operator,
.token.regex,
.token.selector,
.token.url {
  color: #ff46d7;
  text-shadow: 1px 1px #000 !important;
}

.lit {
  color: #0ff !important;
}

.pln {
  color: #ff8f8f !important;
}

.str {
  color: #0f0 !important;
}

.tag,
.token.property,
.token.tag {
  color: #b7b7ff !important;
}

.token.atrule,
.token.punctuation,
.token.string {
  color: #2e7eff;
}

.token.attr-value,
.token.punctuation,
.token.string {
  color: #5d9cff;
}
.token.boolean,
.token.number {
  color: #e8f604 !important;
}

.clo,
.opn,
.pun,
.token.punctuation,
.token.string {
  color: #fff !important;
}

.typ {
  color: #fe4cfe !important;
}
}

