@-moz-document domain("developer.chrome.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}

@-moz-document domain("developer.chrome.com") {
/*________________________________________________________________________*/

* {
	border: none!important;
	border: 0!important;
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

#fatnav .pillar.active .expandee {
	display: block!important;
}


/*___ Fonts ________________________________________________________________*/

body,
h1,
h2,
h3,
h4,
h5,
h6,
html {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

body {
	font-size: 23px !important;
}

.inline-site-toc li.toplevel > ol.toc > li,
.simple th,
table {
	font-size: 21px!important;
}

.simple td {
	font-size: 19px!important;
}

.inline-site-toc .toc .toc li,
.label {
	font-size: 17px!important;
}


/*___ Line-Heights ___________________________________________________________*/

body {
	line-height: 33px !important;
}

.simple td {
	line-height: 27px !important;
}


/*___ Remove Items __________________________________________________________*/

[id*="ContentPane"]:empty,
Selector {
	position: absolute!important;
	top: -9999px!important;
	left: -9999px!important;
}


/*___ Left/Right/Top/Bottom __________________________________________________*/

.article-content [itemprop="articleBody"] {
	margin-left: 24rem!important;
}

.sample ul {
	margin-left: 2rem!important;
}

.sample div {
	margin-top: 2rem!important;
}


/*___ Margins _______________________________________________________________*/

#featured,
.g-content,
.inline-site-toc,
.sample,
img {
	margin: 12px!important;
}

.collapsible dt,
h2 {
	margin: 0.6em 0!important;
}

.collapsible dd {
	margin-bottom: 2em!important;
}


/*___ Paddings _____________________________________________________________*/

#featured,
.article-content [itemprop="articleBody"],
.g-content,
.g-section,
.sample {
	padding: 12px!important;
}

.inline-site-toc .toc .toc li a,
.code,
code,
mark,
pre,
pre b {
	padding: 3px 10px!important;
}


/*___ Heights _______________________________________________________________*/

selector {
	Max-height: none!important;
}


/*___ Widths _______________________________________________________________*/

.gc-container {
	max-width: none!important;
}

#fatnav .expandee {
	width: auto!important;
}

.gc-container {
	width: 92%!important;
}

.inline-site-toc {
	width: 20%!important;
}
}

@-moz-document domain("developer.chrome.com") {
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}


/*_________________________________________________________________________*/

#fatnav .pillar.highlight .toplevel,
#fatnav .toplevel,
body,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Khaki _________________________________________________________________*/

#fatnav .expandee li.submenu,
.simple td:first-child,
h1,
h2 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Burlywood _____________________________________________________________*/

.collapsible dt,
h3,
h4,
th {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Sandy Brown ___________________________________________________________*/

.label,
strong,
h5,
h6 {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Blue _________________________________________________________________*/

selector {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}


/*_________________________________________________________________________*/

.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}


/*_________________________________________________________________________*/
::selection {
	background: #ff9!important;
	color: #000!important;
}

pre::selection {
	background: #ff9!important;
	color: #fff!important;
}


/*___ Pre Code Colors _______________________________________________________*/

.clo,
.opn,
.pun {
	color: #fff!important;
}

.str {
	color: #0f0!important;
}

.pln {
	color: #ff8f8f!important;
}

.kwd {
	color: #9e9eff!important;
}

.lit {
	color: #0ff!important;
}

.tag {
	color: #b7b7ff!important;
}

.dec,
.var {
	color: #f0f!important;
}

.code,
code,
pre {
	color: #02ff02!important;
}

.typ {
	color: #fe4cfe!important;
}

.atn {
	color: #ff53ff!important;
}
}

@-moz-document domain("developer.chrome.com") {
/*________________________________________________________________________*/

html,
body {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

mark,
pre b {
	background: #ffff001a!important;
}

/*___ Background 0000 ______________________________________________________*/

th {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 _________________________________________________*/

.inline-site-toc li.toplevel > ol > li.show {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background __________________________________________________*/

.article-content tr:hover,
.simple tr:not(:first-child):hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*___ Faded Background bottom (Usually expand paragraph) ______________________*/

selector {
	background: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(120, 224, 240, 0.13))!important;
}

/*_________________________________________________________________________*/

#fatnav .expandee a.highlight,
#fatnav .expandee a:hover,
#fatnav .expandee li.submenu.active {
	background-image: none!important;
}

/*_________________________________________________________________________*/

pre,
code {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
}

/*__ Blue __________________________________________________________________*/

.button {
	border-radius: 10px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.button:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

.button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Plain Black _____________________________________________________________*/

#fatnav .expandee,
.article-content [itemprop="articleBody"],
.g-section,
.inline-site-toc {
	background-color: #000!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#fatnav .expandee:hover,
.article-content [itemprop="articleBody"]:hover,
.g-section:hover,
.inline-site-toc:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Plain __________________________________________________________________*/

#featured,
.g-content,
.sample {
	background-color: #111!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#featured:hover,
.g-content:hover,
.sample:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Noise __________________________________________________________________*/

#topnav,
Footer {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

#topnav:hover,
Footer:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Red Gradient ___________________________________________________________*/

.inline-site-toc .toc .toc li a:hover,
aside.caution,
aside.note,
aside.warning,
div.caution,
div.note,
div.warning,
p.caution,
p.note,
p.warning {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/*__ Green Gradient _________________________________________________________*/

#fatnav .pillar.active {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	text-shadow: 1px 1px #555!important;
}
}

@-moz-document domain("developer.chrome.com") {
/*________________________________________________________________________*/

.g-content,
.sample,
dd {
	filter: brightness(75%)!important;
}

/*________________________________________________________________________*/

.g-content:hover,
.sample:hover,
dd:hover {
	filter: brightness(100%)!important;
}

/*________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*________________________________________________________________________*/

li:not(.toplevel):not(.show),
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

li:not(.toplevel):not(.show):hover,
p:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}
}

@-moz-document domain("developer.chrome.com") {





/* Chrome Logo _______________________________________________________________*/

