	background: url(https://i.ibb.co/4sgCdvW/Chrome-Logo.png) no-repeat!important;
	box-sizing: border-box;
}

#site-sections .g-content h2.devtools::before {
    background-size: 50px!important;
    height: 50px!important;
    padding-left: 50px!important;
    width: 10px!important;
}

/* Chrome OS _______________________________________________________________*/

#site-sections .g-content h2.multidevice::before {
	background: url(https://i.ibb.co/dsTpg46/laptop.png) no-repeat!important;
	box-sizing: border-box;
}

#site-sections .g-content h2.multidevice::before {
    background-size: 60px!important;
    height: 60px!important;
    padding-left: 60px!important;
    width: 10px!important;
}

/* Multi Device _______________________________________________________________*/

#site-sections .g-content h2.platform::before {
	background: url(https://i.ibb.co/VHrGNFV/Devices.png) no-repeat!important;
	box-sizing: border-box;
}

#site-sections .g-content h2.platform::before {
    background-size: 159px!important;
    height: 90px!important;
    padding-left: 157px!important;
    width: 10px!important;
}



/* Search _______________________________________________________________*/

#search img {
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box;
}

#search img {
    background-size: 21px!important;
    height: 32px!important;
    padding-left: 25px!important;
    width: 10px!important;
}

}
