css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iizcat.com") {

#header {
    background: url(/*[[bkgr]]*/)center fixed !important;
  }
  
  #header-inner {
    background-color: rgba(0, 0, 0, 0.2);
  }
  
  .saying2 {
    color: #FFF;
}
  
  #footer {
    background-color: rgba(0, 0, 0, 0.2);
    padding: 25px 0px 40px;
    margin: 0px auto;
}
  
  #content-inner {
    background-color: whitesmoke;
}
  
  .rightnews {
    display: block;
    text-decoration: none;
    width: 300px;
    padding: 0px;
    border: medium none;
    position: relative;
    margin-bottom: 15px;
    text-align: left;
    background-color: white;
    box-shadow: 0px 0px 1px 0px rgba(81, 81, 81, 0.21);
}
  
  input.gsc-search-button, input.gsc-search-button:hover, input.gsc-search-button:focus {
    border-color: transparent;
    background-color: rgba(0, 0, 0, 0.2);
    background-image: none;
    filter: none;
}
  
  .post-box {
    box-shadow: 0px 0px 0px 1px rgba(23, 23, 23, 0.13);
}
  
}
