css:
@-moz-document url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {

.tour_guide * {
display: none!Important;
}

.popover.tutorial, .tour_guide .popover {
display: none;
}
.tour_guide.dim .glass {
display: none!important;
}

.tour_guide {
display: none!important;
}

.post_control.like.tour_guide_element:after, .post_control.like.tour_guide_element:before, .post_avatar.tour_guide_element, #discover_button.tour_guide_element .tab_anchor, #discover_button.tour_guide_element:after, #new_post.tour_guide_element .icon_post_text {
animation: none;
-webkit-animation: none;
-moz-animation: none;
}


.tour_guide_element {
z-index: 1!important;
}

.tour_guide_open[data-tour-guide-step='search'] .l-header .logo, .tour_guide_open[data-tour-guide-step='search'] .l-header .compose-button, .tour_guide_open[data-tour-guide-step='search'] .l-header .ui_search:not(.tour_guide_element), .tour_guide_open[data-tour-guide-step='search'] .l-header .tab:not(.tour_guide_element), .tour_guide_open[data-tour-guide-step='discovery'] .l-header .logo, .tour_guide_open[data-tour-guide-step='discovery'] .l-header .compose-button, .tour_guide_open[data-tour-guide-step='discovery'] .l-header .ui_search:not(.tour_guide_element), .tour_guide_open[data-tour-guide-step='discovery'] .l-header .tab:not(.tour_guide_element) {
opacity: 1;
pointer-events: initial;}

#discover_button.tour_guide_element .tab_anchor {
opacity: .5;}

#new_post.tour_guide_element .post_avatar_link {
opacity: 1;
}

}
