css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.google.com") {

  table.F {
    display: block;
    min-width: 356px;
    width: 100%;
    overflow: hidden;
  }
  table.F tbody {
    display: block;
  }
  table.F colgroup,
  table.F tr {
    width: 100%;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
  }
  table.F col,
  table.F td {
    display: block;
    overflow-x: hidden;
  }
  
  /* Handler */
  table.F col:nth-child(1),
  table.F td:nth-child(1) {
    flex: 2px 0; }
  
  /* Checkbox */
  table.F col:nth-child(2),
  table.F td:nth-child(2) {
    flex: 29px 0; }
  table.F td:nth-child(2) {
    padding-top: 5px!important;
    padding-bottom: 1px !important; }
    
  /* Star */
  table.F col:nth-child(3),
  table.F td:nth-child(3) {
    flex: 24px 0; }
  table.F td:nth-child(3) {
    padding-bottom: 4px; }
  
  /* Important */
  table.F col:nth-child(4),
  table.F td:nth-child(4) { 
    flex: 26px 0; }
  table.F td:nth-child(4) {
    padding-left: 3px; }
   
  /* Author */
  table.F col:nth-child(5), 
  table.F td:nth-child(5) { 
    flex: 184px 0; }
  table.F td:nth-child(5) {
    padding-left: 7px;
    padding-top: 5px;
    padding-bottom: 1px; }
  
  /* Subject, preview & badges */
  table.F col:nth-child(6), 
  table.F td:nth-child(6) { 
    flex: 35; }
  table.F td:nth-child(6) {
    padding-top: 5px;
    padding-bottom: 1px; }
    
  /* Attached */
  table.F col:nth-child(7),
  table.F td:nth-child(7) {
    flex: 25px 0; }
  
  /* Date */
  table.F col:nth-child(8),
  table.F td:nth-child(8) {
    flex: 8.25ex 0; }
  table.F td:nth-child(8) {
    padding-top: 5px;
    padding-bottom: 1px; }

}
