css:
@-moz-document domain("scpfoundation.123ubb.com") {
body {
  background-color: #464c55 !important;
}

#ipbwrapper {
  max-width: 1000px !important;
  box-shadow: 0 0 2px rgba(0, 0, 0, .12), 0 2px 4px rgba(0, 0, 0, .24) !important;
  padding: 0 !important;
}

#main-content {
  background-color: #e4e6e9 !important;
}

#main-content > div:first-child,
#main-content > script + div{
  display: none !important;
}

.postprofile {
  max-width: 10em !important;
  margin-left: -10em !important;
}

.postdetails>dt>a>img {
  width: 100% !important;
}

.post-container {
  margin-left: 10em !important;
}

@font-face {
  font-family:'宋体';
  src: local('Wenquanyi Micro Hei'), local('SimSun');

}

.post-entry {
  line-height: 200% !important;
  letter-spacing: 0.5px !important;
}

.borderwrap {
  border: none !important;
  background-color: transparent !important;
  padding: 0 !important;
}

#gfooter .footer-links {
  width: auto !important;
}

/* - - - */

/* mobile */

.content {
  line-height: 200% !important;
  letter-spacing: 0.5px !important;
  font-size: 16px !important;
}
  
.mobile_item {
  background: none #DBDBDD !important;
}

.mobile_set {
  background: none #347DEE !important;
}
  
.mobile_title, .post_header {
  background: none #3f5c80 !important;
}
    
.mobile_prev_button, .mobile_next_button {
  border-radius: 0 !important;
  background: none transparent !important;
  border: none !important;
  margin: 0 !important;
  box-shadow: none !important;
}

.mobile_subtitle, .mobile_comment {
  text-shadow: none !important;
}
  
.mobile_item {
  border: none;
  border-bottom: 1px solid #9C9EA5;
}

.banniere {
  display: none !important;
}

/* both desktop and mobile */
.post {
  background: #e4e6e9 !important;
}
}
