css:
@-moz-document domain('craigslist.org'), url-prefix('http://www.craigslist.org/') {
html, body, .homepage #leftbar, .homepage #rightbar, .ban, #fmtsel, .homepage ul, fieldset{
background-color:black !important;
color:white !important;
}
a, .linklike, .post .picker label, .post #postingForm #perms label, label.std.checkbox .label {
color:#01DF01 !important;
}
.homepage .col ul li a {
color:#01DF01 !important;
}
  
  tr, body .highlight, div#postingbody span {background:transparent!important; border: none !important;}
  
  tbody, header.bchead, .homepage #postlks #post, .homepage #topban, .homepage #topban .sublinks a, .homepage .cal {background: transparent !important;}
  
  #element, .post .images .imgbox .imgwrap, .post .images.ui-sortable .imgbox, div#postingbody table tbody tr td {background:transparent !important;}
  
  .account table.accthp_postings td, #pagecontainer header .user.post a {background:transparent!important;}
  
  .account table.accthp_postings {border: none}
  
  small.gc, #searchform .hasPic, footer .rss .l {background: transparent !important;}
  
  td.buttons, td.status, td.title, td.areacat, td.dates, td.postingID, .account table.accthp_postings th, select#catAbb, select#datePick, input, select, .homepage .cal td, .bchead .highlight, button.pickbutton, .post .picker label input[type="radio"], .post .bigbutton, .post textarea, .post #leafletForm #mapinfo, button.skipmap, .post #leafletForm #search_button, .post #leafletForm #map, .post #uploader, .post #uploader #plupload, .post .images .imgbox:first-child:before, .draft_warning, .draft_warning button, button, .desktop.toc .toc_legend, .form-tab, #searchform, #searchform .categories, .toc .row .gc, .tsb, .posting div.reply_options .anonemail, td div, .posting .attrgroup span, section.body table#searchtable tbody tr td, div.accountsettings span.label {color: white !important; border: 1px solid #01DF01 !important; background: transparent !important;}
  
  .account input.managebtn {color: white; text-decoration: none !important;}
  
  input:hover, button.pickbutton:hover, .post .bigbutton:hover, button.skipmap:hover, .post #leafletForm #search_button:hover, .post #uploader #plupload:hover, .post .images form button:hover, #pagecontainer header .crumb.cl:hover, .draft_warning button:hover, button:hover, .form-tab:hover {border-color: white !important;}
  
  fieldset, .post .posting, #pagecontainer header .crumb.cl {background: transparent !important; border: 1px solid #01DF01 !important;}
  
  table.accthp_postings {background: transparent !important;}
  
  .tabcontainer, .post .psa-box {border-color: #01DF01 !important;}
  
  .tabcontainer b, .tabcontainer a {color: white; border: 1px solid #01DF01 !important; background: transparent !important; border-bottom-color: #01DF01 !important;}
  
  .tabcontainer a:hover {border-color: white !important; background: transparent !important;}
  
  .homepage .cal td:hover, .homepage .cal a:hover {background-color: white !important; color: black !important;}
  
  .homepage #query, .post #uploader .draggable-only, .toc .row time {color: white !important;}
  
  fieldset#searchfieldset table#searchtable tbody tr td {border: none !important;}
  
  .homepage footer {border: none !important; background: transparent !important;}
  
  .homepage a:hover {background: transparent !important; text-decoration: underline !important;}
  
  .post .posting {border-color: #01DF01; box-shadow: 1px 1px 2px 1px #01DF01 !important;}
  
  .leaflet-popup-content-wrapper, .post .images form button, .toc .buttongroup, .toc .daybubbles, #pagecontainer > header, footer, .posting div.reply_options {background-color: black !important; border: 1px solid #01DF01;}
  
  .post .images .imgbox:first-child, .iw .carousel {border-color: #01DF01}
  
  .post #uploader .loading div.pbar {background-color: black;}
  
  .post #uploader .loading div.pbar div {background-color: #01DF01;}
  
  .toc .buttongroup, .toc .toc_legend .paginator .pagenum {color: white !important;}
  
  .iw #thumbs img.selected, #searchform {border-color: #01DF01; box-shadow: none !important;}
  
  .form-tab {padding-bottom: 7px;}
  
  .toc .row .gc:hover {border-color: white !important; text-decoration: none !important;}
  
  .toc .buttongroup .button.sel {color: white!important; border-color: white !important; background: transparent!important;}
}
