css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("eve-central.com") {
  body {
      font-family: "source-sans-pro",sans-serif;
      margin: 0px;
      background: none repeat scroll 0% 0% #222222;
      color: #777;
  }
    #bodyText {
    margin: 2px;
    padding: 0px;
}
  #bodyText form button {
      background-color: #468;
      border-radius: 2px;
  }
  #bodyText form a, #bodyText form a:visited {
              border-radius: 2px;
              background-color: #654;
              color: #cba;
            }
  .search_suggestions a, .search_suggestions a:visited {
                 background-color: #345 !important;
                 color: #ccc;
               }
  .topbar form button, button {
      border: 1px solid #567;
      padding: 0px;
      border-radius: 2px;
      background-color: #456;
  }
  input, #bodyText form input[type="text"]  {
      border: 1px solid #345;
      padding: 3px;
      border-radius: 2px;
      background-color: #234;
      color: #fff;
  }
  a {
      color: #abc;
  }
  a:hover {
      color: #def;
  }
  table {
    border-collapse:collapse;
    border-spacing: 2px;
  }
    tr { padding-left: 5px; }
  td {
  }
  th {
      background-color: #333;
      color: #eee;
  }
  .quicklookSorted {
      background-color: #444;
      border: 0px solid #654;
  }
  .highlightSell {
      background-color: #000;
      border-top: #333;
      border-bottom: #333;
  }
  .highlightBuy {
      background-color: #000;
      border-top: #333;
      border-bottom: #333;
  }
  div #sell_orders {
      width: 49%;
    float: left;
      min-width: 488px;
  }
  div #buy_orders {
      width: 49%;
    float: right;
    min-width: 480px;
  }
  #sell_orders th { 
      padding: 5px;
  }
  #buy_orders th {
      padding: 5px;
  }
  #statsHolder {
    text-align: center;
  }
  #footer {
    clear: both;
  }
  #sell_orders {
  }
  #buy_orders {
      margin-left: 5px;
  }
  #bodyText .quicklookHeader form {
      line-height: 25px;
      background-color: #333;
      margin-left: 0px;
  }
  #bodyText form {
      background-color: #233;
      padding: 8px;
      color: #ccc;
      line-height: 30px;
      border-radius: 5px;
  }
  .stat-bar {
      color: #ccc;
      padding: 15px 5px;
      background-color:  #222;
      border-radius: 3px;
      margin-top: 20px;
      margin-bottom: 30px;
  }
  .stat-bar .sepdiv {
      margin: 10px 0;
      color: #999;
      background-color: #222;
  }
    .stat-bar div:nth-child(even) {
     background: #333;
}
  .picked {
      color: #579;
  }
  .tradeTool {
      width: 100%;
      background-color: #233;
      padding: 23px;
      border-radius: 5px;
      border: 1px solid #344;
      margin-bottom: 20px;
  }
  .doctable th {
      background-color: #333;
  }
  .docsection {
      margin-right: 10em;
      margin-left: -2ex;
      font-family: "freight-sans-pro",sans-serif;
      font-size: 150%;
      font-weight: 700;
      background-color: #333;
  }
}
