css:
@-moz-document domain("archiveofourown.org") {
#outer, #footer, #feedback .group .heading, #feedback .group.odd, #main.maintenance, .reading h4.viewed, .actions a:active, .current, a.current, .current a:visited, fieldset fieldset, fieldset dl dl, form blockquote.userstuff {
    background: #D8D8D8;
}

#footer .heading, #footer a, #footer li {
    color: #111;
}

#header {
    background: #FFF;
}

#header .primary {
    background: #900;
}

#header .menu, #small_login, #header .dropdown:hover .current + .menu, #header .actions a:hover, #header .actions a:focus, #header .dropdown:hover a, #header .open a, #greeting .menu {
    background: #C6C6C6;
    box-shadow: none;
}

#dashboard a:hover, #header .dropdown .menu a:hover, #header .dropdown .menu a:focus {
    background: rgba(225, 225, 225, 0.9);
    color: #900;
}

#dashboard .current, li.relationships a {
    background: rgba(0, 0, 0, 0.09);
}

#dashboard.own {
    border-top: none;
    border-bottom: none;
}

#dashboard ul {
    border-top: none;
}

#inner, #inner .group, .meta {
    background: rgba(0, 0, 0, 0.15);
    border: 1px solid rgba(255, 255, 255, 0.5);
}

#inner, #inner .group, .meta {
    background: rgba(216, 216, 216, 1);
    border: 1px solid rgba(216, 216, 216, 1);
}

#small_login {
    border: none;
}

.splash .intro {
    background: #D8D8D8;
    box-shadow: none;
}

.wrapper {
    box-shadow: none;
}

.works-index .filters {
    display: none;
}

.listbox, fieldset fieldset.listbox, .listbox .index {
    background: #D8D8D8;
    border: 2px solid #D8D8D8;
    box-shadow: none;
}

.actions a:active, .current, a.current, .current a:visited {
    border-color: #D8D8D8;
}

.notice, .comment_notice, ul.notes, .caution, .error, .comment_error {
    background: #BBB;
    border: 1px solid #D8D8D8;
}

.secondary {
    background: #C6C6C6;
    border: none;
    box-shadow: none;
}

.bookmark .count a {
    color: #900;
}

h2 {
    text-shadow: none;
}

li.blurb, .blurb .blurb, form .notice, form ul.notes {
    box-shadow: none;
}

a:hover {
    color: #2D2D2D;
}

fieldset, form dl, fieldset dl dl, fieldset fieldset fieldset, fieldset fieldset dl dl, dd.hideme, form blockquote.userstuff {
    background: #D8D8D8;
    border: none;
    box-shadow: none;
}

dl.meta, div.comment, li.comment {
    border: none;
}

.status .public {
    background: url("https://i.imgur.com/KMrmhWj.png") -125px -50px;
}

.required-tags .rating-notrated, .required-tags .category-none, .required-tags .warning-no, .status .count {
    background: url("https://i.imgur.com/KMrmhWj.png") -150px 0px;
}

.status .private .text {
    background: url("https://i.imgur.com/KMrmhWj.png") -175px -50px;
}

a.rss span {
    background: url("https://i.imgur.com/KMrmhWj.png") no-repeat -150px -275px;
}
}
