css:
@-moz-document domain("cronometer.com") {
*:not(.target-bar){background-color:#222 !important;color:white;text-shadow: 1px 1px 0 #000 !important;/*, 1px 0 #000, 0 -1px #000, 0 1px #000 !important;*/  border-color: #333 !important;
}
.datePickerDayIsValue{color:white;background-color:orange !important;}
.datePickerDayIsToday{background-color:#27ae60 !important;color:white !important;}
.nutrientTargetBar-frame, .target-bar-frame, .gwt-Label, .gwt-Anchor{background-color: transparent !important;}
.GPBYFDEIS, .GPBYFDEJS, .GPBYFDENS, .GPBYFDELS, .GPBYFDEMS{background-color:#27ae60 !important;}
.GPBYFDEGM {background-color:white !important; }
.select * {background-color: black !important;}
.datePickerMonth{color: #fff !important;  
  }

.GHIM42QCHS, .GHIM42QCKS, .GHIM42QCAT, .GHIM42QCNS, .GHIM42QCDQ, .GHIM42QCAQ, .GL-TVABCEV, .GL-TVABCHV, .src-component-Launcher-wrapper.u-isActionable.u-textLeft.u-inlineBlock.u-borderNone.u-textBold.u-textNoWrap.Arrange.Arrange--middle.u-userLauncherColor > * {
    background-color: transparent !important;
}

.src-component-Icon-container.src-component-Launcher-icon.src-styles-components-Icon-Icon.Arrange-sizeFit.u-textInheritColor.u-inlineBlock.Icon > svg {
    background-color: #27ae60 !important;
}
    
svg.highcharts-root {
    filter: invert(86.5%);
}

.summary-protein .nutrientTargetBar-text, .summary-carbs .nutrientTargetBar-text, .summary-lipids .nutrientTargetBar-text, .summary-energy .nutrientTargetBar-text {
    color: white;
}
}
