css:
@-moz-document url-prefix("https://www.tumblr.com") {

body:not(.mceContentBody), html .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .left_column {
background-color: transparent !important;
}

body:not(.mceContentBody) {
background-image: transparent !important;
  background-position: top !important;
  background-repeat: no-repeat !important;
}

#dashboard_iframe {
    background: transparent !important;
  }

html .identity-refresh .controls_section li:hover {
      background-color: transparent !important;
    }
      
      html .identity-refresh .controls_section.user_list li:hover {
  background: transparent !important;
              }
            
            .l-content .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column, .l-header-container--refresh {
  background-color: transparent !important;
      box-shadow: none !important;
                    }
      
      html .identity-refresh .controls_section.user_list li .follow_list_item_blog:before, .identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before, .identity-refresh .radar .radar_footer .radar_avatar:before {
      		background-image: transparent;
			border-right: 5px transparent;
              }
      
      html .identity-refresh .right_column:after {
             background-image: transparent; 
            }

            
            .l-header-container--refresh .l-header {
                  min-width: 910px !important;
                  max-width: 910px !important;
                }
                
                
              .identity-refresh .selected .tab_notice, .l-header-container--refresh .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
  						border:transparent !important;
                        background: transparent !important;
        				left: 32px !important;
						top: -10px !important;
                      }
                      
                 .identity-refresh .tab_notice .tab_notice_value {
                              color: #fff !important;
                            }

.l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .l-content {
        padding: 15px 15px 0px 15px !important;
  margin-top: -20px !important;
      }

.l-header-container--refresh {
    padding-top: 9px !important;
    position: absolute !important;
  }
  
  .elevator-wrapper {
      top: 20px !important;
     right: 20px !important;
  height: 55px !important;
    }

	#return_to_bottom {
  top: auto !important;
  		bottom: 20px !important;
}
    
.ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before, .ui_search .search_query {
                border: 0px !important;
                background: transparent !important;
              }



.elevator {
  width: 55px !important;
  height: 55px !important;
    background: transparent !important;
    border-radius: 10px !important;
  }
  
  
  .elevator:before {
  font-size: 55px !important;
  line-height: 80px !important;
  color: #2C4762 !important;
      }


.l-header-container--refresh .l-header .compose-button {
        display: none !important;
      }
      

}
