css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.manjaro.org") {
*{font-family: Dejavu Sans!important;
  font-size: 13px !important;}
  #quicknav, #notices, .signature, .attachments {display: none;}
  .windowbg2{background-color: #4C4C4C !important;}
  .windowbg{background-color: #696969 !important;}
  
 .table_list .section-header td, .table_grid .section-header th, .table_grid .section-header a, .calendar_table tr th {
    color: #ffffff !important;}
  
 a:link, a:visited {color: #6DBAC2 !important;} 
 body, td, th, tr {color: #D4D4D4 !important;}
  
 #main-content {background: rgb(39, 39, 39);}
 #toolbar {background: #505050;}
 #navbar {background: #111111}
 #logobutton a {display: none !important;}
 .roundframe, span.upperframe {
    background: none repeat scroll 0 0 gray;
    display: block;
    padding: 0 0 0 0px;
  	border: none;}

 span.lowerframe, span.upperframe span, div.title_barIC, div.cat_bar, h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header  td.catbg {
    background: none repeat scroll 0 0 #29B4C3 !important;
    padding-right: 0px;
    padding-left: 0px;}
    
 div.title_barIC h4.titlebg {background: none repeat scroll 0 0 #d3d3d3;}
  
 .inner {border-top: 0px solid #c7c7c7;}
  span, span.topslice, span.botslice {height: 0px !important;background: none !important;}
  td.icon2.stickybg, td.lastpost.stickybg, td.stats.stickybg2, td.subject.stickybg2 {background: #333333;}
  div.roundframe div.cat_bar h3.catbg { background: #505050;}
  .table_grid .stats span, .table_grid .lastpost span {color: #8C8C8C;}
 blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {background: #303030;color:#c3c3c3;border: none;} 
 .smalltext, tr.smalltext th {color: #C6D6C6;font-weight: bold;}
  #display_jump_to {display: none!important;}
  #account li a:hover {background: none repeat scroll 0% 0% #929292;}
  #account li a {background: none repeat scroll 0 0 #111111;}
  #topnav li a.active {background: none repeat scroll 0 0 #929292;}
  #toolbar, .table_list .content td, .table_grid tbody td, #forumposts .windowbg, #forumposts .windowbg2 {
  border-bottom: 1px solid #6f6f6f;}
  #topnav li:hover > a {background: #3F3F3F !important;}
  #upshrinkHeaderIC {background: #505050;}
}
