css:
@-moz-document url-prefix("http://katproxy"), url-prefix("http://kickass") {
	body {
	    color: #95BAEC;
	}
	#wrapperInner {
    background-color: #202020;
	}
	.mainpart {
		background-color: #202020 !important;
	}
	.odd {
		background-color: #2D2A20 !important;
	}
	.cellMainLink {
		color: #BBBBBB !important;
	}
	.torrentFileList tr.last .torTree {
    	background-color: #202020;
	}
	.torrent_files .folder, .torrent_files .folderopen {
    	background: none repeat scroll 0% 0% #594C2D;
	}
	.sharingWidget {
    	background-color: #292620;
	}
	.commentbody, .commentform textarea {
    background-color: #38362B !important;
	}
	.data tr td, .data tr th {
		border-color: -moz-use-text-color #86854A #86854A -moz-use-text-color;
	}
	.qrate, a.turnoverButton, a.turnoverButton.blank:hover, #previewPopupContainer #previewPopupContent, .previewPopupContainer .previewPopupContent  {
    background: linear-gradient(to bottom, rgba(92, 84, 65, 0.73) 0%, rgba(84, 77, 61, 0.75) 100%) repeat scroll 0% 0% rgba(0, 0, 0, 0);
	color: white;
	}
	#contentSearch, .selectedTab {
	background-color: #EEE8C9 !important;
	}
	.even:hover, .odd:hover, .torFileName:hover {
		background-color: #101010 !important;
	}
}
