css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("115.com") {
  div.frame-wrap {width:1500px !important;}
  div.wide-page {width:1500px !important;}
  ul.list-thumb {width:1450px !important;}
  ul.list-thumb li {width:120px !important; height:200px !important;}
  /*ul.list-thumb li i.file-thunb {top:20px !important;}*/
  div.file-info {width:120px !important;position:absolute !important; top:-5px !important;}
  div.file-opt {top:185px !important;left:10px !important;}
  div.img-pos {vertical-align:middle !important;}
  
  .file-name {white-space:pre-wrap !important; word-break: break-all !important; word-wrap:break-word !important;}
  
  ul.list-thumb div.file-name {width:120px !important; height:80px !important; }
  
  ul.list-thumb span.file-name {width:120px !important;height:68px !important; }
  div.file-select  {width:1200px !important; height:700px !important;}
  div.dialog-frame  {height:580px !important;}
  div.file-select-list {height:560px !important;}
  div.dialog-box {width:1000px !important; }
  div.upload-box  {width:1200px !important; }
  div.select-container  {width:auto !important; height:580px !important;}
  div.select-container li  {width:120px !important;}
 
  div.select-thumb li {height:132px !important}
  
  div.select-thumb li a.file-name {width:120px !important; height:64px !important; }
  div.upload-box span.file-name {width:800px !important; white-space:pre-line !important;}
  div.select-container span.file-name {width:120px !important; height:68px !important; }
  div.dialog-input input.text {width:700px !important;height:18px !important; font-size:16px !important;}
  
  ul.list-contents div.file-name {width:800px !important; height:32px !important;}
  ul.list-contents li {float: left !important; width:700px !important;}
}
