css:
@-moz-document domain("instructables.com") {
html body { 
    background-color: #1b1b1b !important; 
    color: #aaaaaa !important;
  
}

.header-bar, .header-gradient, .search-header, .sticky-header.fixed  {
 background: -webkit-gradient(linear, 0 0, 0 100%, from(#6b6b6b), to(#555555)); 
  }

/*Contest Header*/ 
#header-nav-container {
  background:-webkit-gradient(linear, 0 0, 0 100%, from(#6b6b6b), to(#555555)); 
}
#header-promo-container {
   background-color: #303030 !important; 
}

/* Normal Header*/
#gbl-header
.top-bar{
  background: -webkit-gradient(linear, 0 0, 0 100%, from(#6b6b6b), to(#555555)); 
  }
.bottom-bar {
  background-color: #303030 !important; 
  }

#gbl-footer
.top{
    background: linear-gradient(to bottom,rgba(0,0,0,0) 0%,rgba(50,50,50,1) 100%) !important;
    }
.bottom{
      background: #303030 !important; 
      }
  
.pagination ul>li>a, .pagination ul>li>span {
  background-color: #303030;
  border-color: #404040 !important;
  }

/* buttons top dropdown menu */
.header-menu {
  background-color: #303030 !important; 
  border-color: #202020 !important;
  }

#create-menu .type, #explore-menu .btn-flat-white {
  background-color: #505050 !important;
}

/*home page*/
#home-projects{
    background: #222222 !important;
    border-bottom: none !important;
    }


#omni{
background-color: #1b1b1b !important; 
}

#omni .container .header-bar, .header-gradient  {
  background: #303030 !important; 
  
    }


/*dropdown menu's*/
#you-menu .dropdown-menu, .dropdown-menu  {
  background-color: #303030 !important; 
    }
.btn-group.allView.historyView.open {
  background: none !important;
        }

#header-drawer {
          background: #1b1b1b !important; 
            }


#member-profile-fulltitle .read-more {
 background-color: #1b1b1b !important; 
  box-shadow: none;
}

#member-profile-fulltitle {
  -webkit-box-shadow: 0 0 8px #171717 !important;
}

#member-profile-stats li.selected {
    background-color: #303030 !important;
  }

#member-profile-stats li.selected:after, .comment-entry.toplevel, .comment-entry.highlight, .comment-entry {
background: none !important;
}

.comment-entry.highlight {
            color: #ffffff;
}
          
#member-profile-stats .member-profile-stat-count {
  border: 0px solid #303030 !important;
  background-image: none !important;
  background-color: #303030 !important;
}

#member-leftcontent:before {
 box-shadow: 0 0 8px #101010;
}

.commentlist div.imageplace.stats {
    background-color: #505050 !important;
    border-color: #303030 !important;
  }

.comment {
  background-image:none;  
}

li .cover-info, .member-cover-item {
  background-color: #404040 !important;
}

li .cover-stats {
  background-color: #303030 !important;
  border-top: 1px solid #202020 !important;
}

li .cover-stats .thumb-divider {
 border-right: 1px solid #202020 !important;
      }

.cover-item {
  background: #404040 !important;
  }
  
.btn {
  background-color: #505050;
  border: 1px solid #202020;
  }

.btn-group.open .btn.dropdown-toggle {
  background-color: #505050 !important;
        }


.enter-thumb .info a, .enter-thumb .info button {
      color: #e6e6e6;
    }

.button, .button_over, .buttonSmall, .buttonSmall_over, .buttonSmallBlue, .buttonSmallBlue_over, .buttonLarge, .buttonLarge_over {
  background: #303030;
    border-color: #202020;
  }


.commentlist li {
  background: none !important;
  }

.post-comment-box {
 background: #303030 !important; 
 border: 1px solid #101010 !important;
  }
    
.redactor_editor, .redactor_editor:focus  {
 background: #404040 !important; 
      }

.redactor_toolbar {
    border-bottom: none !important;
    background: linear-gradient(to bottom,#505050 0,#404040 94%,#303030 94%,#303030 100%) !important;
    }
    
.redactor_box {
 border: 1px solid #505050 !important; 
 background: #505050 !important;
      }
  
.stepTitleInput {
 background-color: #404040 !important; 
 border-color: #505050 !important;
 color: #cccccc !important;
    }

.dropdown-menu .divider {
    background-color: #404040 !important; 
    border-color: #000 !important;
}

div.photoset {
  background:#303030 !important; 
  }

.photoset:before, .step-container {
  background: none;
    }

div.hatbox {
  border: 1px solid #303030 !important;
}

#ible-about > div:before {
  border-top: none !important;
}

#ible-about > div:after {
  border-color: #303030 !important;
}

.ible-info .ible-details:before  {
    border-right: 1px solid #303030;
      }
      
.ible-info .ible-details:after {
  border-right: none;
            }
            
div.hatbox div.hatbox-content {
  background-color: #404040 !important; 
}

div.hatbox h3 {
  background: #303030 !important; 
  border-bottom: none !important;
}

#ible-about {
  color: #cccccc;
}
/* stat window*/
.imodal-inner {
  background-color: #303030 !important;
  }
  
.imodal-header {
  background: #202020 !important;
    }
    
.ible-stats tr:nth-child(even) {
background: #505050 !important;
        }

div#author-options, #admin-tools {
 background:  #303030 !important; 
 box-shadow: 2px 2px 4px #000;
}

#ible-steps-nav {
 background: #404040; 
  border-top-color:  #303030 !important; 
}


#ibl_header div.centeredContainer {
  background-color: #404040 !important;
  -webkit-box-shadow: 0 0 10px #202020 !important;
}

#attachedImages div.fileset_placeholder {
  background-color: #505050 !important;
  -webkit-box-shadow: inset 0 0 3px 3px #303030 !important;
}

div.InstructableStep, div.stepDetailBox {
  background: none !important;
  background-color:#303030 !important;
  box-shadow: 0 1px 3px #404040 !important;
  border-color: #404040 !important;
    
}

.fileset_placeholder {
  background-color: #505050 !important;
    box-shadow: inset 0 0 3px 3px #303030 !important;
  }
  
  


.navBarButtons div.container {
  background: none !important;
    border-bottom: none !important;
  }

.sidebarBox, ul.topgraytabs li a, table.form td, .entryList, table.membership-details, .instructable-image-bucket, div.tabbedpanes div.small-box, div.small-box, #editpreviewtabbedpanes div.preview, #formfields div.edit, .entryListHeader  {
  background: #404040; 
  }
  
div.hatbox div.image-display-wrapper,.entryListHeader,div.uploadFile_div_1, #actionLinks {
    background: #505050; 
  }
  
.bucketimg, .libraryimg, .entryListHeaderMixed {
  background: #303030; 
    }
  
.empty-item {
    background: none;
      }

  h1, h2, h3, h4, h5, h6, font, strong, .subtitleBold {
    color: #aaaaaa !important; 
}

a {
  color: #cccccc !important; 
}


/* Contest pages */
#contest-navbar {
   background-color: #303030 !important;
  }
  
.full-wrapper, .modal {
    background-color: #1b1b1b !important;
  }
  
.grey-tab {
      background-color: #101010 !important;
    }

.thumbnail {
    background-color: #404040 !important;
  }
  
.statsbox_number {
      color: #aaaaaa !important;
    }
    
.prize-body {
      color: #909090 !important;
    }

.modal-header, .modal-footer {
    background: #303030 !important;
    border-color: #000 !important;
  }
  
.navbar-inner {
   border-color: #000 !important; 
  }

#opened-contests, .ilist a, .group-snippet {
  background: none !important;
}

/*Groups */
.tab-container, div.member-hatbox div.image-display-wrapper {
    background-color: #303030 !important;
  }
  
.tab-button-container {
    background-color: #404040 !important;
  }
  
.tabgroup-button {
    background: none !important;
    background-color: #505050 !important;
  }

/* Forum */
.boxgray {
    background-color: #404040 !important;
  }
  
.boxgray_top, .boxgray_tleft, .boxgray_tright, .boxgray_bot {
    background: #303030 !important;
      color: #cccccc !important;
  }
    
.boxgray_mid {
  border: none !important;      
      }
      
.boxgray_bleft, .boxgray_bright, .contest-archive-year {
  background: none !important;
  }

.member-medallion-wrapper {
    background-color: #303030 !important;
    border-color: #101010 !important;
  }

  .member-medallion-congratulations {
      color: #cccccc !important;
    }
#gopro-plans {
  background: #303030 !important;
  }

.wrapper {
   background: none !important;
}

.containedIble {
   background: #303030 !important;
   border-color: #404040 !important;
}

.description {
  background: #404040 !important;
  border-color: #505050 !important;
  color: #aaa !important;
}

#community-links .btn {
background: #505050 !important;
}

#ible-hits span, .ible-info span {
 color: #aaa !important;   
}

.flat-wrapper, .search-results-wrapper {
    background: none !important;
}

.comment.highlight {
     background: none !important;  
    color: #ccc !important;
}

.comment:not(.token) {
    background-image: none !important;
}

/** bgs **/
.profile-body, .category-projects .channels-list-selector {
background: none !important;
}

.category-landing-projects .category-landing-projects-ible, .site-header-bottom, .recommendations li, .discuss .field, .thumbnail, .admin-panel, .table-striped tbody>tr:nth-child(even)>td, .table-striped tbody>tr:nth-child(even)>th, .nav-tabs>.active>a, .nav-tabs>.active>a:focus, .nav-tabs>.active>a:hover, .category-projects-channels, .category-projects .category-projects-ible {
background: #404040 !important;   
}

.category-landing-projects .category-landing-projects-ible .ible-stats, .recommendations, .lesson-summary, .how-it-works, .file-info, .author-promo, .contest-entry, .discuss, #hamburger-menu button, .sticky-header, .thumbnail .stats, .boxed-content, .profile-header, .admin-module, .table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th, .category-projects .category-projects-ible .ible-stats {
background: #303030 !important; 
}

.contest-info, #contest-wrapper, #contests-header, #contest-winners, .course-lessons, .create-body, .gbl-header .top-bar, .tabs-container, .admin-module .module-header, .badge, .label {
background: #202020 !important;
}

.discuss .field button, .category-landing-classes .category-landing-classes-class, .lesson-summary, .discuss .field, .category-landing-projects .category-landing-projects-ible, .file-info, .author-promo, .contest-entry, .discuss, .boxed-content, .admin-module, .category-projects .category-projects-ible {
border: 1px solid #000;
}

.category-landing-projects .category-landing-projects-ible .ible-stats, .category-projects .category-projects-ible .ible-stats {
border-top: 1px solid #000;    
}

.site-header-bottom, .gbl-header .top-bar, .tab-menu, .profile-header, .category-projects-channels .channels-list {
border-bottom:1px solid #000; 
}

#hamburger-menu button {
border-right: 1px solid #000;
}

.sticky-header .title, .file-info .title, .admin-module .module-header {
color: #ddd !important;
}

.boxContent, .sidebarBox {
border: 1px solid #404040;
}

.sticky-header {
border-color: #000;
}

.photoset {
border: 1px solid #202020;
}

.thumbnail .stats, .ible-about-section:before {
border-top: 1px solid #202020;
}

.v-divider:before {
border-right: 1px solid #202020;
}

.ible-about-section:after {
border-top: 1px solid #505050;
}

.v-divider:after {
border-right: 1px solid #505050;
}

.photoset-showmore {
background: #505050;
border: 1px solid #202020;
box-shadow: none;
}

.badge, .label{
color: #bbb !important; 
}

/** adds **/
#double-inline-ads, .aspace.container, .adBlockWrapper, #gpt-ad-native-middle {
display: none !important;
}
}
