}

a.PROFIL_DECO {

color:#000000;

}

a.PROFIL_DECO:hover {

color:#000000;

}

a.profil_pseudo {

color:#000000;

}

a.profil_pseudo:hover {

color:#000000;

}

#PROFIL_INFOS li {

color:#000000;

}

#PROFIL_INFOS ul a:hover li {

background-color:#000000;

color:#FFFFFF;

}







#MENU {

background-color:#1E90FF;

font-size:15px;

}

