css:
@-moz-document domain('www.solarmovie.is'),
	url-prefix('http://www.solarmovie.is'),
	url-prefix('https://www.solarmovie.is') {

.relative {
	background-color: #222!important;
}

a{
	color: grey!important;
}

.headerpage {
	background-color: #000 !important;
}

.frame .wrapper h1 {
	background-color: #222 !important;
}

.frameWrapper {
	background-color: #000 !important;
}

.frameWrapper .wrapper {
	background: transparent url('http://i61.tinypic.com/m9x8cx.png') no-repeat scroll -119px 0;
	background-color: #222 !important;
}

.ratingsWrapper {
	background-image: linear-gradient(to top,#222,#010101);
}

.reportabug {
	background-image: linear-gradient(to right, #010101 0%, #010101 100%) !important;
}

.SearchesBlock {
	color: grey!important;
}
.commentPreviewBody {
	color: grey!important;
}

.oddComment {
	background-color: #222!important;
}

.comingSoonPage {
	background-color: #222!important;
}

.calendarSort {
	background-color: #222!important;
}

.mediaDescription {
	color: grey!important;
}

.latestTvEpisode {
	background: rgb(75,71,71)!important;
}

.tagSeoContainer {
	background: rgb(116,116,116)!important;
}
.comments {
	color: grey!important;
}
.headerText {
	color: grey!important;
}

.sourceNameCell {
	background-color: #222!important;
	color: grey!important;
}

.qualityCell {
	background-color: #222!important;
	color: grey!important;
}

.ageCell {
	background-color: #222!important;
	color: grey!important;
}

.verionFavoriteCell {
	background-color: #222!important;
	color: grey!important;
}

.twitterNews {
	background-color: #222!important;
	color: grey!important;
}

.searchField {
	background: rgb(128,125,125)!important;
	color: grey!important;
}

.message {
	background: rgb(128,125,125)!important;
	color: grey!important;
}

.js-stars {
	background-color: #222!important;
}

h3 {
	background-color: rgb(139,139,139)!important;
	color: grey!important;
	text-shadow: none!important;
}

.commentDate {
	background-color: rgb(159,159,159)!important;
	color: grey!important;
}

.watchMovieBlock {
	background: none!important;
	color: grey!important;
}

.pseudoText {
	color: grey!important;
}

.tagContainer {
	background: none!important;
	color: grey!important;
}

#footer {
	background-color: #000!important;
}

#footer p {
	background-color: #222!important;
}
		
ul,li,hr,h3,a,span,div {
	border:none !important;
	background: #222 !important;
	background:none !important;
}

input {
	background: none !important;
}

}
