@-moz-document domain("episodecalendar.com") {
body > div.adblocker-warning.visible {display: none !important;}
body.calendar #page {max-width: 100%;}
#calendar .show { font-size: 1em;}
#calendar tr td {border-right: 1px solid #303030;}
.calendar_day_other_month{opacity: 0.9!important;}
.calendar_cell_content li {    padding-bottom: 5px!important;}
.calendar_date {padding: 3px 8px;}
.calendar_today {
	background: linear-gradient(#da0c87, #252125) !important;
  text-shadow: 1px 1px 0 #000 !important;
}
.calendar_today > div.calendar_cell_content > ul > li > strong > a {color: #ffffff !important;}
.calendar_today > div.calendar_cell_content > ul > li > span {color: #fff !important;}
.calendar_today > div.calendar_cell_content > ul > li > strong > a.finale {color: #f00 !important;}
.calendar_today > div.calendar_cell_content > ul > li > strong > a.premiere {color: #3db901 !important;}
#container {background: #252125 !important;}
h2{color:#fff !important;}
.calendar_date {
  background: #444 !important;
  color: #fff !important;
  border: 0 !important;
}
span.episode{color: #fff;}
.calendar_day, .calendar_day_other_month {background: #252125 url(http://i.imgur.com/zpkbBkR.png) 0 0 repeat-x;}
/*http://i.imgur.com/D9I5GqR.png*/
/*http://i.imgur.com/95a1pTM.png*/

/*unwatched episodes*/
.has-selected-state{border-color: #646464!important;}
.selected{border-color: #3c3!important;}
.count>span{background-color: #222 !important;color:#fff!important;}
.epic-list-episode:hover{background-color: #000 !important;}
.options, #show_anchors>h1{color: #fff !important;}
.epic-list-episode:nth-child(even) {background: #333 !important;}
.epic-list-episode:nth-child(odd) {background: #222 !important;}
.episode, .separator{border-color: #333 !important;}
.name, .overview, .date{color: #fff !important;}
#following-3796658 > div > div.epic-card.-no-margin {background-color: #2c2b2c;}

/*my shows*/
.odd>td{background: #222 !important;}
.even>td{background: #333 !important;}
.odd, .even{color:#fff;}
.odd:hover>td, .even:hover>td{background: #000 !important;}

/*all shows*/
.page_header:before{
  background: -webkit-linear-gradient(#000, #252125);
  background: -o-linear-gradient(#000, #252125);
  background: -moz-linear-gradient(#000, #252125);
  background: linear-gradient(#000, #252125);
  border-color: transparent;    
}
.page_header * {  color: #ddd !important;}
.show_list>li a {color: #fff !important;}
.show_list>li:hover {background-color: #000 !important;}
.shows_by_letter h1{color: #fff;}

/*search*/
.alternative, tr > td:nth-child(3) > strong, tr > td:nth-child(4) > strong{color: #fff !important;}
.highlight, td>h2>a>strong, td>h3>a>strong{background-color: /*#da0c87*/#7C054C !important;}

/*show page*/
.color-cover{
  background: -webkit-linear-gradient(#2E021C, #252125) !important;
  background: -o-linear-gradient(#2E021C, #252125) !important;
  background: -moz-linear-gradient(#2E021C, #252125) !important;
  background: linear-gradient(#2E021C, #252125) !important;
  border-color: #333 !important;}
.color-cover + .page-container + .color-cover {
  background: -webkit-linear-gradient(#252125, #252125) !important;
  background: -o-linear-gradient(#252125, #252125) !important;
  background: -moz-linear-gradient(#252125, #252125) !important;
  background: linear-gradient(#252125, #252125) !important;
  }
.h2{color: #fff !important;}
.color-cover strong{color: #fff !important;}
p.margin_bottom_small{color: #fff !important;}
li.tab>a{background-color:#222;}

