css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){

#speeddialTabbox-panelContainer {
  background-color: #F2F2F2;
}

.thumbnail{
  opacity: 0.8 !important; 
  margin:  2px !important;
}

.thumbnail:hover  {
  opacity: 1.0 !important; 
}

.speeddial-container { 
  background-color:  transparent  !important;
  border-width: 1px !important;
  border-color: #cfd1d3 !important; 
}

.speeddial-container:hover { 
  border-color: #969ba1 !important; 
  -moz-transition: all 100ms ease !important;
}

.speeddial-margin-container:hover {
  box-shadow: 0 0 10px #969ba1 !important;
}

.speeddial-container[assigned="f"] {
  background-image: none !important
}

#speeddialTabbox-tabs,
.thumbnail-title,
.speeddial-top-middle,
.speeddial-bottom-middle,
.speeddial-top-middle,
.speeddial-middle-left,
.speeddial-middle-right,
.speeddial-bottom-middle,
.speeddial-top-left,
.speeddial-top-right,
.speeddial-bottom-left,
.speeddial-bottom-right{
  display: none;
}

}
