css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("52tian.net") {
/*元素屏蔽*/
  DIV[style="width:958px; height:88px;"][class="bdad"],DIV[style="width:298px; height:248px;"][class="bdad"],DIV[style="width:648px; height:58px;"][class="bdad"],/*空白*/
  #ft_right_couplet/*ad*/
  {display: none !important;}
}

@-moz-document  domain("52tian.iqiyi.com"),
                url-prefix("http://www.52tian.net/html/movie/")

{
  .position,HTML > BODY > DIV:nth-child(3) > DIV:first-child > H1{font-family: xinsongti, Georgia, serif; font-size: 15px; font-weight:bold; } /*当前位置,H1*/
  /*元素屏蔽*/
  #webboy_kuan,HTML > BODY > DIV:nth-child(3) > DIV:nth-child(5),HTML > BODY > DIV:nth-child(2),/*空白*/
  #ft_right_couplet, /*ad*/
  HTML > BODY > DIV:nth-child(3) > DIV:nth-child(4) > DIV:last-child > DIV > DIV:first-child /*QA标题框*/
  {display: none !important;}
   /* 按钮，上下集等*/
   .buttons{background-color: white !important; position:relative; top:570px !important; opacity: 0.9 !important; border: 0px !important;}
   .linkbutton,.linkbutton.hidead{position:relative; top: 0px !important;}
  /*播放器位置，顶部*/
  .playbox{position: relative;   width:96% !important;  height: 610px !important;background-color: white !important;   top:0px !important;}
  /*播放器大小*/
  #player{position: absolute; width:100% !important; height:95% !important;}
  /*路线选择框*/
  .box.bluebox.server{position: absolute; top: -5px !important; left: 649px !important; width: 309px !important;}
  HTML > BODY > DIV:nth-child(3) > DIV:nth-child(4) > DIV:first-child > DIV:first-child > DIV:first-child{font-size: 8px !important;}
  HTML > BODY > DIV:nth-child(3) > DIV:nth-child(4) > DIV:first-child > DIV:first-child > DIV:first-child > LABEL{font-size: 20px !important; font-weight: bold;}
  /*QA框*/
  .box.playhelp.graybox{position: relative;  top: 73px !important; left:-11px !important; height: 156px !important;width: 309px !important;}
  /*其他板块下移*/
  .box.bluebox.jishu.donghua,.box.bluebox.kgdm,.box.bluebox.pinglun,.box.graybox.playfriendlink,.bottom{position: relative;  top: -5px !important;}
  
}
