css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://k.sogou.com/n/"),
 url-prefix("http://k.sogou.com/touch/"),
    url-prefix("http://k.sogou.com/novel/detail?"){
    #tail_top,#recommend_section,#d1tx,HTML > BODY > SECTION:nth-child(8),HTML > BODY > SECTION:nth-child(9),#best_source_div,#box44,#qqDownloadTips,#yd_t11,#yd_m11,#detail > DIV:nth-child(10),#detail > DIV:nth-child(11),#googleAdId,#googleId,#spreadAd,#sogouUnionAd,.gap,.mianNav{display:none !important;}
        
        /******阅读页***,#nav_top*****/
    nav.novelNav.btmNav{display:none !important;}/*工具栏*/
        #nav_top{background-color: #30374B !important;}  
    #toolbar_bottom,#toolbar_top{/*点击弹出的工具栏*/
        height:40px !important;max-height:40px !important;width: 100%!important;max-width: 800px!important;
        left: auto!important; right: auto!important;
        display:block !important;opacity:0!important;
        background-color: #30374B !important;
        -moz-appearance: none !important; -moz-transition-duration: .5s;-moz-transition-delay: .1s;
       }
        #toolbar_top:hover,#toolbar_bottom:hover{opacity:1!important;}  
        #toolbar_top > .navList > LI > a,#nav_top > .navList > LI > a,#toolbar_bottom > DIV:last-child > a{color: white!important;}
        
        /*新版工具栏*/
        #topbox,div#action.subnav{display:block !important;background-color: rgba(48,55,75,0.9) !important; width: 100%!important; max-width: 800px!important;
                        left: auto!important; right: auto!important; opacity:0 !important;
                        -moz-appearance: none !important; -moz-transition-duration: .5s;-moz-transition-delay: .1s;
       }
        /**************底部工具栏*****************/      
        div#action.subnav{height:auto !important;min-height:100px !important;}    
        
        #topbox:hover,div#action.subnav:hover{opacity:1!important;}
        
        #pageControl,#subBtn{display:flex !important; z-index:10000 !important;}
        
        #closeBookshelfBox,#closeMoreBox,#iSetClose,#closeOfflineBox{background-color:white !important;border-radius:6.5px!important;}/*关闭按钮*/
        
        .setboxm > ul:nth-child(1) > li > span:nth-child(1),
        .setboxm > ul:nth-child(1) > li:nth-child(1) > .fontbox > .fontboxm > span#fontSize,
        #action > div > h2:nth-child(1),
        #collectBtn > p:nth-child(2),
        #recently > li > a:nth-child(1) > p{color:white !important;}
        
        #subBtn > a{background-color: rgba(235, 235, 235,0.8) !important; box-shadow: 0px 0px 1px rgba(48,55,75,1) !important;} /*目录栏*/
        #subBtn > a:hover{box-shadow: 0px 0px 5px rgba(255, 255, 255,1) !important;background-color: rgba(255, 255, 255,1) !important;}
        /**************底部工具栏*****************/      
        
        #topbox > .searchbox,#topbox > #similar_div > #novel_source,#topbox > #similar_div > .tan2m{border-bottom: 1px rgba(168,175,195,1) solid !important;}/*topbox的下划线*/
        
        #i-return{border:none !important;background-color: rgba(235, 235, 235,0.8) !important;border-radius: 23px!important;}/*返回按钮*/
        #i-return:hover{box-shadow: 0px 0px 3px rgba(255, 255, 255,1) !important;background-color: rgba(255, 255, 255,1) !important;}
        
        #similar_list > LI > A,#similar_list > LI > A > STRONG,#novel_source > A,A#a_prev_chapter_top,A#a_next_chapter_top{color: white!important;} /*字体颜色*/
        
        A#a_bookshelf_top{border:none !important;background-color: rgba(235, 235, 235,0.8) !important;border-radius: 23px!important;height:auto  !important;
            line-height:unset!important;margin-top: 15px !important; margin-bottom: auto!important;}
        A#a_bookshelf_top:hover{box-shadow: 0px 0px 3px rgba(255, 255, 255,1) !important;background-color: rgba(255, 255, 255,1) !important;}
        
        A#a_prev_chapter_top,A#a_next_chapter_top{font-size: 20px!important;}/*上 下一章节字体大小*/
        
        A#a_prev_chapter_top:hover,A#a_next_chapter_top:hover{font-size: 22px!important;}
        
        #topbox > #similar_div > .tan2m,#similar_list {height: auto !important;min-height: auto!important;} /*其他来源*/
        
        #topbox > #similar_div > .tan2m > .tan2m-top-shadow,#topbox > #similar_div > .tan2m > .tan2m-bottom-shadow{display:none!important;}/*取消其他来源中上下位置的阴影*/
        
        #similar_list{margin-top: 5px!important; margin-bottom: -22px!important;}
        
        /*#similar_list > LI:last-child{display:none!important;}*/
        
        #similar_list > LI > A,#similar_list > LI > A > STRONG{font-family: songti !important;font-size: 18px!important;}/*其他来源链接*/
        
        #similar_list > LI > A > STRONG{ border-bottom: 0.5px rgba(148,155,175,0.9) solid !important;}/*其他来源链接下划线*/
        #similar_list > LI > A > STRONG:hover{font-size: 20px!important;}
              
     
        /*新版工具栏*/
        
        
    #main,#detail,#spage{background-color: rgba(229,255,205,1) !important;}
        
      H2#title,DIV#switch{background-color: rgba(0,0,0,0.4) !important;}
        
      DIV.source,DIV.simCha{background-color: rgba(0,0,0,0.2) !important;}  
        
        #switch,#detail > DIV.chaNav{max-height: 15px!important;}/*切换新旧版本和上下章节*/
        
        .bg-default .main h2{text-align: left!important;font-size: 26px!important;margin-left: 55px!important;}
        
    .content
    {background-color: rgba(229,255,205,1) !important;
        font-family: 宋体, Georgia, serif;
        font-size:24px;
        font-weight: normal;
        line-height: 200% !important;
        font-style: normal; 
        color:black!important;
    }
    #detailContent{margin-left:25px!important;margin-right: 25px!important;font-size:24px !important;line-height: 200%!important;color:black!important;}
    #detailContent > DIV:first-child > DIV > P:first-child{margin-top: 35px!important;}
    #detailContent > BR,#detailContent > DIV:first-child > DIV > P{margin-bottom: 35px!important;} /*段距*/
        .detail .end{margin-top: -10px!important;margin-bottom: -0px!important;}
        
   HTML{background-color:  rgba(0,0,0,1) !important;}
        
   BODY{ 
       width: 100%!important; max-width: 800px!important;
       margin-left: auto!important; margin-right: auto!important;
       background-color: rgba(229,255,205,1) !important;
    }
        .footer > .searchBox > .querybox{background-color: rgba(255,255,255,1.0) !important;}
        
  HTML > BODY > DIV.wrapper > DIV#main_header > FORM#head_search_form /*搜索栏*/
    {position:absolute!important;
     width: 0%!important;
     height:38px!important;
     margin-top:3px!important;
     background: transparent !important;
     -moz-appearance: none !important; -moz-transition-duration: .4s;-moz-transition-delay: .1s;
    }
    DIV.wrapper > DIV#main_header > FORM#head_search_form:hover{width: 100%!important;}
    
  /*搜索框背景*/  
   DIV.wrapper > DIV#main_header > FORM#head_search_form > DIV.searchBox > DIV.querybox{border: none!important;background: transparent !important;}
    
   /*搜索框*/  
    DIV.wrapper > DIV#main_header > FORM#head_search_form > DIV.searchBox > DIV.querybox > INPUT#novelKeyword{position: relative!important;
        height: 38px!important;
        margin-left: 20px!important;
        background-color: rgba(255,255,255,0.8) !important;
        border-top-right-radius: 19px!important;
        border-bottom-right-radius: 19px!important;
        z-index: 10 !important; 
        padding-left:20px!important;
    }
   DIV.wrapper > DIV#main_header > FORM#head_search_form > DIV.searchBox > DIV.querybox > INPUT#novelKeyword_reset_btn{
       z-index: 11 !important; background-color: rgba(0,0,0,1) !important;}
    /*搜索按钮*/
   DIV.wrapper > DIV#main_header > FORM#head_search_form > DIV.searchBox > DIV.querybox > INPUT#submit_btn{
        z-index: 11 !important;
        border:none!important;
        left:10px!important;  
        box-shadow: 0px 0px 15px 0px rgba(0,0,0,0)!important;
        width: 38px!important;height:38px!important;
        background-position: 10px 10px!important; 
        border-radius: 19px!important;       
        background-color:rgba(255,255,255,1) !important;}
    
DIV.wrapper > DIV.hd_space /*用户名栏*/
    {height:41px!important;display:none!important;}
        
DIV.wrapper > DIV.hd_space > DIV.space_info_area > IMG.thumb_bookshelf  /*头像*/
    {display:none!important;}
DIV.wrapper > DIV.hd_space > DIV.space_info_area > DIV.space_info > div.space_info_txt:nth-child(2) /*余额*/
    {position:relative!important;margin-left:80px!important;bottom:30px!important;}
DIV.wrapper > DIV.hd_space > DIV.space_info_area > DIV.space_info > div.space_info_txt:nth-child(1) /*用户名*/
    {position:relative !important; top:-5px!important;margin-left: 5px!important;}
     
DIV.wrapper > DIV.space_nav2_area /*最近阅读三个按钮位置和背景色*/
    {width:auto !important;max-height: 10px !important;  height: 10px !important;background-color:#73A6F3 !important;}
DIV.wrapper > DIV.space_nav2_area > DIV.space_nav2/*最近阅读三个按钮长度*/
    {min-width:AUTO!important;margin-top: -10px!important;}
      
DIV.wrapper > DIV.space_bar/*编辑按钮*/
    {position: relative!important;
     margin-bottom: -26px!important;
     float:right!important;  
     z-index:11 !important; 
    }
    /*书架*/
    DIV.wrapper > DIV.box1.clearfix,SPAN.schedule_txt,SPAN.schedule_percent,DIV.footer{background-color: rgba(229,255,205,1) !important;}
        
    DIV.wrapper > DIV.box1.clearfix > DIV#userBookList{margin-top: -26px!important;z-index:10 !important;}
        
    DIV.wrapper > DIV.box1{background-color:rgba(0,0,0,0.1) !important;}
        
}
@-moz-document url-prefix("http://k.sogou.com/touch/finRecommend?"){
    HTML > BODY > DIV:nth-child(3){display:none!important;}
}
