css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dm.tsdm.net") {
  /*空白*/
    HTML > BODY > #empty37,
    HTML > BODY > .empty_70,
    .page_content > .box250.fr > DIV:first-child,/*右侧推荐榜顶部的方框*/
    .page_content > .box250.fr >  .blank10,
    HTML > BODY > #tsdm_bd_share,/*微博分享*/
    HTML > BODY > FONT > #tsdm_bd_share,
    HTML > BODY > .play-box > .a300,/*播放器右侧图片*/
    HTML > BODY > .play-box > .play > DIV:last-child > DIV,/*上一集和下一集*/
    HTML > BODY > .page_content > A, /*播放器底部的链接*/
    HTML > BODY > .page_content > DIV[style="margin:0 auto; height:340px;"][class=""],  /*播放页评论区下的空白*/
    HTML > BODY > .page_content > DIV[style="border:1px #BFE6F4 solid;width:600px;height:90px;"],/*播放页评论区下的空白*/
    HTML > BODY > .page_content > DIV:nth-child(9),  
    HTML > BODY > .page_content > DIV:nth-child(11),
    HTML > BODY > .page_content > .blank10,
    HTML > BODY > .page_content > #pfaq > #pfaq,/*影片播放必读*/
    #qvodad{display: none !important;}
    
    /*标题位置修改*/
    HTML > BODY > DIV.phere{position:relative;top:605px!important;left:0px !important;}    
    HTML > BODY > DIV.phere > DIV.channel > H1#playtitle,HTML > BODY > DIV.phere > DIV.channel > A{font-size: 14px!important;}
            
    /*播放器区域，控制播放器大小*/
    /*宽屏播放*/
    
    .play-box{
    background-color: #2C2C2C!important; 
    position: relative;
    top: -32px; margin-bottom:0px !important;
    z-index: 1 !important;
}
    
      /*标题隐藏于播放器之下*/
  HTML > BODY > DIV#head{z-index: 0 !important; position: relative;}
    /*最大化*/
    /*
    .play-box{background-color: #2C2C2C!important; 
  position: fixed ;
  width:100% !important;
  height:100% !important;
  right: 0px; left: 0px; bottom: 0px; top:0px; padding: 0px 0px 0px 0px;
  z-index: 1 !important;
}
*/
    /*播放器外框*/
    HTML > BODY > .play-box > .play > #ccplay > IFRAME{   /* background-color: #2F8F2C!important;  */
    position:absolute;
    left: 0px; right: 0px; bottom: 0px; top: 0px;   
    padding:0px 0px 0px 0px;
    margin: 0 auto!important;
    width:100% !important;
    height:100% !important;  
    z-index: 2 !important;
}

}
@-moz-document url-prefix("http://dm.tsdm.net/js/player/"),
               url-prefix("http://www.tsdmw.net"),
               url-prefix("http://174.139.186.170"),
               url-prefix("http://45.34.36.122:3939/index.html"),
               url-prefix("http://www.tsdm.net/ckplayer/"),
               url-prefix("http://yuntv.letv.com/bcloud.html"),
               url-prefix("http://m.tsdm.net/misc/vs.php?url=")
                  {
                   EMBED,IFRAME,
                   HTML > BODY > DIV#player,
                   HTML > BODY > DIV#player > OBJECT     
                   {
    position:absolute !important;
    margin: 0 auto!important;
    left: 0px!important;right: 0px!important;
    width:100% !important;
    height:100% !important;
    padding:0px 0px 0px 0px;
    z-index: 5 !important; 
                   }

               }
