css:
@-moz-document url("about:newtab"){
     #newtab-margin-top,#newtab-margin-undo-container,#newtab-margin-bottom
       {display:none!important;}
       
     body{        
            background: transparent url("http://ww4.sinaimg.cn/mw690/a004c50bjw1eo847wdb1ij21hc0xcanf.jpg");
            background-color:unset !important;
            background-repeat:no-repeat;
            background-size: cover!important; 
            background-position: right top !important;
            background-attachment: fixed !important;   
        }
   
       /*all box*/
     #newtab-scrollbox{
            background-color: rgba(0,0,0,0) !important;
            padding-bottom: 50px!important;
        }
      
        /*标签框*/
     .newtab-cell{ height: 20% !important;
            width: 20% !important;
            margin-bottom:0px !important;    /*调整tab上下间距*/
            -moz-margin-end:30px !important; /*调整左右间距*/
            background: transparent !important; 
            border:none!important;
        }
    
    /*所有tab的区域范围*/
     #newtab-grid {
        max-width: 980px !important; 
        max-height: 470px !important;
         background-color: rgba(0,0,0,0) !important; 
    }
      
       /*标签的背景透明框*/
     .newtab-link,.newtab-site{
            background-color: rgba(255,255,255,.1) !important;
            border-top-left-radius: 100px!important;/*边缘弧度*/
            border-top-right-radius: 100px;
            border-bottom-right-radius: 100px!important;
            border-bottom-left-radius: 100px!important;
        }
         /*标签图标*/
      .newtab-thumbnail{
            width: 50% !important;
            height: 100% !important;
            opacity:.9!important;
            border-top-left-radius: 100px!important;/*边缘弧度*/
            border-top-right-radius: 100px;
            border-bottom-right-radius: 100px!important;
            border-bottom-left-radius: 100px!important;
            border: 0px solid #767676!important;
        }
    
      .newtab-title{        
            position:absolute!important;
            color:#fff!important;
            text-shadow:0 0 1px #000!important;
            left:0px !important;
            right: 0px !important;                       
            margin-bottom: 35px!important;
            height:20px!important;
            padding-left:10px!important;
            line-height:16px!important;
            color: white!important;
            border-top-left-radius: 100px!important;/*边缘弧度*/
            border-top-right-radius: 100px;
            border-bottom-right-radius: 100px!important;
            border-bottom-left-radius: 100px!important;
            background:rgba(0,0,0,0.6)!important;
        }
        
      /*固定这个网站*/      
    .newtab-control{opacity:.0 !important; top:38px!important;}  
    .newtab-control:hover{opacity:1 !important; } 
    
        /*搜索栏*/
     #newtab-search-container{
            margin-top: 35px!important;
            margin-bottom: 15px!important;
        }
     #newtab-search-form{max-width: 650px !important;
            max-height: 40px !important;}
    
            #newtab-search-text{
            margin-left:0px!important;
            margin-right:0px!important;
        }
    /*搜索按钮*/
     #newtab-search-submit{
            position: relative!important;
            margin-left:-50px!important;
            border:none!important;
            border-top-left-radius: 40px!important;/*边缘弧度*/
            border-bottom-left-radius: 100px!important;
        background:url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go-inverted") no-repeat scroll center center, transparent linear-gradient(#4CB1FF, #1793E5) repeat scroll 0% 0% !important;
            
        }
 
    }
