css:
@-moz-document domain('tvtropes.org') {
  
@-moz-document regexp('http://tvtropes\\.org/pmwiki/(?!pmwiki\\.php/DarthWiki|pmwiki\\.php/SugarWiki).*') {
  .main-nav ul a, .main-nav ul a:link, .main-nav .current-menu-filter,
  .popnav a, .popnav .fa {color: black !important;}
}
.header, .logo, .left-sidebar-gradient {background: none; border: none;}
.main-nav ul a, .random-btn div {font-size: small !important;}

.header .search, .header .random-btn {margin-left: 0; margin-right: 4px;}
.header .random-btn .random-text {display: none;}
.header .random-btn {padding: 0 !important; height: 30px !important;}
#cse-search-box input {box-sizing: border-box; height: 32px !important;}
.search-submit {top: 8px;}
  
.dice-cont img {display: block;
  box-sizing: border-box; -moz-box-sizing: border-box;
  background: url(http://static.tvtropes.org/dice.png) no-repeat;
  background-position: -3px 3px;
  padding-left: 39px; width: 39px; height: 24px;
}

@-moz-document regexp('http://tvtropes\\.org/pmwiki/(?!pmwiki\\.php/DarthWiki|pmwiki\\.php/SugarWiki).*') {
.logo img {display: block;
  box-sizing: border-box; -moz-box-sizing: border-box;
  background: url(http://static.tvtropes.org/pmwiki/pub/images/lampshade_logo_blue.png) no-repeat;
  padding-left: 164px; width: 164px; height: 50px;
}}

.dropdown .dropdown_ul {background: #a6caf0; border-color: #a6caf0;}
.dropdown_ul:after {border-color: transparent transparent #a6caf0 transparent !important; border-style: dashed;}
.dropdown li a {color: black !important;}

.edit-tools {position: relative; display: flex !important; justify-content: center; align-items: center; border: none;}
.edit-tools {padding-bottom: 8px;}
.edit-icon-link {display: none;}
.edit-menu {position: relative;}
.edit-menu ul {position: static; top: 0; left: 0;  width: 100%; padding: 0; display: flex !important; background: none;}
.edit-menu ul:after, .sub-nav ul:after {border: 0;}
.edit-menu ul li, .sub-nav ul li {margin: 0 8px;}
@-moz-document regexp('http://tvtropes\\.org/pmwiki/(?!pmwiki\\.php/DarthWiki|pmwiki\\.php/SugarWiki).*') { 
  .edit-menu li a, .edit-menu li a *, .sub-nav li a, .edit-menu li a * {color: black !important;}
}
.edit-menu li a, .sub-nav li a, .sub-nav li span[onclick] {padding: 0; background: #a6caf0; width: 80px; min-height: 18px; line-height: 18px; 
  border: 1px solid white; border-radius: 9px; box-shadow: 0 0 0 2px #a6caf0, 0 0 0 4px #e0c080;
  font-size: x-small; text-transform: lowercase; text-align: center;
}
.edit-menu ul li a:hover, .sub-nav ul li a:hover, .sub-nav li span[onclick]:hover {background: #b5d9ff; border: 1px solid transparent; box-shadow: 0 0 0 2px #b5d9ff, 0 0 0 4px #e0c080;}
.edit-menu ul, .sub-nav ul {display: inline-block;}
.edit-menu ul li .fa, .sub-nav ul li .fa {margin-right: 0; font-size: inherit;}
  
.sub-nav {position: relative; background: none; border: 0; overflow: visible;}
.sub-nav ul {width: 100%; padding: 0; display: flex !important; justify-content: center; align-items: center;}
  
#watchbtn {width: 100px; height: 18px; line-height: 18px; font-size: small; text-transform: lowercase; text-align: center; border: none;}

.pagetitle {text-align: center;} @-moz-document regexp('http://tvtropes\\.org/pmwiki/(?!pmwiki\\.php/DarthWiki|pmwiki\\.php/SugarWiki).*') {.pagetitle h1 {color: #778899;}}

.acaptionleft, .acaptionright {margin-top: 0; padding: 0.5em; background: white !important; border-style: dotted; box-sizing: border-box !important;}
.DarthWiki .acaptionleft, .DarthWiki .acaptionright {background-color: #EFEFEF !important;}

.wiki-walk span {display: none;}
.wiki-walk .walk-row span {display: inline-block;}

.article-history .sub-nav, .article-history .pagetitle {display: none !important;} .article-history .pagetitle+.diffbox {margin-top: 0 !important;}
.diffbox .diffcontrols {display: inline-block; padding: 0 0.5em 0 0 ; margin: 0 0.5em 0 0; border: 0; border-right: 1px solid silver;}
.diffbox .undo {display: inline;}
.diffbox .undo {text-align: right;}
  
.subpage-icons {display: flex !important; justify-content: center; align-items: center; flex-wrap: wrap;}
.subpage-icons li {margin: 0; padding: 2px;}
.subpage-icons li a, .subpage-icons li a:link {background: none; border-width: 2px !important; border-style: outset !important; border-radius: 8px; border-color: rgba(1,1,1,0.5) !important;}
.subpage-icons li a:hover {background: #eeeeee; border-style: inset !important;}
.subpage-icons li a, .subpage-icons li a:visited {color: #191970;} /*For when no page exists*/

.sub-nav li a, .sub-nav li span[onclick] {width: 100px;}
.forum-topics .sub-nav + div + div,
  .forum-conversations .sub-nav + div + div,
  .discussion-page .sub-nav + div + div {display: flex !important; justify-content: center; align-items: center;}
@-moz-document regexp('http://tvtropes\\.org/pmwiki/addpost\\.php.*') {
  .sub-nav, #forum_search_titles, #forum_search_troper {display: none !important;}
}

input[type="text"], .btn {font-size: small !important;}
  

@-moz-document regexp('http://tvtropes\\.org/pmwiki/pmwiki\\.php/DarthWiki.*') {
  .container {background: black;}
  .main {border-radius: 16px 0 0 16px;}
  .header, .footer, .left-sidebar {border: none !important;}
  .left-side-nav i, .left-side-nav h2 {color: #dddddd !important;}
  .left-side-nav li a {color: #aa8888 !important;}
  .dropdown .dropdown_ul {background: #884444; border-color: #884444;}
  .dropdown_ul:after {border-color: transparent transparent #884444 transparent !important;}
  .edit-menu li a, .edit-menu li a *, .sub-nav li a, .edit-menu li a * {color: #aa8888 !important;}
  .edit-menu li a, .sub-nav li a {background: #880000 !important; border-color: #888888 !important; box-shadow: 0 0 0 2px #880000, 0 0 0 4px black !important;}
  .edit-menu ul li a:hover, .sub-nav ul li a:hover {background: #970f0f !important; border: 1px solid transparent !important; box-shadow: 0 0 0 2px #970f0f, 0 0 0 4px black !important;}
  .walk-row {background: #884444 !important; border: 1px solid #866; border: 1px solid #886666 !important;}
  .wiki-walk span a {color: black !important;}
}
@-moz-document regexp('http://tvtropes\\.org/pmwiki/pmwiki\\.php/SugarWiki.*') {
  .header, .container {background: #D32FBF;}
  .main {border-radius: 16px 0 0 16px;}
  .header, .footer, .left-sidebar {border: none !important;}
  .left-side-nav i, .left-side-nav h2 {color: #dddddd !important;}
  .side-nav-heading {color: white;}
  .dropdown .dropdown_ul {background: #ffffaa; border-color: #ffffaa;}
  .dropdown_ul:after {border-color: transparent transparent #ffffaa transparent !important;}
  .edit-menu li a, .edit-menu li a *, .sub-nav li a, .edit-menu li a * {color: #ff8888 !important;}
  .edit-menu li a, .sub-nav li a {background: #ffffaa !important; border-color: #ffccaa !important; box-shadow: 0 0 0 2px #ffffaa, 0 0 0 4px #aaffaa !important;}
  .edit-menu ul li a:hover, .sub-nav ul li a:hover {background: #ffffaa !important; border: 1px solid transparent !important; box-shadow: 0 0 0 2px #ffffaa, 0 0 0 4px #aaffaa !important;} 
}
  
}
