css:
@-moz-document domain("dieverdammten.de") {
#contentS5,
#contentS6,
#contentS7,
#contentS8,
#contentS9,
#contentS10,
#contentS11,
#contentS12,
#contentS2es,
ul.bagInv,
ul.outInv,
input.button, 
a.inlineButton,
a.button,
.table th,
.ghost .gblock,
.ghost .gblock .header,
.ghost .gblock .footer,
#ghost_pages ul.tabs li,
#ghost_pages ul.tabs li:hover,
#ghost_pages ul.tabs li.selected,
.maps .random_hard, .maps .random,
h1, h2, #tabMenu ul.special,
ul.tabs
{ background-image: none !important; }

.contentBlock div.bg,
#contentBg,
.camp,
.block .header,
.mapBlock .header,
.contentBlock .header,
.mapBlock .footer,
.block .footer,
.contentBlock .footer,
.mapBlock div.bg,
.block div.bg,
ul.logs,
ul.logs li.entry,
ul.logTabs li,
ul.logTabs li a,
ul.logTabs li a.active,
.table td,
.outSpot,
.outside .right a.refresh:first-child + h2,
#tid_forum .tid_bg4,
dl dd,
td.name.important,
td.def.important
{ background: transparent !important;
background-color: transparent !important; }


.sectionArt,
.ambiant,
ul.tools li.label,
.outside h2,
.tutorialBlock,
.helpLink,
.heroMode,
.silenceSeparator,
#quote,
.newFooter,
#tid_bar_down,
#mapTips,
.help,
#ghost_pages .side2,
#ghostImg,
.heroHelp,
#ghostHeroChoose,
#ghostHeroChoose + .sideArt,
form + h2,
.citySign,
.heroAd,
.promoBg,
.promoBt,
.saloon .forumfooter,
tr.building.locked,
.table tr.banner,
a.button.off,
#itemActionsButton
{ display: none !important; }

a.refresh, 
.box .value,
.outside .right a.refresh:first-child + h2,
#itemActions
{ display: block !important; }

#grLagerDRA:hover img {
visibility: hidden;
}

#contentBg,
ul.logs li.entry,
table td,
#ghost_pages ul.tabs li,
#ghost_pages ul.tabs li:hover,
#ghost_pages ul.tabs li.selected,
dl dd, dl dt
{ border: none !important; }

#contentS5, 
#contentS6,
#contentS7,
#contentS8,
#contentS9,
#contentS10,
#contentS11,
#contentS12,
#contentS2es,
.outSpot,
.outSpot .iconBar
{ height: 0px !important; }

#main2, 
#gamebody,
#generic_section,
.outSpot,
.outSpot .iconBar,
ul.tools.stocks.cityInv
{ position: relative; }

.outSpot .iconBar,
.outSpot .iconBar .box,
ul.jobs,
form + h2 + ul.ul
{ position: absolute; }

#clock { 
margin-top: -1px !important; 
margin-left: 65px !important; 
margin-top: 19px !important; 
right: -25px !important; 
top: 26px !important; 
}
.infoBar { 
top: -5px !important; 
height: 70px !important; 
margin-bottom: 0 !important; 
}

.outside .right a.refresh {
margin-left: 130px !important;
margin-top: -20px !important;
}

#sites { 
margin-top: 0 !important; 
top: 26px !important; 
margin-left: -60px; 
}
.outSpot,
.outSpot .iconBar { 
padding: 20px 0 0 !important; 
overflow: hidden; 
}

.outSpot .iconBar,
.outSpot .iconBar .box { top: 0; left: 0; }

.sidePanel, .contentPanel { padding-top: 60px !important; }
.saloon { padding-top: 50px !important; }
#loading_section { top: 80px !important; }
.ghost { padding-top: 50px !important; }
ul.tabs li.selected { margin-top: 0 !important; }
.random { margin-top: -40px !important; }
.news { margin-top: 60px !important; }
ul.tools.stocks.cityInv { width: 570px !important; }
ul.jobs { top: 20px !important; left: -420px !important; }
a.button { line-height: 20px !important; }
#main2 { margin-top: 65px !important; }
a#backReboot { top: 48px !important; }
.news #seasonNews { text-align: left; }
#gameBodyLight { border-top: 1px solid transparent !important; }
table.table.select.large tr { border-bottom: 1px solid #333 !important; }
form + h2 + ul.ul { top: -60px !important; left: -330px !important; }
form + h2 + ul.ul li { float: left; margin: 0 16px; }
#clock { z-index: 0 !important; }
#clock .day { color: #fdd !important; }
#clock .hard { color: #f66 !important; }

ul.tools,
.outside #itemActions .list,
.extractCpt,
ul.logs li.CL_OutsideChat,
ul.ul
{ background: #333 !important; }

input.button, 
a.inlineButton, 
a.button 
{ background: #933 !important; }

ul.logs,
ul.statusSummary,
#buildingList ul.ul.bList
{ background-color: rgba(0, 0, 0, .5) !important; }
.saloon .forumcore, .tid_editorContent .tid_spoil { background-image: none; }
.tid_editorContent .tid_roleplay .tid_wroleplay strong,
.tid_editorContent .tid_roleplay .tid_wroleplay em {
color: #c00 !important;
}
.tid_editorContent .tid_spoil span:first-child {
visibility: visible;
}
.tid_editorContent .tid_spoil:before {
content: "SPOILER ";
font-size: .75em;
color: #c00;
}
.tid_editorContent .tid_spoil {
border: 1px solid #c00;
padding: 5px;
}
.tid_editorContent > p > strong {
opacity: 1;
text-shadow: 0 0 4px #000;
}
#tid_forum .tid_bg2 {
background-color: rgba(0,0,0,.5) !important;
border: 1px dotted #333;
}
.bigBg2 {
background-image: url(http://www.dieverdammten.de/gfx/design/bg_big2.jpg);
background-repeat: no-repeat;
background-position: top center;
background-attachment: fixed;
}
.iglooBankTimerBox {
font-size: 11px;
position: relative;
top: 30px;
right: -302px; 
border: 1px dotted #933;
width: 150px;
background-color: #333;
}
#grLagerDRA {
position: absolute;
right: 4px;
width: 16px !important;
top: 25px;
padding: 0 3px;
transition: .2s all ease;
}
#grLagerDRA:hover {
background-image: url('http://www.dieverdammten.de/gfx/forum/smiley/h_arrow.gif') !important;
background-position: 2px 2px !important;
transform: rotate(90deg);
background-repeat: no-repeat !important;
}

}
