css:
@-moz-document domain("fio.cz") {

/* Vypnuti odporneho karuselu */
#myCarousel, .carousel
	{ 	display:none 		! important;
		visibility: hidden 	! important;
	}

/* Upravy Fio bankovnictvi ver. 2, theme 1.3 */
body {
	font-size: 12px	!important;
	min-width: 800px !important;
}

.container { 
	width: 100% 		!important;
	margin-bottom: 5px	!important;
	/* width: 800px 	!important; */
}

.pohyby {
	min-width: 950px !important;
}

.modal_lg { 
	width: 760px	!important;
}

.fiiglePohyby
{
	width: 780px	!important;
}

.green_border {
	background: 0px 0px #EFAD01	!important;
}

.redirectToIb1 {
	background: #FEC121	!important;
}

.table > thead > tr > th { 
	border-bottom: 2px solid #EFAD01	!important;
}

.table .sumacniRadek td { 
	background: 0px 0px #E6EDF3 !important;
	border-top: 2px solid #EFAD01	!important;
	font-weight: 700;
}

.btnChevron .btn-menu { 
	background: 0px 0px #EFAD01	!important;
}

.widget_boxHeader { 
	padding: 0px 5px	!important;
	height: 22px 		!important;
}

.widget_tools { 
	color: #BBBBBB;
	font-size: 16px		!important;
	margin-top: 0px		!important;
}


.fa-arrows, .fa-times { 
	margin-top: 4px		!important;
}

.fa-cog { 
	height: 28px 		!important;
}

.fa-calendar {
	height: 10px 		!important;
}

.page-header { 
	margin: 0px 0px 5px	!important;
	padding-bottom: 2px	!important;
}

.menu { 
	margin-bottom: 2px	!important;
}


.input-group-addon { 
	font-size: 12px		!important;
	font-weight: 400;
	/* line-height: 0.5	!important; */
	/* height: 5px 		!important; */
	padding: 1px 6px	!important;
}

.form-inline {
	height: 26px 	!important;
}

.form-group { 
	margin-bottom: 6px	!important;
	margin-top: 2px 	!important;
}

.searchInput { 
	height: 28px		!important;
	padding: 2px 6px	!important;
}

.vlPopis { 
	font-size: 11px		!important;
	font-style: normal	!important;
	padding: 2px		!important;
}

.btn { 
	font-size: 12px	!important;
	line-height: 1.4	!important;
	padding: 4px 12px	!important;
}

.btn-default {
	line-height: 1.0	!important;
}


.bootstrap-select > .btn { 
	height: 24px	!important;
}

.bootstrap-select { 
	padding: 0px 0px 	!important;
}


.btn-search { 
	height: 22px	!important;
	padding: 1px 12px !important;
}

.greenBtn, .whiteBtn, .blueBtn {
	height: 22px		!important;
	padding: 1px 12px	!important;
	margin-top: -5px	!important;
}

.form-control {
	padding: 0px 3px 0px 3px	!important;
	height: 24px			!important;
	-webkit-transition: none 	!important;
	-moz-transition: none 		!important;
	-o-transition: none 		!important;
	-ms-transition: none 		!important;
	transition: none 		!important;
}

.green {
	color: #EFAD01  !important;
}

.greenBtn { 
	background-color:  #EFAD01  !important;
}

.greenBtn:hover, .greenBtn:focus { 
	background-color:  #DEA201  !important;
}

.h1_login, .h2_login { 
	border-bottom: 2px solid #DEA201  !important;
}

.fa-share, .fa-pencil, .fa-info-circle, .fa-repeat, .fa-plus, .fa-file-text-o {
	color: #215D99  	!important;
}

.tooltip-inner { 
	background-color: #E6EDF3  !important;
	border-radius: 4px;
	color: #000000  	!important;
	max-width: 200px;
	padding: 3px 8px;
	text-align: center;
	text-decoration: none;
}

.tooltip-arrow {
	border-top: 5px solid #E6EDF3  !important;
	color: #E6EDF3  	!important;
}

.dropdownCloseBtn, .btn-transparent {
	padding: 4px 4px	!important;
}

.bankCode { 
	max-height: 400px;
	min-width: 360px	!important;
	overflow-y: auto;
}


.dropdown-menu > li > a { 
	display: block;
	white-space: normal	!important;
}

.row {
	margin-bottom: 2px	!important;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td { 
	padding: 3px 4px	!important;
}


hr { 
	border-top: 1px solid #EEEEEE;
	margin-bottom: 2px	!important;
	margin-top: 2px		!important;
}

.hidden-print { 
	margin-bottom: 2px	!important;
	margin-top: 2px	!important;
}

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6 { 
	margin-bottom: 2px	!important;
	margin-top: 2px		!important;
}

h1, .h1 { 
	font-size: 18px	!important;
	font-weight: bold	!important;
}

h4, .h4 { 
	font-size: 14px	!important;
	font-weight: bold	!important;
}

.page-headerH2 { 
	border-bottom: 1px solid #EEEEEE;
	margin-bottom: 10px	!important;
}

.selectAcc { 
	font-size: 12px		!important;
}

.selectAcc li a { 
	padding: 2px 20px 2px 20px	!important;
	line-height: 12px		!important;
}

.selectAcc li a i.accIcon { 
	font-size: 12px;
	left: 0px			!important;
	margin-right: 5px;
	position: absolute;
	text-align: center;
	top: 1px			!important;
	width: 22px;
}

.dropdown-menu .divider { 
	margin: 2px 0px	!important;
}

.dropdown_box ul li a { 
	padding-bottom: 2px	!important;
	padding-left: 15px;
	padding-top: 2px	!important;
}

.vypisKaret li { 
	padding: 2px 20px	!important;
}

.crossCard { 
	top: 40px	!important;
}

.vypisKaret .img-responsive { 
	margin-top: 15px	!important;
}

.indexZustatkyBox { 
	padding: 5px 15px	!important;
}

.indexZustatkyBox > div > div { 
	height: auto 		!important;
}

.balanceLabel { 
	white-space: nowrap;
}

/* Zbytecne siroky nazev uctu, preteka box */
div[style="min-width: 340px; word-wrap: break-word;"] {
	min-width: 200px	!important;
}

/* Oprava tooltipu nad grafem zustatku na uctu
  - posledni body vpravo zlobily, tooltip se nezobrazoval, protoze dvouradkovy prekryval bod zobrazujici tooltip.
  Nove je tooltip posunuty vlevo nahoru (temer vycentrovany), takze neprekryva aktivujici tecku ani kdyz je dvouradkovy */	
.ex-tooltip {
    transform: translate(-60px, -15px);
}

/* Prebarveni Fio internet bankovnictvi ver. 1 */

table.main thead th {
    background: none repeat scroll 0% 0% rgb(255, 185, 1)  !important;
}

.accounts li.active {
    color: rgb(0, 0, 0);
    background: none repeat scroll 0% 0% rgb(255, 185, 1)  !important;
    border-bottom-color: rgb(255, 185, 1)  !important;
}

.nav .level1 > li.active {
    background: none rgb(255, 185, 1)   !important;
}

h1.main {
    color: white;
    background: none repeat scroll 0% 0% #EFAD01  !important;
}

.btn-send {
    background: none repeat scroll 0% 0% rgb(255, 185, 1)  !important;
}

.nav .level2Wrapper {
    background: none no-repeat scroll left top #EFAD01   !important;
}
.nav .level2Wrapper .right {
    background: none no-repeat scroll right top #EFAD01  !important;

}

.ibLogin .submit {
    background: none no-repeat scroll left top #EFAD01  !important;
}    

#keyboardToggle {
    background: none no-repeat scroll left top rgb(255, 185, 1)  !important;
    color: white;
}

.nav .subnav li {
    background: none repeat scroll left top rgb(192, 192, 192)  !important;
    opacity:0.93   !important;
}

.nav .subnav a {
    color: rgb(0, 69, 138)  !important;
}

.nav .subnav a:hover {
    color: rgb(150, 104, 1)  !important;
}

.nav .subnav .label {
    color: rgb(60, 60, 60)   !important;
}    

.loggedOut, .loggedOut-error {
    color: #EFAD01   !important;
}

/* Cerne odkazy ve vybrane polozce hlavniho menu
.nav .level1 > li.active > a {
    color: black;
}

.nav .level1 > li.active:hover > a {
    color: black;
}
*/


} /* end of style */
