css:
@-moz-document url-prefix("http://www.okcupid.com/"), url-prefix("https://www.okcupid.com") {
.page-section, body#p_match #match_results, .profile2015-header > .inner, .profile2015-content, .actionbar2015 > .inner {
	width: auto;
}

.upgrade_ad, a[href="https://www.okcupid.com/upgrade?cf=newheader"], a[href="https://www.okcupid.com/upgrade?cf=sitenav"], #alist_intoyou_masthead, #pb_whos_into_you_nonalist, .match-results-cards-ad, #leaderboard_container, .okad, .profilead2015, #quickmatch_ad {
	display: none;
}

.inboxmessages .page-left, body#p_match .monolith, #match-filters .match-filters-in-results > .inner {
	width: 100%;
}

#match-filters {
	padding: 0 3em;
}

.match-results-cards {
	padding: 0 2em;
}

#p_quickmatch .page-left {
	margin-right: 335px;
}

#p_quickmatch .page-right {
	position: fixed;
    right: 2em;
    max-height: calc(100vh - 41em);
	overflow-y: scroll;
}
#p_stalkers .page-left {
	width: calc(100% - 22em);
}

#p_stalkers .page-right {
	position: fixed;
    right: 2em;
}

.profile2015-content {
	display: flex;
}

.profile2015-content-main {
	flex: 1;
}
.page-left, #p_profile .page-right {
	width: auto;
}

#footer_nav li {
	display: inline-block;
    float: none;
}

#footer_nav {
	text-align: center;
}

#boosts.needingtokens, .home-activity-ad, .home-activity-ad-inline {
	display: none;
}

#matches_block .thumbnails {
	width: 100%;
}

#matches_block .thumbnails li, #matches_block .thumbnails li img {
	width: 50px;
	height: 50px;
}

.photobrowser .thumbnails li {
	transition: 0.5s ease transform;
}

.photobrowser .thumbnails li img {
	cursor: -webkit-zoom-in;
	border-radius: 50%;
	float: none;
}

.photobrowser .thumbnails li + li {
	margin-left: 0.8em !important;	
}

.photobrowser .thumbnails li:hover {
	transform: scale(1.7);
}

.match_card_wrapper {
	transition: 0.5s ease transform;
}

.match_card_wrapper:hover {
	transform: scale(1.1);
}

body#p_match .row {
	width: auto !important;
}

#p_profile .monolith {
	display: flex;
	flex-direction: row-reverse;
}

#p_profile .page-left {
	margin-right: 3em;
}

#profile_thumbs .thumb {
	border-radius: 50% !important;
	width: 13em !important;
	height: 13em !important;
}

.thumb:first-child {
	width: 300px;
}

#action_bar {
	left: 0 !important;
	margin-left: 0 !important;
	width: 100% !important;
}
}
